#include <stdio.h>
#include <string.h>
#include <time.h>
#include <stdint.h>

/* Probe helper: write human-readable logs to file for tracing
 * control flow and state around the call to internal_add_connection.
 * This keeps the original logic intact and only records snapshots
 * of the sockaddr_storage before and after the call so agents can
 * determine whether the callee modified the pointed-to object.
 */
static void
mhd_probe_log_snapshot(const char *phase,
                       const void *daemon_ptr,
                       const void *addrptr,
                       socklen_t addrlen,
                       unsigned long long s_val,
                       int sk_nonbl,
                       int sk_spipe_supprs,
                       int sk_cloexec,
                       int sk_non_ip,
                       const unsigned char *bytes,
                       size_t buflen)
{
  FILE *f = fopen("/tmp/logs/673.txt", "a");
  if (NULL == f)
    return; /* best-effort logging */

  time_t t = time(NULL);
  (void) fprintf(f, "TIME=%ld PHASE=%s daemon=%p socket=%llu addrptr=%p addrlen=%u "
                 "sk_nonbl=%d sk_spipe_supprs=%d sk_cloexec=%d sk_non_ip=%d\n",
                 (long) t,
                 phase,
                 daemon_ptr,
                 s_val,
                 addrptr,
                 (unsigned int) addrlen,
                 sk_nonbl,
                 sk_spipe_supprs,
                 sk_cloexec,
                 sk_non_ip);

  /* Dump up to buflen bytes (but not more than addrlen and a safe limit) */
  size_t toprint = buflen;
  if ((size_t) addrlen < toprint)
    toprint = (size_t) addrlen;
  if (toprint > 256u)
    toprint = 256u; /* avoid excessive logs */

  (void) fprintf(f, "%s: addr bytes (first %zu):", phase, toprint);
  for (size_t i = 0; i < toprint; ++i)
    (void) fprintf(f, " %02x", (unsigned int) bytes[i]);
  (void) fprintf(f, "\n\n");

  (void) fclose(f);
}

static void
mhd_probe_log_diff(const void *daemon_ptr,
                    const void *addrptr,
                    socklen_t addrlen,
                    const unsigned char *before,
                    const unsigned char *after,
                    size_t buflen)
{
  FILE *f = fopen("/tmp/logs/673.txt", "a");
  if (NULL == f)
    return;

  size_t limit = buflen;
  if ((size_t) addrlen < limit)
    limit = (size_t) addrlen;
  if (limit > 256u)
    limit = 256u;

  size_t diffs = 0;
  (void) fprintf(f, "DIFF_CHECK daemon=%p addrptr=%p addrlen=%u limit=%zu\n",
                 daemon_ptr, addrptr, (unsigned int) addrlen, limit);
  for (size_t i = 0; i < limit; ++i)
  {
    if (before[i] != after[i])
    {
      if (diffs < 16u)
        (void) fprintf(f, "  byte[%zu]: before=%02x after=%02x\n", i, before[i], after[i]);
      ++diffs;
    }
  }
  (void) fprintf(f, "Total differing bytes (first %zu bytes checked): %zu\n\n", limit, diffs);
  (void) fclose(f);
}

static enum MHD_Result
MHD_accept_connection (struct MHD_Daemon *daemon)
{
  struct sockaddr_storage addrstorage;
  socklen_t addrlen;
  MHD_socket s;
  MHD_socket fd;
  bool sk_nonbl;
  bool sk_spipe_supprs;
  bool sk_cloexec;
  enum MHD_tristate sk_non_ip;
#if defined(_DEBUG) && defined (USE_ACCEPT4)
  const bool use_accept4 = ! daemon->avoid_accept4;
#elif defined (USE_ACCEPT4)
  static const bool use_accept4 = true;
#else  /* ! USE_ACCEPT4 && ! _DEBUG */
  static const bool use_accept4 = false;
#endif /* ! USE_ACCEPT4 && ! _DEBUG */

#ifdef MHD_USE_THREADS
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  if ( (MHD_INVALID_SOCKET == (fd = daemon->listen_fd)) ||
       (daemon->was_quiesced) )
    return MHD_NO;

  addrlen = (socklen_t) sizeof (addrstorage);
  memset (&addrstorage,
          0,
          (size_t) addrlen);
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen;
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

  /* Initialise with default values to avoid compiler warnings */
  sk_nonbl = false;
  sk_spipe_supprs = false;
  sk_cloexec = false;
  s = MHD_INVALID_SOCKET;

#ifdef USE_ACCEPT4
  if (use_accept4 &&
      (MHD_INVALID_SOCKET !=
       (s = accept4 (fd,
                     (struct sockaddr *) &addrstorage,
                     &addrlen,
                     SOCK_CLOEXEC_OR_ZERO | SOCK_NONBLOCK_OR_ZERO
                     | SOCK_NOSIGPIPE_OR_ZERO))))
  {
    sk_nonbl = (SOCK_NONBLOCK_OR_ZERO != 0);
#ifndef MHD_WINSOCK_SOCKETS
    sk_spipe_supprs = (SOCK_NOSIGPIPE_OR_ZERO != 0);
#else  /* MHD_WINSOCK_SOCKETS */
    sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
    sk_cloexec = (SOCK_CLOEXEC_OR_ZERO != 0);
  }
#endif /* USE_ACCEPT4 */
#if defined(_DEBUG) || ! defined(USE_ACCEPT4)
  if (! use_accept4 &&
      (MHD_INVALID_SOCKET !=
       (s = accept (fd,
                    (struct sockaddr *) &addrstorage,
                    &addrlen))))
  {
#ifdef MHD_ACCEPT_INHERIT_NONBLOCK
    sk_nonbl = daemon->listen_nonblk;
#else  /* ! MHD_ACCEPT_INHERIT_NONBLOCK */
    sk_nonbl = false;
#endif /* ! MHD_ACCEPT_INHERIT_NONBLOCK */
#ifndef MHD_WINSOCK_SOCKETS
    sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
    sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
    sk_cloexec = false;
  }
#endif /* _DEBUG || !USE_ACCEPT4 */

  if (MHD_INVALID_SOCKET == s)
  {
    const int err = MHD_socket_get_error_ ();

    /* This could be a common occurrence with multiple worker threads */
    if (MHD_SCKT_ERR_IS_ (err,
                          MHD_SCKT_EINVAL_))
      return MHD_NO;   /* can happen during shutdown */
    if (MHD_SCKT_ERR_IS_DISCNN_BEFORE_ACCEPT_ (err))
      return MHD_NO;   /* do not print error if client just disconnected early */
#ifdef HAVE_MESSAGES
    if (! MHD_SCKT_ERR_IS_EAGAIN_ (err) )
      MHD_DLOG (daemon,
                _ ("Error accepting connection: %s\n"),
                MHD_socket_strerr_ (err));
#endif
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err) )
    {
      /* system/process out of resources */
      if (0 == daemon->connections)
      {
#ifdef HAVE_MESSAGES
        /* Not setting 'at_limit' flag, as there is no way it
           would ever be cleared.  Instead trying to produce
           bit fat ugly warning. */
        MHD_DLOG (daemon,
                  _ ("Hit process or system resource limit at FIRST " \
                     "connection. This is really bad as there is no sane " \
                     "way to proceed. Will try busy waiting for system " \
                     "resources to become magically available.\n"));
#endif
      }
      else
      {
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
        MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
#endif
        daemon->at_limit = true;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
        MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#endif
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("Hit process or system resource limit at %u " \
                     "connections, temporarily suspending accept(). " \
                     "Consider setting a lower MHD_OPTION_CONNECTION_LIMIT.\n"),
                  (unsigned int) daemon->connections);
#endif
      }
    }
    return MHD_NO;
  }

  sk_non_ip = daemon->listen_is_unix;
  if (0 >= addrlen)
  {
#ifdef HAVE_MESSAGES
    if (_MHD_NO != daemon->listen_is_unix)
      MHD_DLOG (daemon,
                _ ("Accepted socket has zero-length address. "
                   "Processing the new socket as a socket with " \
                   "unknown type.\n"));
#endif
    addrlen = 0;
    sk_non_ip = _MHD_YES; /* IP-type addresses have non-zero length */
  }
  if (((socklen_t) sizeof (addrstorage)) < addrlen)
  {
    /* Should not happen as 'sockaddr_storage' must be large enough to
     * store any address supported by the system. */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Accepted socket address is larger than expected by " \
                 "system headers. Processing the new socket as a socket with " \
                 "unknown type.\n"));
#endif
    addrlen = 0;
    sk_non_ip = _MHD_YES; /* IP-type addresses must fit */
  }

  if (! sk_nonbl && ! MHD_socket_nonblocking_ (s))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on incoming connection " \
                 "socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
  }
  else
    sk_nonbl = true;

  if (! sk_cloexec && ! MHD_socket_noninheritable_ (s))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on incoming connection " \
                 "socket.\n"));
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
  }

#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs && ! MHD_socket_nosignal_ (s))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on incoming connection " \
                 "socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      (void) MHD_socket_close_ (s);
      return MHD_NO;
    }
#endif /* MSG_NOSIGNAL */
  }
  else
    sk_spipe_supprs = true;
#endif /* MHD_socket_nosignal_ */
#ifdef HAVE_MESSAGES
#if _MHD_DEBUG_CONNECT
  MHD_DLOG (daemon,
            _ ("Accepted connection on socket %d\n"),
            s);
#endif
#endif

  /* --- Probe: snapshot 'addrstorage' BEFORE calling internal_add_connection --- */
  {
    unsigned char before_buf[sizeof (addrstorage)];
    (void) memcpy(before_buf, &addrstorage, sizeof (addrstorage));

    mhd_probe_log_snapshot("BEFORE",
                           (const void *) daemon,
                           (const void *) &addrstorage,
                           addrlen,
                           (unsigned long long) (uintptr_t) s,
                           (int) sk_nonbl,
                           (int) sk_spipe_supprs,
                           (int) sk_cloexec,
                           (int) sk_non_ip,
                           before_buf,
                           sizeof (before_buf));

    /* Call original function (preserve semantics). */
    (void) internal_add_connection (daemon,
                                    s,
                                    &addrstorage,
                                    addrlen,
                                    false,
                                    sk_nonbl,
                                    sk_spipe_supprs,
                                    sk_non_ip);

    /* Capture 'after' snapshot and compare to detect any modification. */
    unsigned char after_buf[sizeof (addrstorage)];
    (void) memcpy(after_buf, &addrstorage, sizeof (addrstorage));

    mhd_probe_log_snapshot("AFTER",
                           (const void *) daemon,
                           (const void *) &addrstorage,
                           addrlen,
                           (unsigned long long) (uintptr_t) s,
                           (int) sk_nonbl,
                           (int) sk_spipe_supprs,
                           (int) sk_cloexec,
                           (int) sk_non_ip,
                           after_buf,
                           sizeof (after_buf));

    mhd_probe_log_diff((const void *) daemon,
                       (const void *) &addrstorage,
                       addrlen,
                       before_buf,
                       after_buf,
                       sizeof (before_buf));
  }

  return MHD_YES;
}
