#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>

/* Simple probe logger used to collect control-flow/state traces. */
static void probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/682.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_connection (struct MHD_Daemon *daemon,
                    MHD_socket client_socket,
                    const struct sockaddr *addr,
                    socklen_t addrlen)
{
  bool sk_nonbl;
  bool sk_spipe_supprs;
  struct sockaddr_storage addrstorage;

  /* Log entry and key initial state */
  probe_log("MHD_add_connection ENTER: daemon=%p client_socket=%lld addr=%p addrlen=%u",
            (void *) daemon,
            (long long) (intptr_t) client_socket,
            (void *) addr,
            (unsigned) addrlen);

  /* TODO: fix atomic value reading */
  if ((! MHD_D_IS_THREAD_SAFE_ (daemon)) &&
      (daemon->connection_limit <= daemon->connections))
    MHD_cleanup_connections (daemon);

#ifdef HAVE_MESSAGES
  if (MHD_D_IS_USING_THREADS_ (daemon) &&
      (0 == (daemon->options & MHD_USE_ITC)))
  {
    MHD_DLOG (daemon,
              _ ("MHD_add_connection() has been called for daemon started"
                 " without MHD_USE_ITC flag.\nDaemon will not process newly"
                 " added connection until any activity occurs in already"
                 " added sockets.\n"));
  }
#endif /* HAVE_MESSAGES */
  if (0 != addrlen)
  {
    if (AF_INET == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        probe_log("MHD_add_connection: invalid addrlen for AF_INET: addrlen=%u sizeof(sockaddr_in)=%zu",
                  (unsigned) addrlen,
                  sizeof(struct sockaddr_in));
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        probe_log("MHD_add_connection: mismatched sa_len for AF_INET: sa_len=%u sizeof(sockaddr_in)=%zu",
                  (unsigned) addr->sa_len,
                  sizeof(struct sockaddr_in));
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_INET6
    if (AF_INET6 == addr->sa_family)
    {
      if (sizeof(struct sockaddr_in6) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        probe_log("MHD_add_connection: invalid addrlen for AF_INET6: addrlen=%u sizeof(sockaddr_in6)=%zu",
                  (unsigned) addrlen,
                  sizeof(struct sockaddr_in6));
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in6) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        probe_log("MHD_add_connection: mismatched sa_len for AF_INET6: sa_len=%u sizeof(sockaddr_in6)=%zu",
                  (unsigned) addr->sa_len,
                  sizeof(struct sockaddr_in6));
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if ((0 != addr->sa_len) &&
        (addrlen > addr->sa_len))
      addrlen = (socklen_t) addr->sa_len;   /* Use safest value */
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
#endif /* HAVE_INET6 */
  }

  if (! MHD_socket_nonblocking_ (client_socket))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    sk_nonbl = false;
    probe_log("MHD_add_connection: failed set nonblocking, sk_nonbl=false");
  }
  else
  {
    sk_nonbl = true;
    probe_log("MHD_add_connection: nonblocking OK, sk_nonbl=true");
  }

#ifndef MHD_WINSOCK_SOCKETS
  sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
  sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
  probe_log("MHD_add_connection: initial sk_spipe_supprs=%d", sk_spipe_supprs);
#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs)
    sk_spipe_supprs = MHD_socket_nosignal_ (client_socket);
  probe_log("MHD_add_connection: after MHD_socket_nosignal_ call sk_spipe_supprs=%d", sk_spipe_supprs);
  if (! sk_spipe_supprs)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
    /* Also write a probe entry so behaviour is captured even when DLOG
       might not be enabled or visible. */
    probe_log("MHD_add_connection: HAVE_MESSAGES defined -> MHD_DLOG executed for SIGPIPE suppression failure");
#else  /* ! HAVE_MESSAGES */
    /* Original code had a no-op here: (void) 0;   */
    /* Insert probe to evidence that this no-op path was executed. */
    probe_log("MHD_add_connection: HAVE_MESSAGES not defined -> original code executes a no-op here (dead-code candidate)");
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      int err = MHD_socket_get_error_ ();
      probe_log("MHD_add_connection: SIGPIPE suppression failed and daemon->sigpipe_blocked==false, err=%d", err);
      MHD_socket_close_ (client_socket);
      MHD_socket_fset_error_ (err);
      probe_log("MHD_add_connection: returning MHD_NO due to SIGPIPE policy");
      return MHD_NO;
    }
    else
    {
      probe_log("MHD_add_connection: SIGPIPE suppression failed but daemon->sigpipe_blocked==true -> continue");
    }
#endif /* MSG_NOSIGNAL */
  }
#endif /* MHD_socket_nosignal_ */

  if ( (0 != (daemon->options & MHD_USE_TURBO)) &&
       (! MHD_socket_noninheritable_ (client_socket)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on new client socket.\n"));
#endif
    probe_log("MHD_add_connection: failed to set noninheritable mode (options=0x%08x)", daemon->options);
  }

  /* Copy to sockaddr_storage structure to avoid alignment problems */
  if (0 < addrlen)
  {
    memcpy (&addrstorage, addr, (size_t) addrlen);
    probe_log("MHD_add_connection: copied addr to addrstorage, addrlen=%u", (unsigned) addrlen);
  }
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen; /* Force set the right length */
  probe_log("MHD_add_connection: set addrstorage.ss_len=%u", (unsigned) addrlen);
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {
    unsigned int i;
    /* have a pool, try to find a pool with capacity; we use the
       socket as the initial offset into the pool for load
       balancing */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      struct MHD_Daemon *const worker =
        &daemon->worker_pool[(i + (unsigned int) client_socket)
                             % daemon->worker_pool_size];
      if (worker->connections < worker->connection_limit)
      {
        probe_log("MHD_add_connection: delegating to worker %u (connections=%u limit=%u)",
                  i,
                  worker->connections,
                  worker->connection_limit);
        return internal_add_connection (worker,
                                        client_socket,
                                        &addrstorage,
                                        addrlen,
                                        true,
                                        sk_nonbl,
                                        sk_spipe_supprs,
                                        _MHD_UNKNOWN);
      }
    }
    /* all pools are at their connection limit, must refuse */
    probe_log("MHD_add_connection: all worker pools full, closing client_socket=%lld",
              (long long) (intptr_t) client_socket);
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    probe_log("MHD_add_connection: returning MHD_NO due to pool limits");
    return MHD_NO;
  }
#endif /* MHD_USE_POSIX_THREADS || MHD_USE_W32_THREADS */

  probe_log("MHD_add_connection: calling internal_add_connection with sk_nonbl=%d sk_spipe_supprs=%d",
            sk_nonbl, sk_spipe_supprs);

  return internal_add_connection (daemon,
                                  client_socket,
                                  &addrstorage,
                                  addrlen,
                                  true,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  _MHD_UNKNOWN);
}
