#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdint.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/689.txt */
static void mhd_log_probe(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/689.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fputc('\n', f);
    va_end(ap);
    fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_add_connection (struct MHD_Daemon *daemon,
                    MHD_socket client_socket,
                    const struct sockaddr *addr,
                    socklen_t addrlen)
{
  bool sk_nonbl;
  bool sk_spipe_supprs;
  struct sockaddr_storage addrstorage;

  mhd_log_probe("ENTER: MHD_add_connection daemon=%p client_socket=%llu addr=%p addrlen=%lu",
                (void *) daemon,
                (unsigned long long) client_socket,
                (const void *) addr,
                (unsigned long) addrlen);

  /* TODO: fix atomic value reading */
  if ((! MHD_D_IS_THREAD_SAFE_ (daemon)) &&
      (daemon->connection_limit <= daemon->connections))
  {
    mhd_log_probe("INFO: connection_limit reached (limit=%lu connections=%lu). Calling MHD_cleanup_connections",
                  (unsigned long) daemon->connection_limit,
                  (unsigned long) daemon->connections);
    MHD_cleanup_connections (daemon);
  }

#ifdef HAVE_MESSAGES
  if (MHD_D_IS_USING_THREADS_ (daemon) &&
      (0 == (daemon->options & MHD_USE_ITC)))
  {
    MHD_DLOG (daemon,
              _ ("MHD_add_connection() has been called for daemon started"
                 " without MHD_USE_ITC flag.\nDaemon will not process newly"
                 " added connection until any activity occurs in already"
                 " added sockets.\n"));
  }
#endif /* HAVE_MESSAGES */
  if (0 != addrlen)
  {
    mhd_log_probe("ADDR CHECK: addr=%p family=%d addrlen=%lu",
                  (const void *) addr,
                  (int) addr->sa_family,
                  (unsigned long) addrlen);
    if (AF_INET == addr->sa_family)
    {
      mhd_log_probe("FAMILY=AF_INET: sizeof(sockaddr_in)=%zu addrlen=%lu",
                    sizeof(struct sockaddr_in), (unsigned long) addrlen);
      if (sizeof(struct sockaddr_in) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        mhd_log_probe("REJECT: AF_INET addrlen too small -> return MHD_NO");
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        mhd_log_probe("REJECT: AF_INET addr->sa_len inconsistent -> return MHD_NO sa_len=%u",
                      (unsigned) addr->sa_len);
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_INET6
    if (AF_INET6 == addr->sa_family)
    {
      mhd_log_probe("FAMILY=AF_INET6: sizeof(sockaddr_in6)=%zu addrlen=%lu",
                    sizeof(struct sockaddr_in6), (unsigned long) addrlen);
      if (sizeof(struct sockaddr_in6) > (size_t) addrlen)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with "
                     "incorrect 'addrlen' value.\n"));
#endif /* HAVE_MESSAGES */
        mhd_log_probe("REJECT: AF_INET6 addrlen too small -> return MHD_NO");
        return MHD_NO;
      }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
      if ((0 != addr->sa_len) &&
          (sizeof(struct sockaddr_in6) > (size_t) addr->sa_len) )
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon,
                  _ ("MHD_add_connection() has been called with " \
                     "non-zero value of 'sa_len' member of " \
                     "'struct sockaddr' which does not match 'sa_family'.\n"));
#endif /* HAVE_MESSAGES */
        mhd_log_probe("REJECT: AF_INET6 addr->sa_len inconsistent -> return MHD_NO sa_len=%u",
                      (unsigned) addr->sa_len);
        return MHD_NO;
      }
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
    }
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if ((0 != addr->sa_len) &&
        (addrlen > addr->sa_len))
      addrlen = (socklen_t) addr->sa_len;   /* Use safest value */
    mhd_log_probe("ADJUST: after sa_len clamp, addrlen=%lu (sa_len=%u)",
                  (unsigned long) addrlen,
                  (unsigned) (addr ? addr->sa_len : 0));
#endif /* HAVE_STRUCT_SOCKADDR_SA_LEN */
#endif /* HAVE_INET6 */
  }

  if (! MHD_socket_nonblocking_ (client_socket))
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set nonblocking mode on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#endif
    sk_nonbl = false;
    mhd_log_probe("SOCKET: nonblocking failed for client_socket=%llu",
                  (unsigned long long) client_socket);
  }
  else
  {
    sk_nonbl = true;
    mhd_log_probe("SOCKET: nonblocking set for client_socket=%llu",
                  (unsigned long long) client_socket);
  }

#ifndef MHD_WINSOCK_SOCKETS
  sk_spipe_supprs = false;
#else  /* MHD_WINSOCK_SOCKETS */
  sk_spipe_supprs = true; /* Nothing to suppress on W32 */
#endif /* MHD_WINSOCK_SOCKETS */
#if defined(MHD_socket_nosignal_)
  if (! sk_spipe_supprs)
    sk_spipe_supprs = MHD_socket_nosignal_ (client_socket);
  if (! sk_spipe_supprs)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to suppress SIGPIPE on new client socket: %s\n"),
              MHD_socket_last_strerr_ ());
#else  /* ! HAVE_MESSAGES */
    (void) 0; /* Mute compiler warning */
#endif /* ! HAVE_MESSAGES */
#ifndef MSG_NOSIGNAL
    /* Application expects that SIGPIPE will be suppressed,
     * but suppression failed and SIGPIPE cannot be suppressed with send(). */
    if (! daemon->sigpipe_blocked)
    {
      int err = MHD_socket_get_error_ ();
      MHD_socket_close_ (client_socket);
      MHD_socket_fset_error_ (err);
      mhd_log_probe("SIGPIPE suppression failed and cannot emulate: closing socket and returning MHD_NO err=%d",
                    err);
      return MHD_NO;
    }
#endif /* MSG_NOSIGNAL */
  }
#endif /* MHD_socket_nosignal_ */

  if ( (0 != (daemon->options & MHD_USE_TURBO)) &&
       (! MHD_socket_noninheritable_ (client_socket)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Failed to set noninheritable mode on new client socket.\n"));
#endif
    mhd_log_probe("SOCKET: failed to set noninheritable on client_socket=%llu",
                  (unsigned long long) client_socket);
  }

  /* Copy to sockaddr_storage structure to avoid alignment problems */
  if (0 < addrlen)
  {
    /* Log details before memcpy so we can detect potential overflow attempts */
    mhd_log_probe("ABOUT_TO_MEMCPY: addr=%p addrlen=%lu sizeof(addrstorage)=%zu family=%d",
                  (const void *) addr,
                  (unsigned long) addrlen,
                  sizeof(addrstorage),
                  (int) (addr ? addr->sa_family : -1));
#ifdef HAVE_STRUCT_SOCKADDR_SA_LEN
    if (addr)
      mhd_log_probe("ABOUT_TO_MEMCPY: addr->sa_len=%u",
                    (unsigned) addr->sa_len);
#endif
    if ((size_t) addrlen > sizeof(addrstorage))
    {
      /* Do not alter behavior: only log the risky condition */
      mhd_log_probe("POTENTIAL_OVERFLOW: addrlen (%lu) > sizeof(addrstorage) (%zu). memcpy may overflow.",
                    (unsigned long) addrlen, sizeof(addrstorage));
    }
    memcpy (&addrstorage, addr, (size_t) addrlen);
    mhd_log_probe("AFTER_MEMCPY: copied %lu bytes into addrstorage @%p",
                  (unsigned long) addrlen,
                  (void *) &addrstorage);
  }
#ifdef HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN
  addrstorage.ss_len = addrlen; /* Force set the right length */
  mhd_log_probe("SET ss_len=%lu on addrstorage", (unsigned long) addrlen);
#endif /* HAVE_STRUCT_SOCKADDR_STORAGE_SS_LEN */

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if (NULL != daemon->worker_pool)
  {
    unsigned int i;
    /* have a pool, try to find a pool with capacity; we use the
       socket as the initial offset into the pool for load
       balancing */
    for (i = 0; i < daemon->worker_pool_size; ++i)
    {
      struct MHD_Daemon *const worker =
        &daemon->worker_pool[(i + (unsigned int) client_socket)
                             % daemon->worker_pool_size];
      if (worker->connections < worker->connection_limit)
      {
        mhd_log_probe("HANDOFF: delegating to worker=%p (index=%u) connections=%lu limit=%lu",
                      (void *) worker, i,
                      (unsigned long) worker->connections,
                      (unsigned long) worker->connection_limit);
        return internal_add_connection (worker,
                                        client_socket,
                                        &addrstorage,
                                        addrlen,
                                        true,
                                        sk_nonbl,
                                        sk_spipe_supprs,
                                        _MHD_UNKNOWN);
      }
    }
    /* all pools are at their connection limit, must refuse */
    mhd_log_probe("REFUSE: all worker pools full (worker_pool_size=%u). Closing socket.",
                  daemon->worker_pool_size);
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    return MHD_NO;
  }
#endif /* MHD_USE_POSIX_THREADS || MHD_USE_W32_THREADS */

  mhd_log_probe("FINAL: calling internal_add_connection with daemon=%p client_socket=%llu addrstorage=%p addrlen=%lu",
                (void *) daemon,
                (unsigned long long) client_socket,
                (void *) &addrstorage,
                (unsigned long) addrlen);

  return internal_add_connection (daemon,
                                  client_socket,
                                  &addrstorage,
                                  addrlen,
                                  true,
                                  sk_nonbl,
                                  sk_spipe_supprs,
                                  _MHD_UNKNOWN);
}
