#include <stdio.h>
#include <stddef.h>

bool
MHD_add_response_entry_no_alloc_ (struct MHD_Response *response,
                                  enum MHD_ValueKind kind,
                                  char *header,
                                  size_t header_len,
                                  char *content,
                                  size_t content_len)
{
  struct MHD_HTTP_Res_Header *hdr;
  FILE *log = fopen("/tmp/logs/1913.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER MHD_add_response_entry_no_alloc_: response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu\n",
            (void *)response, (int)kind, (void *)header, header_len, (void *)content, content_len);
  }

  mhd_assert (0 != header_len);
  mhd_assert (0 != content_len);
  if (NULL == (hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header))))
  {
    if (log)
    {
      fprintf(log, "ALLOCATION FAILED: MHD_calloc_ returned NULL\n");
      fclose(log);
    }
    return false;
  }

  /* Preserve original assignments */
  hdr->header = header;
  hdr->header_size = header_len;
  hdr->value = content;
  hdr->value_size = content_len;
  hdr->kind = kind;

  if (log)
  {
    fprintf(log, "AFTER ASSIGN: hdr=%p hdr->header=%p hdr->header_size=%zu hdr->value=%p hdr->value_size=%zu hdr->kind=%d\n",
            (void *)hdr, (void *)hdr->header, hdr->header_size, (void *)hdr->value, hdr->value_size, (int)hdr->kind);
    fprintf(log, "About to call _MHD_insert_header_last(response=%p, hdr=%p)\n", (void *)response, (void *)hdr);
    /* Keep the file open so the order of events is preserved in case the
       insertion routine logs as well. */
  }

  _MHD_insert_header_last (response, hdr);

  if (log)
  {
    fprintf(log, "INSERTED: header inserted, returning true\n");
    fclose(log);
  }

  return true; /* Success exit point */
}
