#include <stdio.h>
#include <stdlib.h>
#include <string.h>
#include <stdarg.h>
#include <stdbool.h>
#include <stddef.h>

/* Probe logging helper: append formatted messages to /tmp/logs/1922.txt */
static void probe_logf(const char *fmt, ...)
{
  char buf[512];
  va_list ap;

  va_start(ap, fmt);
  vsnprintf(buf, sizeof(buf), fmt, ap);
  va_end(ap);

  FILE *f = fopen("/tmp/logs/1922.txt", "a");
  if (NULL != f)
  {
    fprintf(f, "%s\n", buf);
    fclose(f);
  }
}

bool
MHD_add_response_entry_no_check_ (struct MHD_Response *response,
                                  enum MHD_ValueKind kind,
                                  const char *header,
                                  size_t header_len,
                                  const char *content,
                                  size_t content_len)
{
  char *header_malloced;
  char *value_malloced;

  /* Log entry and values that would be asserted. This allows other
     agents to observe the state even when mhd_assert is a no-op. */
  probe_logf("ENTER: MHD_add_response_entry_no_check_ response=%p kind=%d header=%p header_len=%zu content=%p content_len=%zu",
             (void *)response, (int)kind, (const void *)header, header_len, (const void *)content, content_len);

  probe_logf("ASSERT_CHECK: header_len expected != 0; actual header_len=%zu", header_len);
  mhd_assert (0 != header_len);
  probe_logf("ASSERT_CHECK: content_len expected != 0; actual content_len=%zu", content_len);
  mhd_assert (0 != content_len);
  probe_logf("ASSERTS_DONE");

  header_malloced = malloc (header_len + 1);
  probe_logf("malloc header: requested=%zu+1 -> header_malloced=%p", header_len, (void *)header_malloced);
  if (NULL == header_malloced)
  {
    probe_logf("header_malloced == NULL -> returning false");
    return false;
  }

  memcpy (header_malloced, header, header_len);
  header_malloced[header_len] = 0;
  probe_logf("copied header to header_malloced=%p (len=%zu) -> "%"%s"" , (void *)header_malloced, header_len, header_malloced);

  value_malloced = malloc (content_len + 1);
  probe_logf("malloc value: requested=%zu+1 -> value_malloced=%p", content_len, (void *)value_malloced);
  if (NULL != value_malloced)
  {
    memcpy (value_malloced, content, content_len);
    value_malloced[content_len] = 0;
    probe_logf("copied content to value_malloced=%p (len=%zu) -> "%"%s"" , (void *)value_malloced, content_len, value_malloced);

    probe_logf("calling MHD_add_response_entry_no_alloc_ with response=%p kind=%d header_malloced=%p header_len=%zu value_malloced=%p content_len=%zu",
               (void *)response, (int)kind, (void *)header_malloced, header_len, (void *)value_malloced, content_len);

    if (MHD_add_response_entry_no_alloc_ (response, kind,
                                          header_malloced, header_len,
                                          value_malloced, content_len))
    {
      probe_logf("MHD_add_response_entry_no_alloc_ returned true -> success exit");
      return true; /* Success exit point */
    }

    probe_logf("MHD_add_response_entry_no_alloc_ returned false -> freeing value_malloced=%p", (void *)value_malloced);
    free (value_malloced);
  }
  else
  {
    probe_logf("value_malloced == NULL");
  }

  probe_logf("freeing header_malloced=%p and returning false", (void *)header_malloced);
  free (header_malloced);

  return false; /* Failure exit point */
}
