#include <stdio.h>
#include <time.h>
#include <unistd.h>

/* Probe logging helper: append human-readable logs about control flow
 * and connection state to /tmp/logs/82.txt. This function is non-intrusive
 * (only writes logs) and preserves original behavior. */
static void
mhd_log_probe_conn (const char *event, struct MHD_Connection *connection, const char *extra)
{
  FILE *f = fopen("/tmp/logs/82.txt", "a");
  if (f)
    {
      time_t t = time (NULL);
      pid_t pid = getpid ();
      if (connection != NULL)
        {
          const char *state_str = "(unknown)";
          /* Try to obtain a human-readable state, but guard against NULL. */
          /* MHD_state_to_string is expected to be available in the original build. */
          state_str = MHD_state_to_string (connection->state);
          fprintf (f, "%ld pid=%d EVENT=%s conn=%p state=%d (%s) tls_state=%d extra=%s\n",
                   (long) t, (int) pid, event, (void *) connection,
                   connection->state, state_str, connection->tls_state,
                   (extra ? extra : "(none)"));
        }
      else
        {
          fprintf (f, "%ld pid=%d EVENT=%s conn=NULL extra=%s\n",
                   (long) t, (int) pid, event, (extra ? extra : "(none)"));
        }
      fclose (f);
    }
}

static void
MHD_connection_update_event_loop_info (struct MHD_Connection *connection)
{
  /* Log entry and initial state */
  mhd_log_probe_conn ("enter", connection, NULL);

  /* Do not update states of suspended connection */
  if (connection->suspended)
    {
      mhd_log_probe_conn ("suspended-return", connection, "suspended flag set");
      return; /* States will be updated after resume. */
    }
#ifdef HTTPS_SUPPORT
  if (MHD_TLS_CONN_NO_TLS != connection->tls_state)
  {   /* HTTPS connection. */
    mhd_log_probe_conn ("tls-branch", connection, NULL);
    switch (connection->tls_state)
    {
    case MHD_TLS_CONN_INIT:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_log_probe_conn ("tls-init", connection, "set READ");
      return;
    case MHD_TLS_CONN_HANDSHAKING:
    case MHD_TLS_CONN_WR_CLOSING:
      if (0 == gnutls_record_get_direction (connection->tls_session))
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      else
        connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_log_probe_conn ("tls-handshake/wr_closing", connection, (0 == gnutls_record_get_direction (connection->tls_session)) ? "set READ" : "set WRITE");
      return;
    case MHD_TLS_CONN_CONNECTED:
      mhd_log_probe_conn ("tls-connected", connection, "fallthrough to normal processing");
      break; /* Do normal processing */
    case MHD_TLS_CONN_WR_CLOSED:
    case MHD_TLS_CONN_TLS_FAILED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      mhd_log_probe_conn ("tls-closed/failed", connection, "set CLEANUP");
      return;
    case MHD_TLS_CONN_TLS_CLOSING:  /* Not implemented yet */
    case MHD_TLS_CONN_TLS_CLOSED:   /* Not implemented yet */
    case MHD_TLS_CONN_INVALID_STATE:
    case MHD_TLS_CONN_NO_TLS: /* Not possible */
    default:
      mhd_log_probe_conn ("tls-invalid", connection, "about to panic");
      MHD_PANIC (_ ("Invalid TLS state value.\n"));
    }
  }
#endif /* HTTPS_SUPPORT */
  while (1)
  {
#if DEBUG_STATES
    MHD_DLOG (connection->daemon,
              _ ("In function %s handling connection at state: %s\n"),
              MHD_FUNC_,
              MHD_state_to_string (connection->state));
#endif
    switch (connection->state)
    {
    case MHD_CONNECTION_INIT:
    case MHD_CONNECTION_REQ_LINE_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_log_probe_conn ("set-event", connection, "REQ_LINE_RECEIVING -> READ");
      break;
    case MHD_CONNECTION_REQ_LINE_RECEIVED:
      mhd_log_probe_conn ("assert-case", connection, "REQ_LINE_RECEIVED: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_REQ_HEADERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_log_probe_conn ("set-event", connection, "REQ_HEADERS_RECEIVING -> READ");
      break;
    case MHD_CONNECTION_HEADERS_RECEIVED:
    case MHD_CONNECTION_HEADERS_PROCESSED:
      mhd_log_probe_conn ("assert-case", connection, "HEADERS_RECEIVED/PROCESSED: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CONTINUE_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_log_probe_conn ("set-event", connection, "CONTINUE_SENDING -> WRITE");
      break;
    case MHD_CONNECTION_BODY_RECEIVING:
      if ((connection->rq.some_payload_processed) &&
          has_unprocessed_upload_body_data_in_buffer (connection))
      {
        /* Some data was processed, the buffer must have some free space */
        mhd_assert (connection->read_buffer_offset < \
                    connection->read_buffer_size);
        if (! connection->rq.have_chunked_upload)
        {
          /* Not a chunked upload. Do not read more than necessary to
             process the current request. */
          if (connection->rq.remaining_upload_size >=
              connection->read_buffer_offset)
            {
              connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
              mhd_log_probe_conn ("set-event", connection, "BODY_RECEIVING -> PROCESS");
            }
          else
            {
              connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
              mhd_log_probe_conn ("set-event", connection, "BODY_RECEIVING -> PROCESS_READ");
            }
        }
        else
        {
          /* Chunked upload. The size of the current request is unknown.
             Continue reading as the space in the read buffer is available. */
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS_READ;
          mhd_log_probe_conn ("set-event", connection, "BODY_RECEIVING (chunked) -> PROCESS_READ");
        }
      }
      else
        {
          connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
          mhd_log_probe_conn ("set-event", connection, "BODY_RECEIVING -> READ");
        }
      break;
    case MHD_CONNECTION_BODY_RECEIVED:
      mhd_log_probe_conn ("assert-case", connection, "BODY_RECEIVED: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_READ;
      mhd_log_probe_conn ("set-event", connection, "FOOTERS_RECEIVING -> READ");
      break;
    case MHD_CONNECTION_FOOTERS_RECEIVED:
      mhd_log_probe_conn ("assert-case", connection, "FOOTERS_RECEIVED: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FULL_REQ_RECEIVED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      mhd_log_probe_conn ("set-event", connection, "FULL_REQ_RECEIVED -> PROCESS");
      break;
    case MHD_CONNECTION_START_REPLY:
      mhd_log_probe_conn ("assert-case", connection, "START_REPLY: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_HEADERS_SENDING:
      /* headers in buffer, keep writing */
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_log_probe_conn ("set-event", connection, "HEADERS_SENDING -> WRITE");
      break;
    case MHD_CONNECTION_HEADERS_SENT:
      mhd_log_probe_conn ("assert-case", connection, "HEADERS_SENT: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_NORMAL_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      mhd_log_probe_conn ("set-event", connection, "NORMAL_BODY_UNREADY -> PROCESS");
      break;
    case MHD_CONNECTION_NORMAL_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_log_probe_conn ("set-event", connection, "NORMAL_BODY_READY -> WRITE");
      break;
    case MHD_CONNECTION_CHUNKED_BODY_UNREADY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_PROCESS;
      mhd_log_probe_conn ("set-event", connection, "CHUNKED_BODY_UNREADY -> PROCESS");
      break;
    case MHD_CONNECTION_CHUNKED_BODY_READY:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_log_probe_conn ("set-event", connection, "CHUNKED_BODY_READY -> WRITE");
      break;
    case MHD_CONNECTION_CHUNKED_BODY_SENT:
      mhd_log_probe_conn ("assert-case", connection, "CHUNKED_BODY_SENT: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_FOOTERS_SENDING:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_WRITE;
      mhd_log_probe_conn ("set-event", connection, "FOOTERS_SENDING -> WRITE");
      break;
    case MHD_CONNECTION_FULL_REPLY_SENT:
      mhd_log_probe_conn ("assert-case", connection, "FULL_REPLY_SENT: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
    case MHD_CONNECTION_CLOSED:
      connection->event_loop_info = MHD_EVENT_LOOP_INFO_CLEANUP;
      mhd_log_probe_conn ("closed", connection, "set CLEANUP and return");
      return;           /* do nothing, not even reading */
#ifdef UPGRADE_SUPPORT
    case MHD_CONNECTION_UPGRADE:
      mhd_log_probe_conn ("assert-case", connection, "UPGRADE: hitting mhd_assert(0)");
      mhd_assert (0);
      break;
#endif /* UPGRADE_SUPPORT */
    default:
      mhd_log_probe_conn ("assert-case", connection, "default: hitting mhd_assert(0)");
      mhd_assert (0);
    }

    if (0 != (MHD_EVENT_LOOP_INFO_READ & connection->event_loop_info))
    {
      /* Check whether the space is available to receive data */
      if (! check_and_grow_read_buffer_space (connection))
      {
        mhd_log_probe_conn ("buffer-grow-failed", connection, connection->discard_request ? "discard_request true" : "discard_request false");
        mhd_assert (connection->discard_request);
        continue;
      }
      mhd_log_probe_conn ("buffer-space-ok", connection, "ready to READ");
    }
    break; /* Everything was processed. */
  }

  mhd_log_probe_conn ("exit", connection, NULL);
}
