#include <stdio.h>

/* Probe logging helper: append concise state and control-flow info to a file. */
static void
mhd_pp_log_state (const char *stage,
                  struct MHD_PostProcessor *ret,
                  size_t buffer_size,
                  size_t blen,
                  const char *boundary,
                  const char *encoding)
{
  FILE *f = fopen("/tmp/logs/2272.txt", "a");
  if (NULL == f)
    return;

  fprintf (f, "--- MHD_create_post_processor: %s\n", stage);
  fprintf (f, " buffer_size=%zu blen=%zu\n", buffer_size, blen);
  fprintf (f, " boundary=%p encoding=%p\n", (void *)boundary, (void *)encoding);
  fprintf (f, " RN_Inactive=%ld\n", (long) RN_Inactive);
  if (NULL != ret)
    {
      /* Print important members that are set around allocation and the
         skip_rn member which is relevant for the reported dead-code
         scenario. Casts to long/size_t are used to get printable values
         without knowing exact enum/integer widths here. */
      fprintf (f, " ret=%p connection=%p ikvi=%p cls=%p\n",
               (void *) ret,
               (void *) ret->connection,
               (void *) ret->ikvi,
               (void *) ret->cls);
      fprintf (f, " ret->encoding=%p ret->buffer_size=%zu ret->state=%d\n",
               (void *) ret->encoding,
               (size_t) ret->buffer_size,
               (int) ret->state);
      fprintf (f, " ret->blen=%zu ret->boundary=%p ret->skip_rn=%ld\n",
               (size_t) ret->blen,
               (void *) ret->boundary,
               (long) ret->skip_rn);
    }
  fprintf (f, "--- end %s\n\n", stage);
  fclose (f);
}

_MHD_EXTERN struct MHD_PostProcessor *
MHD_create_post_processor (struct MHD_Connection *connection,
                           size_t buffer_size,
                           MHD_PostDataIterator iter,
                           void *iter_cls)
{
  struct MHD_PostProcessor *ret;
  const char *encoding;
  const char *boundary;
  size_t blen;

  if ( (buffer_size < 256) ||
       (NULL == connection) ||
       (NULL == iter))
    MHD_PANIC (_ ("libmicrohttpd API violation.\n"));
  encoding = NULL;
  if (MHD_NO ==
      MHD_lookup_connection_value_n (connection,
                                     MHD_HEADER_KIND,
                                     MHD_HTTP_HEADER_CONTENT_TYPE,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_HEADER_CONTENT_TYPE),
                                     &encoding,
                                     NULL))
    return NULL;
  mhd_assert (NULL != encoding);
  boundary = NULL;
  if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_FORM_URLENCODED,
                                   encoding,
                                   MHD_STATICSTR_LEN_ (
                                     MHD_HTTP_POST_ENCODING_FORM_URLENCODED)))
  {
    if (! MHD_str_equal_caseless_n_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA,
                                     encoding,
                                     MHD_STATICSTR_LEN_ (
                                       MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)))
      return NULL;
    boundary =
      &encoding[MHD_STATICSTR_LEN_ (MHD_HTTP_POST_ENCODING_MULTIPART_FORMDATA)];
    /* Q: should this be "strcasestr"? */
    boundary = strstr (boundary, "boundary=");
    if (NULL == boundary)
      return NULL; /* failed to determine boundary */
    boundary += MHD_STATICSTR_LEN_ ("boundary=");
    blen = strlen (boundary);
    if ( (blen < 2) ||
         (blen * 2 + 2 > buffer_size) )
      return NULL;              /* (will be) out of memory or invalid boundary */
    if ( (boundary[0] == '"') &&
         (boundary[blen - 1] == '"') )
    {
      /* remove enclosing quotes */
      ++boundary;
      blen -= 2;
    }
  }
  else
    blen = 0;
  buffer_size += 4; /* round up to get nice block sizes despite boundary search */

  /* log state just before allocation */
  mhd_pp_log_state ("pre_alloc", NULL, buffer_size, blen, boundary, encoding);

  /* add +1 to ensure we ALWAYS have a zero-termination at the end */
  if (NULL == (ret = MHD_calloc_ (1, sizeof (struct MHD_PostProcessor)
                                  + buffer_size + 1)))
    return NULL;

  /* Log state right after allocation: allocated memory will be zeroed by
     MHD_calloc_, so this helps to reproduce whether ret->skip_rn already
     contains zero before the explicit assignment below. */
  mhd_pp_log_state ("post_alloc_before_init", ret, buffer_size, blen, boundary, encoding);

  ret->connection = connection;
  ret->ikvi = iter;
  ret->cls = iter_cls;
  ret->encoding = encoding;
  ret->buffer_size = buffer_size;
  ret->state = PP_Init;
  ret->blen = blen;
  ret->boundary = boundary;

  /* Log state immediately before assigning skip_rn to capture the
     previous contents (expected zero if calloc zero-initialised). */
  mhd_pp_log_state ("before_skip_rn_assign", ret, buffer_size, blen, boundary, encoding);

  ret->skip_rn = RN_Inactive;

  /* Log state after assignment to capture final value written. */
  mhd_pp_log_state ("after_skip_rn_assign", ret, buffer_size, blen, boundary, encoding);

  return ret;
}
