#include <stdio.h>
#include <stdarg.h>

static void mhd_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/103.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fputc('\n', f);
    fclose(f);
}

_MHD_EXTERN int
MHD_get_connection_values (struct MHD_Connection *connection,
                           enum MHD_ValueKind kind,
                           MHD_KeyValueIterator iterator,
                           void *iterator_cls)
{
  int ret;
  struct MHD_HTTP_Req_Header *pos;

  mhd_log("MHD_get_connection_values called: connection=%p kind=%d iterator=%p iterator_cls=%p",
          (void *)connection, (int)kind, (void *)iterator, iterator_cls);

  if (NULL == connection) {
    mhd_log("connection is NULL -> return -1");
    return -1;
  }

  ret = 0;
  for (pos = connection->rq.headers_received; NULL != pos; pos = pos->next) {
    mhd_log("visiting header node: pos=%p kind=0x%x header=%p value=%p",
            (void *)pos, (unsigned)pos->kind, (void *)pos->header, (void *)pos->value);

    if (pos->header)
      mhd_log("header string: %s", pos->header);
    else
      mhd_log("header string: <NULL>");

    if (pos->value)
      mhd_log("value string: %s", pos->value);
    else
      mhd_log("value string: <NULL>");

    if (0 != (pos->kind & kind))
    {
      ret++;
      mhd_log("kind matches (pos->kind & kind) -> ret now %d; iterator=%p", ret, (void *)iterator);
      if ( (NULL != iterator) &&
           (MHD_NO == iterator (iterator_cls,
                                pos->kind,
                                pos->header,
                                pos->value)) )
      {
        mhd_log("iterator returned MHD_NO -> returning ret=%d", ret);
        return ret;
      }
      else
      {
        if (NULL == iterator)
          mhd_log("iterator is NULL, continuing");
        else
          mhd_log("iterator returned non-MHD_NO, continuing");
      }
    }
    else
    {
      mhd_log("kind does not match (pos->kind & kind)==0, continuing");
    }
  }
  mhd_log("completed traversal, returning ret=%d", ret);
  return ret;
}
