/* Probe logging helpers: append human-readable traces to /tmp/logs/773.txt */
static void mhd_log_printf (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/773.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputs ("\n", f);
  fclose (f);
}

