#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>

/* Probe logger: append messages to /tmp/logs/1401.txt */
static void mhd_probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1401.txt", "a");
    if (!f)
        return;

    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);

    fprintf(f, "\n");
    fclose(f);
}

