#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>

/* Probe logging helper: append simple messages to /tmp/logs/153.txt */
static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/153.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

_MHD_EXTERN enum MHD_Result
MHD_queue_response (struct MHD_Connection *connection,
                    unsigned int status_code,
                    struct MHD_Response *response)
{
  struct MHD_Daemon *daemon;
  bool reply_icy;

  if ((NULL == connection) || (NULL == response))
  {
    mhd_probe_log("MHD_queue_response: early return - null param: connection=%p response=%p", (void *)connection, (void *)response);
    return MHD_NO;
  }

  daemon = connection->daemon;
  mhd_probe_log("MHD_queue_response: assigned daemon=%p from connection=%p", (void *)daemon, (void *)connection);

  if ((! connection->in_access_handler) && (! connection->suspended) &&
      MHD_D_IS_USING_THREADS_ (daemon))
  {
    mhd_probe_log("MHD_queue_response: refused - not in access handler, not suspended and daemon uses threads: in_access_handler=%d suspended=%d options=0x%x",
                  (int)connection->in_access_handler, (int)connection->suspended,
                  (unsigned int)daemon->options);
    return MHD_NO;
  }

  reply_icy = (0 != (status_code & MHD_ICY_FLAG));
  status_code &= ~MHD_ICY_FLAG;
  mhd_probe_log("MHD_queue_response: status_code adjusted=%u reply_icy=%d", status_code, (int)reply_icy);

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  if ( (! connection->suspended) &&
       MHD_D_IS_USING_THREADS_ (daemon) &&
       (! MHD_thread_handle_ID_is_current_thread_ (connection->tid)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Attempted to queue response on wrong thread!\n"));
#endif
    mhd_probe_log("MHD_queue_response: refused - attempted to queue response on wrong thread: suspended=%d daemon_options=0x%x tid=%p",
                  (int)connection->suspended, (unsigned int)daemon->options, (void *)connection->tid);
    return MHD_NO;
  }
#endif

  if (NULL != connection->rp.response)
  {
    mhd_probe_log("MHD_queue_response: refused - response already set: connection->rp.response=%p", (void *)connection->rp.response);
    return MHD_NO; /* The response was already set */
  }

  if ( (MHD_CONNECTION_HEADERS_PROCESSED != connection->state) &&
       (MHD_CONNECTION_FULL_REQ_RECEIVED != connection->state) )
  {
    mhd_probe_log("MHD_queue_response: refused - wrong connection state=%d", (int)connection->state);
    return MHD_NO; /* Wrong connection state */
  }

  mhd_probe_log("MHD_queue_response: daemon->shutdown=%d daemon->options=0x%x daemon->sigpipe_blocked=%d",
                (int)daemon->shutdown, (unsigned int)daemon->options, (int)daemon->sigpipe_blocked);

  if (daemon->shutdown)
  {
    mhd_probe_log("MHD_queue_response: refused - daemon shutting down");
    return MHD_NO;
  }

#ifdef UPGRADE_SUPPORT
  if (NULL != response->upgrade_handler)
  {
    struct MHD_HTTP_Res_Header *conn_header;
    if (0 == (daemon->options & MHD_ALLOW_UPGRADE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Attempted 'upgrade' connection on daemon without" \
                   " MHD_ALLOW_UPGRADE option!\n"));
#endif
      mhd_probe_log("MHD_queue_response: refused - upgrade requested but daemon lacks MHD_ALLOW_UPGRADE option: daemon->options=0x%x", (unsigned int)daemon->options);
      return MHD_NO;
    }
    if (MHD_HTTP_SWITCHING_PROTOCOLS != status_code)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid status code for" \
                   " 'upgrade' response!\n"));
#endif
      mhd_probe_log("MHD_queue_response: refused - upgrade response without 101 status: status_code=%u", status_code);
      return MHD_NO;
    }
    if (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"Connection\" header!\n"));
#endif
      mhd_probe_log("MHD_queue_response: refused - upgrade response missing Connection header: response=%p", (void *)response);
      return MHD_NO;
    }
    conn_header = response->first_header;
    mhd_assert (NULL != conn_header);
    mhd_assert (MHD_str_equal_caseless_ (conn_header->header,
                                         MHD_HTTP_HEADER_CONNECTION));
    if (! MHD_str_has_s_token_caseless_ (conn_header->value,
                                         "upgrade"))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used invalid response" \
                   " without \"upgrade\" token in" \
                   " \"Connection\" header!\n"));
#endif
      mhd_probe_log("MHD_queue_response: refused - upgrade response Connection header lacks 'upgrade' token: header_value=%s", conn_header->value ? conn_header->value : "(null)");
      return MHD_NO;
    }
    if (! MHD_IS_HTTP_VER_1_1_COMPAT (connection->rq.http_ver))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Connection \"Upgrade\" can be used only " \
                   "with HTTP/1.1 connections!\n"));
#endif
      mhd_probe_log("MHD_queue_response: refused - upgrade only for HTTP/1.1: http_ver=%d", (int)connection->rq.http_ver);
      return MHD_NO;
    }
  }
#endif /* UPGRADE_SUPPORT */
  if (MHD_HTTP_SWITCHING_PROTOCOLS == status_code)
  {
#ifdef UPGRADE_SUPPORT
    if (NULL == response->upgrade_handler)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Application used status code 101 \"Switching Protocols\" " \
                   "with non-'upgrade' response!\n"));
#endif /* HAVE_MESSAGES */
      mhd_probe_log("MHD_queue_response: refused - 101 status without upgrade handler");
      return MHD_NO;
    }
#else  /* ! UPGRADE_SUPPORT */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Application used status code 101 \"Switching Protocols\", " \
                 "but this MHD was built without \"Upgrade\" support!\n"));
#endif /* HAVE_MESSAGES */
    mhd_probe_log("MHD_queue_response: refused - 101 status but build lacks UPGRADE_SUPPORT");
    return MHD_NO;
#endif /* ! UPGRADE_SUPPORT */
  }
  if ( (100 > status_code) ||
       (999 < status_code) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Refused wrong status code (%u). " \
                 "HTTP requires three digits status code!\n"),
              status_code);
#endif
    mhd_probe_log("MHD_queue_response: refused - wrong status code (%u)", status_code);
    return MHD_NO;
  }
  if (200 > status_code)
  {
    if (MHD_HTTP_VER_1_0 == connection->rq.http_ver)
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 clients do not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_probe_log("MHD_queue_response: refused - 1xx status with HTTP/1.0 client: status=%u http_ver=%d", status_code, (int)connection->rq.http_ver);
      return MHD_NO;
    }
    if (0 != (response->flags & (MHD_RF_HTTP_1_0_COMPATIBLE_STRICT
                                 | MHD_RF_HTTP_1_0_SERVER)))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Wrong status code (%u) refused. " \
                   "HTTP/1.0 reply mode does not support 1xx status codes!\n"),
                (status_code));
#endif
      mhd_probe_log("MHD_queue_response: refused - 1xx status with HTTP/1.0 reply mode: response_flags=0x%x status=%u", (unsigned int)response->flags, status_code);
      return MHD_NO;
    }
  }
  if ( (MHD_HTTP_MTHD_CONNECT == connection->rq.http_mthd) &&
       (2 == status_code / 100) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Successful (%u) response code cannot be used to answer " \
                 "\"CONNECT\" request!\n"),
              (status_code));
#endif
    mhd_probe_log("MHD_queue_response: refused - CONNECT request answered with 2xx status: method=%d status=%u", (int)connection->rq.http_mthd, status_code);
    return MHD_NO;
  }

  if ( (0 != (MHD_RF_HEAD_ONLY_RESPONSE & response->flags)) &&
       (RP_BODY_HEADERS_ONLY < is_reply_body_needed (connection, status_code)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("HEAD-only response cannot be used when the request requires "
                 "reply body to be sent!\n"));
#endif
    mhd_probe_log("MHD_queue_response: refused - HEAD-only response but body required: response_flags=0x%x needed=%d", (unsigned int)response->flags, (int)is_reply_body_needed (connection, status_code));
    return MHD_NO;
  }

#ifdef HAVE_MESSAGES
  if ( (0 != (MHD_RF_INSANITY_HEADER_CONTENT_LENGTH & response->flags)) &&
       (0 != (MHD_RAF_HAS_CONTENT_LENGTH & response->flags_auto)) )
  {
    MHD_DLOG (daemon,
              _ ("The response has application-defined \"Content-Length\" " \
                 "header. The reply to the request will be not " \
                 "HTTP-compliant and may result in hung connection or " \
                 "other problems!\n"));
  }
#endif

  MHD_increment_response_rc (response);
  mhd_probe_log("MHD_queue_response: MHD_increment_response_rc called for response=%p (rc changed)", (void *)response);

  connection->rp.response = response;
  connection->rp.responseCode = status_code;
  connection->rp.responseIcy = reply_icy;
  mhd_probe_log("MHD_queue_response: connection->rp set: response=%p responseCode=%u responseIcy=%d",
                (void *)connection->rp.response, (unsigned int)connection->rp.responseCode, (int)connection->rp.responseIcy);

#if defined(_MHD_HAVE_SENDFILE)
  if ( (response->fd == -1) ||
       (response->is_pipe) ||
       (0 != (connection->daemon->options & MHD_USE_TLS))
#if defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED) && \
       defined(MHD_SEND_SPIPE_SUPPRESS_POSSIBLE)
       || (! daemon->sigpipe_blocked && ! connection->sk_spipe_suppress)
#endif /* MHD_SEND_SPIPE_SUPPRESS_NEEDED &&
          MHD_SEND_SPIPE_SUPPRESS_POSSIBLE */
       )
    connection->rp.resp_sender = MHD_resp_sender_std;
  else
    connection->rp.resp_sender = MHD_resp_sender_sendfile;
  mhd_probe_log("MHD_queue_response: sendfile decision: response->fd=%d is_pipe=%d daemon->options=0x%x daemon->sigpipe_blocked=%d connection->sk_spipe_suppress=%d selected_sender=%p",
                (int)response->fd, (int)response->is_pipe, (unsigned int)connection->daemon->options, (int)daemon->sigpipe_blocked, (int)connection->sk_spipe_suppress, (void *)connection->rp.resp_sender);
#endif /* _MHD_HAVE_SENDFILE */
  /* FIXME: if 'is_pipe' is set, TLS is off, and we have *splice*, we could use splice()
     to avoid two user-space copies... */

  if ( (MHD_HTTP_MTHD_HEAD == connection->rq.http_mthd) ||
       (MHD_HTTP_OK > status_code) ||
       (MHD_HTTP_NO_CONTENT == status_code) ||
       (MHD_HTTP_NOT_MODIFIED == status_code) )
  {
    /* if this is a "HEAD" request, or a status code for
       which a body is not allowed, pretend that we
       have already sent the full message body. */
    /* TODO: remove the next assignment, use 'rp_props.send_reply_body' in
     * checks */
    connection->rp.rsp_write_position = response->total_size;
    mhd_probe_log("MHD_queue_response: adjusted rsp_write_position to total_size=%llu", (unsigned long long)response->total_size);
  }
  if (MHD_CONNECTION_HEADERS_PROCESSED == connection->state)
  {
    /* response was queued "early", refuse to read body / footers or
       further requests! */
    connection->discard_request = true;
    connection->state = MHD_CONNECTION_START_REPLY;
    connection->rq.remaining_upload_size = 0;
    mhd_probe_log("MHD_queue_response: queued early - set discard_request=true state=%d remaining_upload_size=0", (int)connection->state);
  }
  if (! connection->in_idle)
    (void) MHD_connection_handle_idle (connection);
  MHD_update_last_activity_ (connection);
  mhd_probe_log("MHD_queue_response: success - returning MHD_YES for connection=%p response=%p status=%u",
                (void *)connection, (void *)response, (unsigned int)status_code);
  return MHD_YES;
}
