static void MHD_log_probe(struct MHD_Daemon *daemon,
                              const char *event,
                              int32_t millisec,
                              int err_state)
{
  FILE *f = fopen("/tmp/logs/987.txt", "a");
  if (NULL == f)
    return;
  /* Log basic state: event, daemon pointer, millisec, err_state */
  fprintf(f, "MHD_select: %s: daemon=%p millisec=%ld err_state=%d\n",
          event, (void *) daemon, (long) millisec, err_state);
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  if (daemon->shutdown)
  {
    /* log that we are returning early due to shutdown */
    MHD_log_probe(daemon, "shutdown_before_return", millisec, -1);
    return MHD_NO;
  }

  /* log entry with initial millisec and err_state unknown (-1) */
  MHD_log_probe(daemon, "entry_after_shutdown_check", millisec, -1);

  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;

  /* Log state before potential suspend/resume modification */
  MHD_log_probe(daemon, "before_suspend_resume_check", millisec, err_state);

  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
  {
    millisec = 0;
    /* Log that millisec was set to 0 due to suspend/resume handling */
    MHD_log_probe(daemon, "after_suspend_resume_set_millisec_0", millisec, err_state);
  }

  /* Log state after suspend/resume check */
  MHD_log_probe(daemon, "after_suspend_resume_check", millisec, err_state);

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      err_state = MHD_YES;
      /* Log error state change */
      MHD_log_probe(daemon, "internal_get_fdset2_failed", millisec, err_state);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
        /* Log error state when adding ITC fd fails */
        MHD_log_probe(daemon, "itc_add_failed", millisec, err_state);
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          err_state = MHD_YES;
          /* Log error state when adding listen socket fails */
          MHD_log_probe(daemon, "listen_add_failed", millisec, err_state);
        }
      }
    }
  }

  /* Log state before the err_state based millisec reset */
  MHD_log_probe(daemon, "before_err_state_check", millisec, err_state);
  if (MHD_NO != err_state)
  {
    millisec = 0;
    /* Log that millisec was set to 0 due to err_state != MHD_NO */
    MHD_log_probe(daemon, "after_err_state_set_millisec_0", millisec, err_state);
  }

  /* Log state before timeout calculation/select */
  MHD_log_probe(daemon, "before_timeout_calc", millisec, err_state);

  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
    }
  }

  /* Log just before calling select to capture final millisec/tv state */
  MHD_log_probe(daemon, "just_before_select", millisec, err_state);
  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);

  /* Log result of select */
  MHD_log_probe(daemon, "after_select", millisec, err_state);

  if (daemon->shutdown)
  {
    MHD_log_probe(daemon, "shutdown_after_select_return", millisec, err_state);
    return MHD_NO;
  }
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      MHD_log_probe(daemon, "select_eintr_return", millisec, err_state);
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    MHD_log_probe(daemon, "select_failed_return", millisec, err_state);
    return MHD_NO;
  }
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    MHD_log_probe(daemon, "internal_run_from_select_nonzero_return", millisec, err_state);
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  MHD_log_probe(daemon, "exit_normal_return", millisec, err_state);
  return MHD_NO;
}
