#include <stdio.h>
#include <stdarg.h>

/* Probe logging helper - append-only to avoid affecting behavior much */
static void mhd_probe_log(const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/982.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
MHD_select (struct MHD_Daemon *daemon,
            int32_t millisec)
{
  int num_ready;
  fd_set rs;
  fd_set ws;
  fd_set es;
  MHD_socket maxsock;
  struct timeval timeout;
  struct timeval *tv;
  int err_state;
  MHD_socket ls;

  /* Initial assignment that is suspected dead code. Log it. */
  timeout.tv_sec = 0;
  timeout.tv_usec = 0;
  mhd_probe_log("MHD_select: initial assignment -> timeout.tv_sec=%lld, timeout.tv_usec=%lld, millisec=%d",
                (long long) timeout.tv_sec,
                (long long) timeout.tv_usec,
                (int) millisec);

  if (daemon->shutdown)
    return MHD_NO;
  FD_ZERO (&rs);
  FD_ZERO (&ws);
  FD_ZERO (&es);
  maxsock = MHD_INVALID_SOCKET;
  err_state = MHD_NO;

  /* Log options/resume path and any change to millisec */
  if ( (0 != (daemon->options & MHD_TEST_ALLOW_SUSPEND_RESUME)) &&
       (MHD_NO != resume_suspended_connections (daemon)) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) )
  {
    mhd_probe_log("MHD_select: suspend/resume path taken, setting millisec=0 (was %d)", (int) millisec);
    millisec = 0;
  }
  else
  {
    mhd_probe_log("MHD_select: suspend/resume path not taken, millisec=%d", (int) millisec);
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* single-threaded, go over everything */
    if (MHD_NO ==
        internal_get_fdset2 (daemon,
                             &rs,
                             &ws,
                             &es,
                             &maxsock,
                             (int) FD_SETSIZE))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Could not obtain daemon fdsets.\n"));
#endif
      err_state = MHD_YES;
      mhd_probe_log("MHD_select: internal_get_fdset2 failed -> err_state set to MHD_YES");
    }
    else
    {
      mhd_probe_log("MHD_select: internal_get_fdset2 succeeded -> maxsock=%lld", (long long) maxsock);
    }
  }
  else
  {
    bool itc_added;
    /* accept only, have one thread per connection */
    itc_added = false;
    if (MHD_ITC_IS_VALID_ (daemon->itc))
    {
      itc_added = MHD_add_to_fd_set_ (MHD_itc_r_fd_ (daemon->itc),
                                      &rs,
                                      &maxsock,
                                      (int) FD_SETSIZE);
      mhd_probe_log("MHD_select: attempted to add ITC fd -> itc_added=%d, maxsock=%lld", itc_added ? 1 : 0, (long long) maxsock);
      if (! itc_added)
      {
#ifdef HAVE_MESSAGES
        MHD_DLOG (daemon, _ ("Could not add control inter-thread " \
                             "communication channel FD to fdset.\n"));
#endif
        err_state = MHD_YES;
        mhd_probe_log("MHD_select: failed to add ITC fd -> err_state set to MHD_YES");
      }
    }
    if ( (MHD_INVALID_SOCKET != (ls = daemon->listen_fd)) &&
         (! daemon->was_quiesced) )
    {
      /* Stop listening if we are at the configured connection limit */
      /* If we're at the connection limit, no point in really
         accepting new connections; however, make sure we do not miss
         the shutdown OR the termination of an existing connection; so
         only do this optimisation if we have a signaling ITC in
         place. */
      if (! itc_added ||
          ((daemon->connections < daemon->connection_limit) &&
           ! daemon->at_limit))
      {
        if (! MHD_add_to_fd_set_ (ls,
                                  &rs,
                                  &maxsock,
                                  (int) FD_SETSIZE))
        {
#ifdef HAVE_MESSAGES
          MHD_DLOG (daemon,
                    _ ("Could not add listen socket to fdset.\n"));
#endif
          err_state = MHD_YES;
          mhd_probe_log("MHD_select: failed to add listen fd -> err_state set to MHD_YES");
        }
        else
        {
          mhd_probe_log("MHD_select: added listen fd -> ls=%lld, maxsock=%lld", (long long) ls, (long long) maxsock);
        }
      }
      else
      {
        mhd_probe_log("MHD_select: not adding listen fd due to limits: itc_added=%d, connections=%d, limit=%d, at_limit=%d",
                      itc_added ? 1 : 0,
                      (int) daemon->connections,
                      (int) daemon->connection_limit,
                      daemon->at_limit ? 1 : 0);
      }
    }
  }

  if (MHD_NO != err_state)
  {
    mhd_probe_log("MHD_select: err_state != MHD_NO -> forcing millisec=0 (was %d)", (int) millisec);
    millisec = 0;
  }

  if (0 == millisec)
  {
    timeout.tv_usec = 0;
    timeout.tv_sec = 0;
    tv = &timeout;
    mhd_probe_log("MHD_select: millisec==0 branch -> timeout reset to tv_sec=%lld, tv_usec=%lld, tv=%p",
                  (long long) timeout.tv_sec,
                  (long long) timeout.tv_usec,
                  (void *) tv);
  }
  else
  {
    uint64_t mhd_tmo;
    uint64_t select_tmo;

    if ( (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon)) &&
         (MHD_NO != MHD_get_timeout64 (daemon, &mhd_tmo)) )
    {
      if ( (0 < millisec) &&
           (mhd_tmo > (uint64_t) millisec) )
        select_tmo = (uint64_t) millisec;
      else
        select_tmo = mhd_tmo;
      tv = &timeout; /* have timeout value */
      mhd_probe_log("MHD_select: using mhd timeout -> mhd_tmo=%llu, millisec=%d, select_tmo=%llu, tv=%p",
                    (unsigned long long) mhd_tmo,
                    (int) millisec,
                    (unsigned long long) select_tmo,
                    (void *) tv);
    }
    else if (0 < millisec)
    {
      select_tmo = (uint64_t) millisec;
      tv = &timeout; /* have timeout value */
      mhd_probe_log("MHD_select: using millisec directly -> select_tmo=%llu, tv=%p",
                    (unsigned long long) select_tmo,
                    (void *) tv);
    }
    else
    {
      select_tmo = 0; /* Not actually used, silent compiler warning */
      tv = NULL;
      mhd_probe_log("MHD_select: no timeout -> tv set to NULL");
    }

    if (NULL != tv)
    { /* have timeout value */
#if (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC
      if (select_tmo / 1000 > TIMEVAL_TV_SEC_MAX)
        timeout.tv_sec = TIMEVAL_TV_SEC_MAX;
      else
#endif /* (SIZEOF_UINT64_T - 2) >= SIZEOF_STRUCT_TIMEVAL_TV_SEC */
      timeout.tv_sec = (_MHD_TIMEVAL_TV_SEC_TYPE) (select_tmo / 1000);

      timeout.tv_usec = ((uint16_t) (select_tmo % 1000)) * ((int32_t) 1000);
      mhd_probe_log("MHD_select: computed timeout -> select_tmo=%llu, timeout.tv_sec=%lld, timeout.tv_usec=%lld",
                    (unsigned long long) select_tmo,
                    (long long) timeout.tv_sec,
                    (long long) timeout.tv_usec);
    }
  }

  mhd_probe_log("MHD_select: about to call select -> maxsock=%lld, tv=%p, timeout.tv_sec=%lld, timeout.tv_usec=%lld",
                (long long) maxsock,
                (void *) tv,
                (long long) timeout.tv_sec,
                (long long) timeout.tv_usec);

  num_ready = MHD_SYS_select_ (maxsock + 1,
                               &rs,
                               &ws,
                               &es,
                               tv);

  mhd_probe_log("MHD_select: select returned num_ready=%d", num_ready);

  if (daemon->shutdown)
    return MHD_NO;
  if (num_ready < 0)
  {
    const int err = MHD_socket_get_error_ ();
    mhd_probe_log("MHD_select: select error -> err=%d", err);
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_probe_log("MHD_select: select interrupted by signal (EINTR), returning based on err_state=%d", err_state);
      return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
    }
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("select failed: %s\n"),
              MHD_socket_strerr_ (err));
#endif
    return MHD_NO;
  }
  if (MHD_NO != internal_run_from_select (daemon,
                                          &rs,
                                          &ws,
                                          &es,
                                          (int) FD_SETSIZE))
  {
    mhd_probe_log("MHD_select: internal_run_from_select returned MHD_YES (or non-MHD_NO), returning based on err_state=%d", err_state);
    return (MHD_NO == err_state) ? MHD_YES : MHD_NO;
  }
  mhd_probe_log("MHD_select: exiting normally (no work) -> returning MHD_NO");
  return MHD_NO;
}
