#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <stdbool.h>
#include <stdint.h>

/* Probe logging helper. Appends human-readable logs to /tmp/logs/1757.txt */
static void mhd_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/1757.txt", "a");
    if (!f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    fprintf(f, "\n");
    va_end(ap);
    fclose(f);
}

ssize_t
MHD_send_data_ (struct MHD_Connection *connection,
                const char *buffer,
                size_t buffer_size,
                bool push_data)
{
  MHD_socket s = connection->socket_fd;
  ssize_t ret;
#ifdef HTTPS_SUPPORT
  const bool tls_conn = (connection->daemon->options & MHD_USE_TLS);
#else  /* ! HTTPS_SUPPORT */
  const bool tls_conn = false;
#endif /* ! HTTPS_SUPPORT */

  mhd_log("ENTRY: connection=%p s=%lld buffer=%p buffer_size=%zu push_data=%d tls_conn=%d state=%d",
          (void *)connection, (long long)s, (const void *)buffer, buffer_size, (int)push_data, (int)tls_conn, (int)connection->state);

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_log("EARLY_RETURN: Not connected. s=%lld state=%d", (long long)s, (int)connection->state);
    return MHD_ERR_NOTCONN_;
  }

  if (buffer_size > SSIZE_MAX)
  {
    mhd_log("PARAM_ADJUST: buffer_size (%zu) > SSIZE_MAX. About to set buffer_size=SSIZE_MAX and push_data=false", buffer_size);
    buffer_size = SSIZE_MAX; /* Max return value */
    push_data = false; /* Incomplete send */
    mhd_log("PARAM_ADJUSTED: buffer_size=%zu push_data=%d", buffer_size, (int)push_data);
  }

  if (tls_conn)
  {
#ifdef HTTPS_SUPPORT
    mhd_log("TLS_PATH: calling pre_send_setopt with push_data=%d", (int)push_data);
    pre_send_setopt (connection, (! tls_conn), push_data);
    ret = gnutls_record_send (connection->tls_session,
                              buffer,
                              buffer_size);
    mhd_log("TLS_SEND_RET: ret=%zd", (ssize_t)ret);
    if (GNUTLS_E_AGAIN == ret)
    {
#ifdef EPOLL_SUPPORT
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif
      mhd_log("TLS_EAGAIN: returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if (GNUTLS_E_INTERRUPTED == ret)
    {
      mhd_log("TLS_INTERRUPTED: returning MHD_ERR_AGAIN_");
      return MHD_ERR_AGAIN_;
    }
    if ( (GNUTLS_E_ENCRYPTION_FAILED == ret) ||
         (GNUTLS_E_INVALID_SESSION == ret) ||
         (GNUTLS_E_COMPRESSION_FAILED == ret) ||
         (GNUTLS_E_EXPIRED == ret) ||
         (GNUTLS_E_HASH_FAILED == ret) )
    {
      mhd_log("TLS_FAIL: returning MHD_ERR_TLS_ (ret=%zd)", (ssize_t)ret);
      return MHD_ERR_TLS_;
    }
    if ( (GNUTLS_E_PUSH_ERROR == ret) ||
         (GNUTLS_E_INTERNAL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_IOCTL_ERROR == ret) ||
         (GNUTLS_E_CRYPTODEV_DEVICE_ERROR == ret) )
    {
      mhd_log("TLS_PIPE_ERROR: returning MHD_ERR_PIPE_ (ret=%zd)", (ssize_t)ret);
      return MHD_ERR_PIPE_;
    }
#if defined(GNUTLS_E_PREMATURE_TERMINATION)
    if (GNUTLS_E_PREMATURE_TERMINATION == ret)
    {
      mhd_log("TLS_PREMATURE_TERMINATION: returning MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#elif defined(GNUTLS_E_UNEXPECTED_PACKET_LENGTH)
    if (GNUTLS_E_UNEXPECTED_PACKET_LENGTH == ret)
    {
      mhd_log("TLS_UNEXPECTED_PACKET_LENGTH: returning MHD_ERR_CONNRESET_");
      return MHD_ERR_CONNRESET_;
    }
#endif /* GNUTLS_E_UNEXPECTED_PACKET_LENGTH */
    if (GNUTLS_E_MEMORY_ERROR == ret)
    {
      mhd_log("TLS_MEMORY_ERROR: returning MHD_ERR_NOMEM_");
      return MHD_ERR_NOMEM_;
    }
    if (ret < 0)
    {
      /* Treat any other error as hard error. */
      mhd_log("TLS_OTHER_ERROR: ret=%zd returning MHD_ERR_NOTCONN_", (ssize_t)ret);
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    /* Unlike non-TLS connections, do not reset "write-ready" if
     * sent amount smaller than provided amount, as TLS
     * connections may break data into smaller parts for sending. */
#endif /* EPOLL_SUPPORT */
#else  /* ! HTTPS_SUPPORT  */
    ret = MHD_ERR_NOTCONN_;
    mhd_log("TLS_NOT_SUPPORTED: returning MHD_ERR_NOTCONN_");
#endif /* ! HTTPS_SUPPORT  */
  }
  else
  {
    /* plaintext transmission */
    if (buffer_size > MHD_SCKT_SEND_MAX_SIZE_)
    {
      mhd_log("PARAM_ADJUST_PLAINTEXT: buffer_size (%zu) > MHD_SCKT_SEND_MAX_SIZE_. About to set buffer_size=%d and push_data=false", buffer_size, (int)MHD_SCKT_SEND_MAX_SIZE_);
      buffer_size = MHD_SCKT_SEND_MAX_SIZE_; /* send() return value limit */
      push_data = false; /* Incomplete send */
      mhd_log("PARAM_ADJUSTED_PLAINTEXT: buffer_size=%zu push_data=%d", buffer_size, (int)push_data);
    }

    mhd_log("PLAINTEXT_PATH: calling pre_send_setopt with push_data=%d buffer_size=%zu", (int)push_data, buffer_size);
    pre_send_setopt (connection, (! tls_conn), push_data);
#ifdef MHD_USE_MSG_MORE
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      push_data ? 0 : MSG_MORE);
#else
    ret = MHD_send4_ (s,
                      buffer,
                      buffer_size,
                      0);
#endif

    mhd_log("PLAINTEXT_SEND_RET: ret=%zd", (ssize_t)ret);
    if (0 > ret)
    {
      const int err = MHD_socket_get_error_ ();

      mhd_log("PLAINTEXT_SEND_ERR: err=%d", err);

      if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
      {
#ifdef EPOLL_SUPPORT
        /* EAGAIN, no longer write-ready */
        connection->epoll_state &=
          ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
        mhd_log("PLAINTEXT_EAGAIN: returning MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_EINTR_ (err))
      {
        mhd_log("PLAINTEXT_EINTR: returning MHD_ERR_AGAIN_");
        return MHD_ERR_AGAIN_;
      }
      if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
      {
        mhd_log("PLAINTEXT_REMOTE_DISCONNECT: returning MHD_ERR_CONNRESET_");
        return MHD_ERR_CONNRESET_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
      {
        mhd_log("PLAINTEXT_EPIPE: returning MHD_ERR_PIPE_");
        return MHD_ERR_PIPE_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
      {
        mhd_log("PLAINTEXT_EOPNOTSUPP: returning MHD_ERR_OPNOTSUPP_");
        return MHD_ERR_OPNOTSUPP_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
      {
        mhd_log("PLAINTEXT_ENOTCONN: returning MHD_ERR_NOTCONN_");
        return MHD_ERR_NOTCONN_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
      {
        mhd_log("PLAINTEXT_EINVAL: returning MHD_ERR_INVAL_");
        return MHD_ERR_INVAL_;
      }
      if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
      {
        mhd_log("PLAINTEXT_LOW_RESOURCES: returning MHD_ERR_NOMEM_");
        return MHD_ERR_NOMEM_;
      }
      if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
      {
        mhd_log("PLAINTEXT_EBADF: returning MHD_ERR_BADF_");
        return MHD_ERR_BADF_;
      }
      /* Treat any other error as a hard error. */
      mhd_log("PLAINTEXT_UNKNOWN_ERROR: returning MHD_ERR_NOTCONN_");
      return MHD_ERR_NOTCONN_;
    }
#ifdef EPOLL_SUPPORT
    else if (buffer_size > (size_t) ret)
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
  }

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  /* If TLS connection is used then next final send() will be
   * without MSG_MORE support. If non-TLS connection is used
   * it's unknown whether sendfile() will be used or not so
   * assume that next call will be the same, like this call. */
  if ( (push_data) &&
       (buffer_size == (size_t) ret) )
  {
    mhd_log("POST_SEND_CONDITION_TRUE: push_data=%d buffer_size=%zu ret=%zd - calling post_send_setopt",
            (int)push_data, buffer_size, (ssize_t)ret);
    post_send_setopt (connection, (! tls_conn), push_data);
  }
  else
  {
    mhd_log("POST_SEND_CONDITION_FALSE: push_data=%d buffer_size=%zu ret=%zd - not calling post_send_setopt",
            (int)push_data, buffer_size, (ssize_t)ret);
  }

  mhd_log("EXIT: ret=%zd final_buffer_size=%zu final_push_data=%d", (ssize_t)ret, buffer_size, (int)push_data);

  return ret;
}
