#include <stdio.h>
#include <stdbool.h>
#include <stddef.h>
#include <stdint.h>
#include <inttypes.h>

static void mhd_log_probe(const char *where,
                          struct MHD_Connection *connection,
                          size_t header_size_val,
                          size_t body_size_val,
                          bool complete_resp_val,
                          ssize_t ret_val,
                          const char *extra)
{
  FILE *f = fopen("/tmp/logs/1767.txt", "a");
  if (f)
  {
    fprintf(f, "[MHD_PROBE] %s: conn=%p header_size=%zu body_size=%zu complete_response=%d ret=%zd %s\n",
            where, (void *) connection, header_size_val, body_size_val,
            complete_resp_val ? 1 : 0, ret_val, (extra ? extra : ""));
    fclose(f);
  }
}

ssize_t
MHD_send_hdr_and_body_ (struct MHD_Connection *connection,
                        const char *header,
                        size_t header_size,
                        bool never_push_hdr,
                        const char *body,
                        size_t body_size,
                        bool complete_response)
{
  ssize_t ret;
  bool push_hdr;
  bool push_body;
  MHD_socket s = connection->socket_fd;
#ifndef _WIN32
#define _MHD_SEND_VEC_MAX   MHD_SCKT_SEND_MAX_SIZE_
#else  /* ! _WIN32 */
#define _MHD_SEND_VEC_MAX   UINT32_MAX
#endif /* ! _WIN32 */
#ifdef MHD_VECT_SEND
#if defined(HAVE_SENDMSG) || defined(HAVE_WRITEV)
  struct iovec vector[2];
#ifdef HAVE_SENDMSG
  struct msghdr msg;
#endif /* HAVE_SENDMSG */
#endif /* HAVE_SENDMSG || HAVE_WRITEV */
#ifdef _WIN32
  WSABUF vector[2];
  DWORD vec_sent;
#endif /* _WIN32 */
  bool no_vec; /* Is vector-send() disallowed? */

  no_vec = false;
#ifdef HTTPS_SUPPORT
  no_vec = no_vec || (connection->daemon->options & MHD_USE_TLS);
#endif /* HTTPS_SUPPORT */
#if (! defined(HAVE_SENDMSG) || ! defined(MSG_NOSIGNAL) ) && \
  defined(MHD_SEND_SPIPE_SEND_SUPPRESS_POSSIBLE) && \
  defined(MHD_SEND_SPIPE_SUPPRESS_NEEDED)
  no_vec = no_vec || (! connection->daemon->sigpipe_blocked &&
                      ! connection->sk_spipe_suppress);
#endif /* (!HAVE_SENDMSG || ! MSG_NOSIGNAL) &&
          MHD_SEND_SPIPE_SEND_SUPPRESS_POSSIBLE &&
          MHD_SEND_SPIPE_SUPPRESS_NEEDED */
#endif /* MHD_VECT_SEND */

  mhd_assert ( (NULL != body) || (0 == body_size) );

  mhd_log_probe("entry", connection, header_size, body_size, complete_response, (ssize_t)-1, "start");

  if ( (MHD_INVALID_SOCKET == s) ||
       (MHD_CONNECTION_CLOSED == connection->state) )
  {
    mhd_log_probe("early_return_notconn", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_NOTCONN_, "socket invalid or closed");
    return MHD_ERR_NOTCONN_;
  }

  push_body = complete_response;
  mhd_log_probe("after_init_push_body", connection, header_size, body_size, complete_response, (ssize_t)-1, "push_body set from complete_response");

  if (! never_push_hdr)
  {
    if (! complete_response)
      push_hdr = true; /* Push the header as the client may react
                        * on header alone while the body data is
                        * being prepared. */
    else
    {
      if (1400 > (header_size + body_size))
        push_hdr = false;  /* Do not push the header as complete
                           * reply is already ready and the whole
                           * reply most probably will fit into
                           * the single IP packet. */
      else
        push_hdr = true;   /* Push header alone so client may react
                           * on it while reply body is being delivered. */
    }
  }
  else
    push_hdr = false;

  mhd_log_probe("after_compute_push_hdr", connection, header_size, body_size, complete_response, (ssize_t)-1, (push_hdr ? "push_hdr=1" : "push_hdr=0"));

  if (complete_response && (0 == body_size))
    push_hdr = true; /* The header alone is equal to the whole response. */

  mhd_log_probe("after_force_push_hdr_if_empty_body", connection, header_size, body_size, complete_response, (ssize_t)-1, (push_hdr ? "push_hdr=1" : "push_hdr=0"));

  if (
#ifdef MHD_VECT_SEND
    (no_vec) ||
    (0 == body_size) ||
    ((size_t) SSIZE_MAX <= header_size) ||
    ((size_t) _MHD_SEND_VEC_MAX < header_size)
#ifdef _WIN32
    || ((size_t) UINT_MAX < header_size)
#endif /* _WIN32 */
#else  /* ! MHD_VECT_SEND */
    true
#endif /* ! MHD_VECT_SEND */
    )
  {
    ret = MHD_send_data_ (connection,
                          header,
                          header_size,
                          push_hdr);

    mhd_log_probe("after_send_header", connection, header_size, body_size, complete_response, ret, "sent header");

    if ( (header_size == (size_t) ret) &&
         ((size_t) SSIZE_MAX > header_size) &&
         (0 != body_size) &&
         (connection->sk_nonblck) )
    {
      ssize_t ret2;
      /* The header has been sent completely.
       * Try to send the reply body without waiting for
       * the next round. */
      /* Make sure that sum of ret + ret2 will not exceed SSIZE_MAX as
       * function needs to return positive value if succeed. */
      if ( (((size_t) SSIZE_MAX) - ((size_t) ret)) <  body_size)
      {
        /* Log state just before modifying body_size/complete_response */
        mhd_log_probe("before_shrink_body_on_ssize_limit", connection, header_size, body_size, complete_response, ret, "about to adjust body_size and complete_response");

        body_size = (((size_t) SSIZE_MAX) - ((size_t) ret));
        complete_response = false;
        push_body = complete_response;

        mhd_log_probe("after_shrink_body_on_ssize_limit", connection, header_size, body_size, complete_response, ret, "modified body_size and complete_response");
      }

      ret2 = MHD_send_data_ (connection,
                             body,
                             body_size,
                             push_body);
      mhd_log_probe("after_try_send_body", connection, header_size, body_size, complete_response, ret2, "attempted to send body immediately");

      if (0 < ret2)
        return ret + ret2; /* Total data sent */
      if (MHD_ERR_AGAIN_ == ret2)
        return ret;

      mhd_log_probe("return_error_from_send_body", connection, header_size, body_size, complete_response, ret2, "error sending body after header");
      return ret2; /* Error code */
    }
    mhd_log_probe("return_after_header_only_path", connection, header_size, body_size, complete_response, ret, "no immediate body send or header send only");
    return ret;
  }
#ifdef MHD_VECT_SEND

  if ( ((size_t) SSIZE_MAX <= body_size) ||
       ((size_t) SSIZE_MAX < (header_size + body_size)) )
  {
    /* Return value limit */
    mhd_log_probe("before_vect_shrink_body_on_ssize", connection, header_size, body_size, complete_response, (ssize_t)-1, "about to adjust for SSIZE_MAX");

    body_size = SSIZE_MAX - header_size;
    complete_response = false;
    push_body = complete_response;

    mhd_log_probe("after_vect_shrink_body_on_ssize", connection, header_size, body_size, complete_response, (ssize_t)-1, "modified body_size and complete_response for SSIZE_MAX");
  }
#if (SSIZE_MAX != _MHD_SEND_VEC_MAX) || (_MHD_SEND_VEC_MAX + 0 == 0)
  if (((size_t) _MHD_SEND_VEC_MAX <= body_size) ||
      ((size_t) _MHD_SEND_VEC_MAX < (header_size + body_size)))
  {
    /* Send total amount limit */
    mhd_log_probe("before_vect_shrink_body_on_vec_max", connection, header_size, body_size, complete_response, (ssize_t)-1, "about to adjust for _MHD_SEND_VEC_MAX");

    body_size = _MHD_SEND_VEC_MAX - header_size;
    complete_response = false;
    push_body = complete_response;

    mhd_log_probe("after_vect_shrink_body_on_vec_max", connection, header_size, body_size, complete_response, (ssize_t)-1, "modified body_size and complete_response for _MHD_SEND_VEC_MAX");
  }
#endif /* SSIZE_MAX != _MHD_SEND_VEC_MAX */

  pre_send_setopt (connection,
#ifdef HAVE_SENDMSG
                   true,
#else  /* ! HAVE_SENDMSG */
                   false,
#endif /* ! HAVE_SENDMSG */
                   push_hdr || push_body);
#if defined(HAVE_SENDMSG) || defined(HAVE_WRITEV)
  vector[0].iov_base = _MHD_DROP_CONST (header);
  vector[0].iov_len = header_size;
  vector[1].iov_base = _MHD_DROP_CONST (body);
  vector[1].iov_len = body_size;

#if defined(HAVE_SENDMSG)
  memset (&msg, 0, sizeof(msg));
  msg.msg_iov = vector;
  msg.msg_iovlen = 2;

  ret = sendmsg (s, &msg, MSG_NOSIGNAL_OR_ZERO);
#elif defined(HAVE_WRITEV)
  ret = writev (s, vector, 2);
#endif /* HAVE_WRITEV */
#endif /* HAVE_SENDMSG || HAVE_WRITEV */
#ifdef _WIN32
  if ((size_t) UINT_MAX < body_size)
  {
    /* Send item size limit */
    mhd_log_probe("before_win_shrink_body", connection, header_size, body_size, complete_response, (ssize_t)-1, "about to adjust for UINT_MAX on Windows");

    body_size = UINT_MAX;
    complete_response = false;
    push_body = complete_response;

    mhd_log_probe("after_win_shrink_body", connection, header_size, body_size, complete_response, (ssize_t)-1, "modified body_size and complete_response for UINT_MAX");
  }
  vector[0].buf = (char *) _MHD_DROP_CONST (header);
  vector[0].len = (unsigned long) header_size;
  vector[1].buf = (char *) _MHD_DROP_CONST (body);
  vector[1].len = (unsigned long) body_size;

  ret = WSASend (s, vector, 2, &vec_sent, 0, NULL, NULL);
  if (0 == ret)
    ret = (ssize_t) vec_sent;
  else
    ret = -1;
#endif /* _WIN32 */

  mhd_log_probe("after_vect_send_attempt", connection, header_size, body_size, complete_response, ret, "vector send attempted");

  if (0 > ret)
  {
    const int err = MHD_socket_get_error_ ();

    if (MHD_SCKT_ERR_IS_EAGAIN_ (err))
    {
#ifdef EPOLL_SUPPORT
      /* EAGAIN, no longer write-ready */
      connection->epoll_state &=
        ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */
      mhd_log_probe("vect_send_eagain", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_AGAIN_, "EAGAIN from vect send");
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_EINTR_ (err))
    {
      mhd_log_probe("vect_send_eintr", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_AGAIN_, "EINTR from vect send");
      return MHD_ERR_AGAIN_;
    }
    if (MHD_SCKT_ERR_IS_REMOTE_DISCNN_ (err))
    {
      mhd_log_probe("vect_send_connreset", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_CONNRESET_, "remote disconnect");
      return MHD_ERR_CONNRESET_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EPIPE_))
    {
      mhd_log_probe("vect_send_epipe", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_PIPE_, "EPIPE");
      return MHD_ERR_PIPE_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EOPNOTSUPP_))
    {
      mhd_log_probe("vect_send_eopnotsupp", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_OPNOTSUPP_, "EOPNOTSUPP");
      return MHD_ERR_OPNOTSUPP_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_ENOTCONN_))
    {
      mhd_log_probe("vect_send_enotconn", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_NOTCONN_, "ENOTCONN");
      return MHD_ERR_NOTCONN_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EINVAL_))
    {
      mhd_log_probe("vect_send_einval", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_INVAL_, "EINVAL");
      return MHD_ERR_INVAL_;
    }
    if (MHD_SCKT_ERR_IS_LOW_RESOURCES_ (err))
    {
      mhd_log_probe("vect_send_lowres", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_NOMEM_, "low resources");
      return MHD_ERR_NOMEM_;
    }
    if (MHD_SCKT_ERR_IS_ (err, MHD_SCKT_EBADF_))
    {
      mhd_log_probe("vect_send_ebadf", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_BADF_, "EBADF");
      return MHD_ERR_BADF_;
    }
    /* Treat any other error as a hard error. */
    mhd_log_probe("vect_send_other_err", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_NOTCONN_, "other error");
    return MHD_ERR_NOTCONN_;
  }
#ifdef EPOLL_SUPPORT
  else if ((header_size + body_size) > (size_t) ret)
    connection->epoll_state &=
      ~((enum MHD_EpollState) MHD_EPOLL_STATE_WRITE_READY);
#endif /* EPOLL_SUPPORT */

  /* If there is a need to push the data from network buffers
   * call post_send_setopt(). */
  if ( (push_body) &&
       ((header_size + body_size) == (size_t) ret) )
  {
    /* Complete reply has been sent. */
    /* If TLS connection is used then next final send() will be
     * without MSG_MORE support. If non-TLS connection is used
     * it's unknown whether next 'send' will be plain send() / sendmsg() or
     * sendfile() will be used so assume that next final send() will be
     * the same, like for this response. */
    post_send_setopt (connection,
#ifdef HAVE_SENDMSG
                      true,
#else  /* ! HAVE_SENDMSG */
                      false,
#endif /* ! HAVE_SENDMSG */
                      true);
    mhd_log_probe("post_send_setopt_push_body", connection, header_size, body_size, complete_response, ret, "post_send_setopt called for push_body");
  }
  else if ( (push_hdr) &&
            (header_size <= (size_t) ret))
  {
    /* The header has been sent completely and there is a
     * need to push the header data. */
    /* Luckily the type of send function will be used next is known. */
    post_send_setopt (connection,
#if defined(_MHD_HAVE_SENDFILE)
                      MHD_resp_sender_std == connection->rp.resp_sender,
#else  /* ! _MHD_HAVE_SENDFILE */
                      true,
#endif /* ! _MHD_HAVE_SENDFILE */
                      true);
    mhd_log_probe("post_send_setopt_push_hdr", connection, header_size, body_size, complete_response, ret, "post_send_setopt called for push_hdr");
  }

  mhd_log_probe("return_vect_send_success", connection, header_size, body_size, complete_response, ret, "vector send success or partial");
  return ret;
#else  /* ! MHD_VECT_SEND */
  mhd_assert (false);
  mhd_log_probe("unreachable_no_vect_send", connection, header_size, body_size, complete_response, (ssize_t)MHD_ERR_CONNRESET_, "unreachable");
  return MHD_ERR_CONNRESET_; /* Unreachable. Mute warnings. */
#endif /* ! MHD_VECT_SEND */
}
