#include <stdbool.h>
#include <stddef.h>
#include <stdio.h>

/* Prototype for external helper used by original code. */
extern int charsequalcaseless(char a, char b);

/* Probe logging helper. Appends human-readable trace information to /tmp/logs/1514.txt.
   Keeps the original logic untouched; only records state and control-flow points. */
static void mhd_log_probe(const char *event,
                          size_t i, size_t j,
                          size_t quoted_len, size_t unquoted_len,
                          char qch, char uch)
{
  FILE *f = fopen("/tmp/logs/1514.txt", "a");
  if (f != NULL)
  {
    /* Write a compact, parseable log line */
    fprintf(f, "%s: i=%zu j=%zu quoted_len=%zu unquoted_len=%zu qch=0x%02x uch=0x%02x\n",
            event, i, j, quoted_len, unquoted_len, (unsigned)(unsigned char)qch, (unsigned)(unsigned char)uch);
    fclose(f);
  }
}

bool
MHD_str_equal_caseless_quoted_bin_n (const char *quoted,
                                     size_t quoted_len,
                                     const char *unquoted,
                                     size_t unquoted_len)
{
  size_t i;
  size_t j;

  /* Log entry and initial state */
  mhd_log_probe("entry", 0, 0, quoted_len, unquoted_len, '\0', '\0');

  if (unquoted_len < quoted_len / 2)
  {
    mhd_log_probe("early_length_fail", 0, 0, quoted_len, unquoted_len, '\0', '\0');
    return false;
  }

  j = 0;
  mhd_log_probe("before_loop", 0, j, quoted_len, unquoted_len, '\0', '\0');

  for (i = 0; quoted_len > i && unquoted_len > j; ++i, ++j)
  {
    /* Log start of iteration. Accesses are safe due to loop condition. */
    mhd_log_probe("loop_start", i, j, quoted_len, unquoted_len, quoted[i], unquoted[j]);

    if ('\\' == quoted[i])
    {
      mhd_log_probe("backslash_found", i, j, quoted_len, unquoted_len, quoted[i], unquoted[j]);

      i++; /* Advance to the next character */

      /* Log that the loop counter i was modified inside the body (this is the violation scenario). */
      mhd_log_probe("i_incremented_in_body", i, j, quoted_len, unquoted_len,
                    (i < quoted_len) ? quoted[i] : '?', unquoted[j]);

      if (quoted_len == i)
      {
        mhd_log_probe("escaped_but_no_char_after", i, j, quoted_len, unquoted_len, '?', '?');
        return false; /* No character after escaping backslash */
      }
    }

    if (! charsequalcaseless (quoted[i], unquoted[j]))
    {
      mhd_log_probe("char_mismatch", i, j, quoted_len, unquoted_len, quoted[i], unquoted[j]);
      return false; /* Different characters */
    }

    mhd_log_probe("char_match_continue", i, j, quoted_len, unquoted_len, quoted[i], unquoted[j]);
  }

  mhd_log_probe("after_loop", i, j, quoted_len, unquoted_len, '\0', '\0');

  if ((quoted_len != i) || (unquoted_len != j))
  {
    mhd_log_probe("final_length_mismatch", i, j, quoted_len, unquoted_len, '\0', '\0');
    return false; /* The strings have different length */
  }

  mhd_log_probe("return_true", i, j, quoted_len, unquoted_len, '\0', '\0');
  return true;
}
