#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stddef.h>
#include <stdbool.h>

/* Probe logging helper: append textual logs to /tmp/logs/1602.txt
 * Designed to be non-intrusive and preserve original function logic. */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/1602.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

bool
MHD_str_remove_tokens_caseless_ (char *str,
                                 size_t *str_len,
                                 const char *const tokens,
                                 const size_t tokens_len)
{
  const char *const t = tokens;   /**< a short alias for @a tokens */
  size_t pt;                      /**< position in @a tokens */
  bool token_removed;

  /* Log entrance state */
  mhd_probe_log ("enter: str=%p str_len=%zu tokens=%p tokens_len=%zu", (void *)str, *str_len, (const void *)tokens, tokens_len);

  mhd_assert (NULL == memchr (tokens, 0, tokens_len));

  token_removed = false;
  pt = 0;

  while (pt < tokens_len && *str_len != 0)
  {
    const char *tkn; /**< the current token */
    size_t tkn_len;

    /* Skip any initial whitespaces and empty tokens in 'tokens' */
    while ( (pt < tokens_len) &&
            ((' ' == t[pt]) || ('\t' == t[pt]) || (',' == t[pt])) )
      pt++;

    if (pt >= tokens_len)
      break; /* No more tokens, nothing to remove */

    /* Found non-whitespace char which is not a comma */
    tkn = t + pt;
    do
    {
      do
      {
        pt++;
      } while (pt < tokens_len &&
               (' ' != t[pt] && '\t' != t[pt] && ',' != t[pt]));
      /* Found end of the token string, space, tab, or comma */
      tkn_len = pt - (size_t) (tkn - t);

      /* Skip all spaces and tabs */
      while (pt < tokens_len && (' ' == t[pt] || '\t' == t[pt]))
        pt++;
      /* Found end of the token string or non-whitespace char */
    } while (pt < tokens_len && ',' != t[pt]);

    /* 'tkn' is the input token with 'tkn_len' chars */
    /* Probe: record token parsing details. Limit preview to 128 chars to
     * avoid very large prints and keep logs readable. */
    {
      size_t preview_len = tkn_len > 128 ? 128 : tkn_len;
      mhd_probe_log ("token_parse: pt=%zu tokens_len=%zu tkn_len=%zu str_len=%zu tkn_preview=\'%.*s\'",
                     pt, tokens_len, tkn_len, *str_len, (int)preview_len, tkn);
    }

    /* Diagnostic assertion in original code; keep as-is but log the checked
     * condition to gather evidence whether tkn_len can be zero at runtime. */
    mhd_probe_log ("assert_check_before: (0 != tkn_len) -> tkn_len=%zu", tkn_len);
    mhd_assert (0 != tkn_len);
    if (0 == tkn_len)
    {
      /* If this branch ever executes, record it. This does not alter any
       * program state and preserves original semantics. */
      mhd_probe_log ("assert_violation: encountered tkn_len==0 at pt=%zu tokens_len=%zu str_len=%zu", pt, tokens_len, *str_len);
    }
    else
    {
      mhd_probe_log ("assert_ok: tkn_len_nonzero=%zu", tkn_len);
    }

    if (*str_len == tkn_len)
    {
      if (MHD_str_equal_caseless_bin_n_ (str, tkn, tkn_len))
      {
        *str_len = 0;
        token_removed = true;
        mhd_probe_log ("action: exact_match_removed str_len_set_to_0 token_len=%zu", tkn_len);
      }
      continue;
    }
    /* 'tkn' cannot match part of 'str' if length of 'tkn' is larger
     * than length of 'str'.
     * It's know that 'tkn' is not equal to the 'str' (was checked previously).
     * As 'str' is normalized when 'tkn' is not equal to the 'str'
     * it is required that 'str' to be at least 3 chars larger then 'tkn'
     * (the comma, the space and at least one additional character for the next
     * token) to remove 'tkn' from the 'str'. */
    if (*str_len > tkn_len + 2)
    { /* Remove 'tkn' from the input string */
      size_t pr;    /**< the 'read' position in the @a str */
      size_t pw;    /**< the 'write' position in the @a str */

      pr = 0;
      pw = 0;

      do
      {
        mhd_assert (pr >= pw);
        mhd_assert ((*str_len) >= (pr + tkn_len));
        if ( ( ((*str_len) == (pr + tkn_len)) || (',' == str[pr + tkn_len]) ) &&
             MHD_str_equal_caseless_bin_n_ (str + pr, tkn, tkn_len) )
        {
          /* current token in the input string matches the 'tkn', skip it */
          mhd_assert ((*str_len == pr + tkn_len) || \
                      (' ' == str[pr + tkn_len + 1])); /* 'str' must be normalized */
          token_removed = true;
          mhd_probe_log ("action: token_matched_and_skipped pr=%zu pw=%zu tkn_len=%zu", pr, pw, tkn_len);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += tkn_len + 2;
        }
        else
        {
          /* current token in the input string does not match the 'tkn',
           * copy to the output */
          if (0 != pw)
          { /* not the first output token, add ", " to separate */
            if (pr != pw + 2)
            {
              str[pw++] = ',';
              str[pw++] = ' ';
            }
            else
              pw += 2; /* 'str' is not yet modified in this round */
          }
          do
          {
            if (pr != pw)
              str[pw] = str[pr];
            pr++;
            pw++;
          } while (pr < *str_len && ',' != str[pr]);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += 2;
        }
        /* 'pr' should point to the next token in the input string or beyond
         * the end of the input string */
        if ((*str_len) < (pr + tkn_len))
        { /* The rest of the 'str + pr' is too small to match 'tkn' */
          if ((*str_len) > pr)
          { /* Copy the rest of the string */
            size_t copy_size;
            copy_size = *str_len - pr;
            if (0 != pw)
            { /* not the first output token, add ", " to separate */
              if (pr != pw + 2)
              {
                str[pw++] = ',';
                str[pw++] = ' ';
              }
              else
                pw += 2; /* 'str' is not yet modified in this round */
            }
            if (pr != pw)
              memmove (str + pw, str + pr, copy_size);
            pw += copy_size;
          }
          *str_len = pw;
          mhd_probe_log ("action: finished_partial_copy final_str_len=%zu", *str_len);
          break;
        }
        mhd_assert ((' ' != str[0]) && ('\t' != str[0]));
        mhd_assert ((0 == pr) || (3 <= pr));
        mhd_assert ((0 == pr) || (' ' == str[pr - 1]));
        mhd_assert ((0 == pr) || (',' == str[pr - 2]));
      } while (1);
    }
  }

  mhd_probe_log ("exit: token_removed=%d final_str_len=%zu", token_removed ? 1 : 0, *str_len);

  return token_removed;
}
