#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdbool.h>
#include <stddef.h>

/* Helper logging function: append formatted logs to /tmp/logs/1602.txt */
static void
mhd_logf (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/1602.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fflush (f);
  fclose (f);
}

bool
MHD_str_remove_tokens_caseless_ (char *str,
                                 size_t *str_len,
                                 const char *const tokens,
                                 const size_t tokens_len)
{
  const char *const t = tokens;   /**< a short alias for @a tokens */
  size_t pt;                      /**< position in @a tokens */
  bool token_removed;

  /* Probe: log the memchr check that the original mhd_assert does */
  {
    const void *mc = memchr (tokens, 0, tokens_len);
    mhd_logf ("[probe] entry: tokens_len=%zu, memchr_found=%s", tokens_len, (mc ? "YES" : "NO"));
  }
  mhd_assert (NULL == memchr (tokens, 0, tokens_len));

  token_removed = false;
  pt = 0;

  mhd_logf ("[probe] start loop: initial_str_len=%zu, pt=%zu", *str_len, pt);

  while (pt < tokens_len && *str_len != 0)
  {
    const char *tkn; /**< the current token */
    size_t tkn_len;

    /* Skip any initial whitespaces and empty tokens in 'tokens' */
    while ( (pt < tokens_len) &&
            ((' ' == t[pt]) || ('\t' == t[pt]) || (',' == t[pt])) )
      pt++;

    mhd_logf ("[probe] after skip: pt=%zu", pt);

    if (pt >= tokens_len)
      break; /* No more tokens, nothing to remove */

    /* Found non-whitespace char which is not a comma */
    tkn = t + pt;
    do
    {
      do
      {
        pt++;
      } while (pt < tokens_len &&
               (' ' != t[pt] && '\t' != t[pt] && ',' != t[pt]));
      /* Found end of the token string, space, tab, or comma */
      tkn_len = pt - (size_t) (tkn - t);

      /* Skip all spaces and tabs */
      while (pt < tokens_len && (' ' == t[pt] || '\t' == t[pt]))
        pt++;
      /* Found end of the token string or non-whitespace char */
    } while (pt < tokens_len && ',' != t[pt]);

    /* 'tkn' is the input token with 'tkn_len' chars */
    /* Probe: log tkn and length */
    {
      /* Write only a limited number of chars to avoid huge logs */
      size_t show = tkn_len;
      if (show > 64)
        show = 64;
      char buf[65];
      if (show > 0)
      {
        memcpy (buf, tkn, show);
        buf[show] = '\0';
      }
      else
        buf[0] = '\0';
      mhd_logf ("[probe] token_found: tkn_len=%zu, tkn_prefix='%s'", tkn_len, buf);
    }

    mhd_assert (0 != tkn_len);

    if (*str_len == tkn_len)
    {
      if (MHD_str_equal_caseless_bin_n_ (str, tkn, tkn_len))
      {
        *str_len = 0;
        token_removed = true;
        mhd_logf ("[probe] exact match removed: new_str_len=%zu", *str_len);
      }
      continue;
    }
    /* 'tkn' cannot match part of 'str' if length of 'tkn' is larger
     * than length of 'str'.
     * It's know that 'tkn' is not equal to the 'str' (was checked previously).
     * As 'str' is normalized when 'tkn' is not equal to the 'str'
     * it is required that 'str' to be at least 3 chars larger then 'tkn'
     * (the comma, the space and at least one additional character for the next
     * token) to remove 'tkn' from the 'str'. */
    if (*str_len > tkn_len + 2)
    { /* Remove 'tkn' from the input string */
      size_t pr;    /**< the 'read' position in the @a str */
      size_t pw;    /**< the 'write' position in the @a str */

      pr = 0;
      pw = 0;

      mhd_logf ("[probe] remove_loop_start: str_len=%zu, tkn_len=%zu", *str_len, tkn_len);

      do
      {
        /* Probe before the assertion pr >= pw */
        mhd_logf ("[probe] before_assert pr_ge_pw: pr=%zu, pw=%zu, cond=%s", pr, pw,
                  (pr >= pw) ? "TRUE" : "FALSE");
        mhd_assert (pr >= pw);

        /* Probe before the assertion (*str_len) >= (pr + tkn_len) */
        mhd_logf ("[probe] before_assert len_ge_pr_plus_tkn: str_len=%zu, pr=%zu, tkn_len=%zu, cond=%s",
                  *str_len, pr, tkn_len,
                  ((*str_len) >= (pr + tkn_len)) ? "TRUE" : "FALSE");
        mhd_assert ((*str_len) >= (pr + tkn_len));

        if ( ( ((*str_len) == (pr + tkn_len)) || (',' == str[pr + tkn_len]) ) &&
             MHD_str_equal_caseless_bin_n_ (str + pr, tkn, tkn_len) )
        {
          /* current token in the input string matches the 'tkn', skip it */
          /* Probe the normalized string assertion in a safe manner */
          if ((*str_len) >= (pr + tkn_len + 2))
          {
            char c = str[pr + tkn_len + 1];
            mhd_logf ("[probe] matched_token_norm_check: str_len=%zu, pr=%zu, tkn_len=%zu, next_char='%c'",
                      *str_len, pr, tkn_len, (c >= 32 && c < 127) ? c : '?');
          }
          else
          {
            mhd_logf ("[probe] matched_token_norm_check: str_len=%zu, pr=%zu, tkn_len=%zu, next_char=OUT_OF_BOUNDS",
                      *str_len, pr, tkn_len);
          }

          mhd_assert ((*str_len == pr + tkn_len) || \
                      (' ' == str[pr + tkn_len + 1])); /* 'str' must be normalized */
          token_removed = true;
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += tkn_len + 2;
          mhd_logf ("[probe] token matched and skipped: new_pr=%zu, token_removed=%s", pr, token_removed ? "TRUE" : "FALSE");
        }
        else
        {
          /* current token in the input string does not match the 'tkn',
           * copy to the output */
          if (0 != pw)
          { /* not the first output token, add ", " to separate */
            if (pr != pw + 2)
            {
              str[pw++] = ',';
              str[pw++] = ' ';
            }
            else
              pw += 2; /* 'str' is not yet modified in this round */
          }
          do
          {
            if (pr != pw)
              str[pw] = str[pr];
            pr++;
            pw++;
          } while (pr < *str_len && ',' != str[pr]);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += 2;

          mhd_logf ("[probe] copied_token: new_pr=%zu, new_pw=%zu", pr, pw);
        }
        /* 'pr' should point to the next token in the input string or beyond
         * the end of the input string */
        if ((*str_len) < (pr + tkn_len))
        { /* The rest of the 'str + pr' is too small to match 'tkn' */
          if ((*str_len) > pr)
          { /* Copy the rest of the string */
            size_t copy_size;
            copy_size = *str_len - pr;
            if (0 != pw)
            { /* not the first output token, add ", " to separate */
              if (pr != pw + 2)
              {
                str[pw++] = ',';
                str[pw++] = ' ';
              }
              else
                pw += 2; /* 'str' is not yet modified in this round */
            }
            if (pr != pw)
              memmove (str + pw, str + pr, copy_size);
            pw += copy_size;
          }
          *str_len = pw;
          mhd_logf ("[probe] tail_too_small: final_str_len=%zu", *str_len);
          break;
        }
        /* Several assertions about normalization and indexes. Log them safely. */
        if (*str_len > 0)
          mhd_logf ("[probe] check_str0_space_tab: str0='%c', cond=%s", (
                    (*str_len > 0) ? str[0] : '?'), (((*str_len > 0) && (' ' != str[0]) && ('\t' != str[0])) ? "TRUE" : "FALSE"));
        else
          mhd_logf ("[probe] check_str0_space_tab: str_len=0");

        mhd_logf ("[probe] check_pr_vals: pr=%zu, cond_ge3_or_zero=%s", pr, ((0 == pr) || (3 <= pr)) ? "TRUE" : "FALSE");

        if (pr != 0)
        {
          if (pr >= 1)
            mhd_logf ("[probe] check_pr_minus1_space: str[pr-1]='%c', cond=%s", str[pr - 1], ((' ' == str[pr - 1]) ? "TRUE" : "FALSE"));
          if (pr >= 2)
            mhd_logf ("[probe] check_pr_minus2_comma: str[pr-2]='%c', cond=%s", str[pr - 2], ((',' == str[pr - 2]) ? "TRUE" : "FALSE"));
        }

        mhd_assert ((' ' != str[0]) && ('\t' != str[0]));
        mhd_assert ((0 == pr) || (3 <= pr));
        mhd_assert ((0 == pr) || (' ' == str[pr - 1]));
        mhd_assert ((0 == pr) || (',' == str[pr - 2]));
      } while (1);
    }
  }

  mhd_logf ("[probe] exit: token_removed=%s, final_str_len=%zu", token_removed ? "TRUE" : "FALSE", *str_len);

  return token_removed;
}
