#include <stdio.h>
#include <stdarg.h>
#include <sys/stat.h>
#include <string.h>
#include <stdlib.h>
#include <stdbool.h>
#include <stddef.h>

/* Probe logging helper. Appends human-readable logs to /tmp/logs/1609.txt. */
static void
probe_log (const char *fmt, ...)
{
  va_list ap;
  /* try to create the directory if it does not exist; ignore errors */
  (void) mkdir("/tmp/logs", 0777);
  FILE *f = fopen("/tmp/logs/1609.txt", "a");
  if (NULL == f)
    return;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fflush (f);
  fclose (f);
}

/*
 * The target function with probes inserted. Original logic preserved as
 * much as possible. The probes record: entry parameters, the memchr call
 * that is the subject of the violation scenario, and key control-flow
 * events (token parsing positions, lengths, and removal events).
 *
 * Note: this file does not define `main`, and does not define macros.
 */
bool
MHD_str_remove_tokens_caseless_ (char *str,
                                 size_t *str_len,
                                 const char *const tokens,
                                 const size_t tokens_len)
{
  const char *const t = tokens;   /**< a short alias for @a tokens */
  size_t pt;                      /**< position in @a tokens */
  bool token_removed;

  /* Probe: log the memchr invocation parameters before it is executed.
   * This helps reproduce the violation scenario where memchr reads beyond
   * the actual object referenced by 'tokens' if tokens_len is wrong. */
  probe_log ("ENTER: MHD_str_remove_tokens_caseless_: str=%p, str_len_ptr=%p, *str_len=%zu, tokens=%p, tokens_len=%zu",
             (void *) str, (void *) str_len, (str_len ? *str_len : (size_t) 0), (const void *) tokens, tokens_len);

  /* Perform the same check as original code, but log the memchr result so
   * there is an evidence trail in the log file whether memchr was invoked
   * and what it returned. This mirrors the original behavior while
   * providing diagnostics. */
  {
    const void *memnul = memchr (tokens, 0, tokens_len);
    probe_log ("memchr called on tokens=%p with len=%zu -> result=%p",
               (const void *) tokens, tokens_len, memnul);
    mhd_assert (NULL == memnul);
  }

  token_removed = false;
  pt = 0;

  while (pt < tokens_len && *str_len != 0)
  {
    const char *tkn; /**< the current token */
    size_t tkn_len;

    /* Log loop entrance state */
    probe_log ("LOOP_START: pt=%zu, tokens_len=%zu, *str_len=%zu",
               pt, tokens_len, *str_len);

    /* Skip any initial whitespaces and empty tokens in 'tokens' */
    while ( (pt < tokens_len) &&
            ((' ' == t[pt]) || ('\t' == t[pt]) || (',' == t[pt])) )
      pt++;

    if (pt >= tokens_len)
    {
      probe_log ("NO_MORE_TOKENS: pt=%zu >= tokens_len=%zu, exiting loop",
                 pt, tokens_len);
      break; /* No more tokens, nothing to remove */
    }

    /* Found non-whitespace char which is not a comma */
    tkn = t + pt;
    do
    {
      do
      {
        pt++;
      } while (pt < tokens_len &&
               (' ' != t[pt] && '\t' != t[pt] && ',' != t[pt]));
      /* Found end of the token string, space, tab, or comma */
      tkn_len = pt - (size_t) (tkn - t);

      /* Skip all spaces and tabs */
      while (pt < tokens_len && (' ' == t[pt] || '\t' == t[pt]))
        pt++;
      /* Found end of the token string or non-whitespace char */
    } while (pt < tokens_len && ',' != t[pt]);

    /* 'tkn' is the input token with 'tkn_len' chars */
    probe_log ("TOKEN_FOUND: tkn_ptr=%p, tkn_offset=%zu, tkn_len=%zu",
               (const void *) tkn, (size_t) (tkn - t), tkn_len);

    mhd_assert (0 != tkn_len);

    if (*str_len == tkn_len)
    {
      probe_log ("COMPARE_EQ_LEN: *str_len==tkn_len==%zu, comparing content",
                 tkn_len);
      if (MHD_str_equal_caseless_bin_n_ (str, tkn, tkn_len))
      {
        *str_len = 0;
        token_removed = true;
        probe_log ("TOKEN_REMOVED: exact match -> *str_len set to 0, token_removed=true");
      }
      continue;
    }
    /* 'tkn' cannot match part of 'str' if length of 'tkn' is larger
     * than length of 'str'.
     * It's know that 'tkn' is not equal to the 'str' (was checked previously).
     * As 'str' is normalized when 'tkn' is not equal to the 'str'
     * it is required that 'str' to be at least 3 chars larger then 'tkn'
     * (the comma, the space and at least one additional character for the next
     * token) to remove 'tkn' from the 'str'. */
    if (*str_len > tkn_len + 2)
    { /* Remove 'tkn' from the input string */
      size_t pr;    /**< the 'read' position in the @a str */
      size_t pw;    /**< the 'write' position in the @a str */

      pr = 0;
      pw = 0;

      probe_log ("REMOVE_PHASE_START: initial pr=%zu, pw=%zu, *str_len=%zu, tkn_len=%zu",
                 pr, pw, *str_len, tkn_len);

      do
      {
        mhd_assert (pr >= pw);
        mhd_assert ((*str_len) >= (pr + tkn_len));
        if ( ( ((*str_len) == (pr + tkn_len)) || (',' == str[pr + tkn_len]) ) &&
             MHD_str_equal_caseless_bin_n_ (str + pr, tkn, tkn_len) )
        {
          /* current token in the input string matches the 'tkn', skip it */
          mhd_assert ((*str_len == pr + tkn_len) || \
                      (' ' == str[pr + tkn_len + 1])); /* 'str' must be normalized */
          token_removed = true;
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          probe_log ("MATCH_IN_STR: pr=%zu, matched_len=%zu -> skipping (advance pr)",
                     pr, tkn_len);
          pr += tkn_len + 2;
        }
        else
        {
          /* current token in the input string does not match the 'tkn',
           * copy to the output */
          if (0 != pw)
          { /* not the first output token, add ", " to separate */
            if (pr != pw + 2)
            {
              str[pw++] = ',';
              str[pw++] = ' ';
            }
            else
              pw += 2; /* 'str' is not yet modified in this round */
          }
          do
          {
            if (pr != pw)
              str[pw] = str[pr];
            pr++;
            pw++;
          } while (pr < *str_len && ',' != str[pr]);
          /* Advance to the next token in the input string or beyond
           * the end of the input string. */
          pr += 2;
          probe_log ("COPY_TOKEN: copied up to pr=%zu, pw=%zu", pr, pw);
        }
        /* 'pr' should point to the next token in the input string or beyond
         * the end of the input string */
        if ((*str_len) < (pr + tkn_len))
        { /* The rest of the 'str + pr' is too small to match 'tkn' */
          if ((*str_len) > pr)
          { /* Copy the rest of the string */
            size_t copy_size;
            copy_size = *str_len - pr;
            if (0 != pw)
            { /* not the first output token, add ", " to separate */
              if (pr != pw + 2)
              {
                str[pw++] = ',';
                str[pw++] = ' ';
              }
              else
                pw += 2; /* 'str' is not yet modified in this round */
            }
            if (pr != pw)
              memmove (str + pw, str + pr, copy_size);
            pw += copy_size;
            probe_log ("COPY_REST: copied copy_size=%zu, new_pw=%zu", copy_size, pw);
          }
          *str_len = pw;
          probe_log ("END_REMOVE_PHASE: updated *str_len=%zu, token_removed=%d", *str_len, token_removed);
          break;
        }
        mhd_assert ((' ' != str[0]) && ('\t' != str[0]));
        mhd_assert ((0 == pr) || (3 <= pr));
        mhd_assert ((0 == pr) || (' ' == str[pr - 1]));
        mhd_assert ((0 == pr) || (',' == str[pr - 2]));
      } while (1);
    }
  }

  probe_log ("EXIT: MHD_str_remove_tokens_caseless_: token_removed=%d, final *str_len=%zu, str=%p",
             token_removed, (str_len ? *str_len : (size_t) 0), (void *) str);

  return token_removed;
}
