#include <stdio.h>
#include <stdarg.h>
#include <stdlib.h>
#include <string.h>
#include <stdint.h>
#include <stdbool.h>
#include <sys/types.h>

/* Probe logging helper. Appends human-readable logs to /tmp/logs/2199.txt. */
static void
mhd_logf (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/2199.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fputc ('\n', f);
  va_end (ap);
  fclose (f);
}

static enum MHD_Result
add_response_header_connection (struct MHD_Response *response,
                                const char *value)
{
  static const char *key = MHD_HTTP_HEADER_CONNECTION;
  /** the length of the "Connection" key */
  static const size_t key_len =
    MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONNECTION);
  size_t value_len; /**< the length of the @a value */
  size_t old_value_len; /**< the length of the existing "Connection" value */
  size_t buf_size;  /**< the size of the buffer */
  size_t norm_len;  /**< the length of the normalised value */
  char *buf;        /**< the temporal buffer */
  struct MHD_HTTP_Res_Header *hdr; /**< existing "Connection" header */
  bool value_has_close; /**< the @a value has "close" token */
  bool already_has_close; /**< existing "Connection" header has "close" token */
  size_t pos = 0;   /**< position of addition in the @a buf */

  mhd_logf ("enter add_response_header_connection: response=%p, value=%p", (void *) response, (void *) value);
  if (value)
    mhd_logf ("value (preview)='%s'", value);

  if ( (NULL != strchr (value, '\r')) ||
       (NULL != strchr (value, '\n')) )
  {
    mhd_logf ("rejecting value: contains CR or LF -> returning MHD_NO");
    return MHD_NO;
  }

  if (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    hdr = MHD_get_response_element_n_ (response, MHD_HEADER_KIND,
                                       key, key_len);
    already_has_close =
      (0 != (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    mhd_logf ("existing connection header present: hdr=%p, already_has_close=%d, hdr->value=%p",
              (void *) hdr, (int) already_has_close, hdr ? (void *) hdr->value : NULL);
    if (hdr)
      mhd_logf ("hdr->value_size=%zu", hdr->value_size);
    mhd_assert (already_has_close == (0 == memcmp (hdr->value, "close", 5)));
    mhd_assert (NULL != hdr);
  }
  else
  {
    hdr = NULL;
    already_has_close = false;
    mhd_logf ("no existing connection header present (flags_auto=0x%08zx)", (size_t) response->flags_auto);
    mhd_assert (NULL == MHD_get_response_element_n_ (response,
                                                     MHD_HEADER_KIND,
                                                     key, key_len));
    mhd_assert (0 == (response->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
  }
  if (NULL != hdr)
    old_value_len = hdr->value_size + 2; /* additional size for ", " */
  else
    old_value_len = 0;

  value_len = strlen (value);
  mhd_logf ("computed lengths: value_len=%zu, old_value_len=%zu", value_len, old_value_len);
  if (value_len >= SSIZE_MAX)
  {
    mhd_logf ("value_len >= SSIZE_MAX -> returning MHD_NO");
    return MHD_NO;
  }
  /* Additional space for normalisation and zero-termination */
  norm_len = value_len + value_len / 2 + 1;
  if (norm_len >= SSIZE_MAX)
  {
    mhd_logf ("norm_len >= SSIZE_MAX -> returning MHD_NO");
    return MHD_NO;
  }
  buf_size = old_value_len + (size_t) norm_len;
  mhd_logf ("buf_size computed: old_value_len=%zu, norm_len=%zu, buf_size=%zu",
            old_value_len, norm_len, buf_size);

  buf = malloc (buf_size);
  mhd_logf ("malloc(%zu) -> buf=%p", buf_size, (void *) buf);
  if (NULL == buf)
  {
    mhd_logf ("malloc failed -> returning MHD_NO");
    return MHD_NO;
  }
  if (1)
  { /* local scope */
    ssize_t norm_len_s = (ssize_t) norm_len;
    /* Remove "close" token (if any), it will be moved to the front */
    value_has_close = MHD_str_remove_token_caseless_ (value, value_len, "close",
                                                      MHD_STATICSTR_LEN_ ( \
                                                        "close"),
                                                      buf + old_value_len,
                                                      &norm_len_s);
    mhd_logf ("after MHD_str_remove_token_caseless_: value_has_close=%d, norm_len_s=%zd",
              (int) value_has_close, norm_len_s);
    mhd_assert (0 <= norm_len_s);
    if (0 > norm_len_s)
    {
      /* Must never happen with realistic sizes */
      mhd_logf ("norm_len_s < 0 -> freeing buf and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    else
      norm_len = (size_t) norm_len_s;
    mhd_logf ("normalized length updated: norm_len=%zu", norm_len);
  }
#ifdef UPGRADE_SUPPORT
  if ( (NULL != response->upgrade_handler) && value_has_close)
  { /* The "close" token cannot be used with connection "upgrade" */
    mhd_logf ("upgrade handler present and value_has_close -> freeing buf and returning MHD_NO");
    free (buf);
    return MHD_NO;
  }
#endif /* UPGRADE_SUPPORT */
  if (0 != norm_len)
  {
    MHD_str_remove_tokens_caseless_ (buf + old_value_len, &norm_len,
                                     "keep-alive",
                                     MHD_STATICSTR_LEN_ ("keep-alive"));
    mhd_logf ("after removing keep-alive: norm_len=%zu", norm_len);
  }
  if (0 == norm_len)
  { /* New value is empty after normalisation */
    if (! value_has_close)
    { /* The new value had no tokens */
      mhd_logf ("norm_len==0 and no close -> freeing buf and returning MHD_NO");
      free (buf);
      return MHD_NO;
    }
    if (already_has_close)
    { /* The "close" token is already present, nothing to modify */
      mhd_logf ("norm_len==0 but already_has_close -> freeing buf and returning MHD_YES");
      free (buf);
      return MHD_YES;
    }
  }
  /* Add "close" token if required */
  if (value_has_close && ! already_has_close)
  {
    /* Need to insert "close" token at the first position */
    mhd_logf ("need to add 'close' token: value_has_close=%d, already_has_close=%d",
              (int) value_has_close, (int) already_has_close);
    mhd_logf ("assertion check: buf_size=%zu, required=%zu",
              buf_size,
              old_value_len + norm_len + MHD_STATICSTR_LEN_ ("close, ") + 1);
    mhd_assert (buf_size >= old_value_len + norm_len   \
                + MHD_STATICSTR_LEN_ ("close, ") + 1);
    if (0 != norm_len)
    {
      mhd_logf ("performing memmove: dest=%p, src=%p, len=%zu",
                (void *) (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len),
                (void *) (buf + old_value_len), norm_len + 1);
      memmove (buf + MHD_STATICSTR_LEN_ ("close, ") + old_value_len,
               buf + old_value_len, norm_len + 1);
    }
    mhd_logf ("performing memcpy: dest=%p, src=\"close\", len=%zu",
              (void *) buf, (size_t) MHD_STATICSTR_LEN_ ("close"));
    memcpy (buf, "close", MHD_STATICSTR_LEN_ ("close"));
    pos += MHD_STATICSTR_LEN_ ("close");
    mhd_logf ("after inserting 'close': pos=%zu", pos);
  }
  /* Add old value tokens (if any) */
  if (0 != old_value_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    mhd_logf ("copying old header value: hdr=%p, hdr->value=%p, hdr->value_size=%zu, dest=%p",
              (void *) hdr, hdr ? (void *) hdr->value : NULL,
              hdr ? hdr->value_size : 0, (void *) (buf + pos));
    memcpy (buf + pos, hdr->value,
            hdr->value_size);
    pos += hdr->value_size;
    mhd_logf ("after copying old header: pos=%zu", pos);
  }
  /* Add new value token (if any) */
  if (0 != norm_len)
  {
    if (0 != pos)
    {
      buf[pos++] = ',';
      buf[pos++] = ' ';
    }
    /* The new value tokens must be already at the correct position */
    mhd_assert ((value_has_close && ! already_has_close) ? \
                (MHD_STATICSTR_LEN_ ("close, ") + old_value_len == pos) : \
                (old_value_len == pos));
    pos += norm_len;
    mhd_logf ("added new normalized tokens: norm_len=%zu, pos=%zu", norm_len, pos);
  }
  mhd_assert (buf_size > pos);
  mhd_logf ("final check before null-terminate: buf_size=%zu, pos=%zu", buf_size, pos);
  buf[pos] = 0; /* Null terminate the result */
  mhd_logf ("final buffer (preview)='%s'", buf);

  if (NULL == hdr)
  {
    struct MHD_HTTP_Res_Header *new_hdr; /**< new "Connection" header */
    /* Create new response header entry */
    new_hdr = MHD_calloc_ (1, sizeof (struct MHD_HTTP_Res_Header));
    if (NULL != new_hdr)
    {
      new_hdr->header = malloc (key_len + 1);
      mhd_logf ("allocating new header struct: new_hdr=%p, new_hdr->header=%p", (void *) new_hdr, (void *) new_hdr->header);
      if (NULL != new_hdr->header)
      {
        memcpy (new_hdr->header, key, key_len + 1);
        new_hdr->header_size = key_len;
        new_hdr->value = buf;
        new_hdr->value_size = pos;
        new_hdr->kind = MHD_HEADER_KIND;
        if (value_has_close)
          response->flags_auto = (MHD_RAF_HAS_CONNECTION_HDR
                                  | MHD_RAF_HAS_CONNECTION_CLOSE);
        else
          response->flags_auto = MHD_RAF_HAS_CONNECTION_HDR;
        _MHD_insert_header_first (response, new_hdr);
        mhd_logf ("inserted new header: new_hdr=%p, new_hdr->value=%p, new_hdr->value_size=%zu, response->flags_auto=0x%08zx",
                  (void *) new_hdr, (void *) new_hdr->value, new_hdr->value_size,
                  (size_t) response->flags_auto);
        return MHD_YES;
      }
      mhd_logf ("failed to allocate new_hdr->header -> freeing new_hdr and buf");
      free (new_hdr);
    }
    free (buf);
    mhd_logf ("failed to create new header -> returning MHD_NO");
    return MHD_NO;
  }

  /* Update existing header entry */
  mhd_logf ("updating existing header hdr=%p: free old value %p, set new value %p, new size=%zu",
            (void *) hdr, (void *) hdr->value, (void *) buf, pos);
  free (hdr->value);
  hdr->value = buf;
  hdr->value_size = pos;
  if (value_has_close && ! already_has_close)
    response->flags_auto |= MHD_RAF_HAS_CONNECTION_CLOSE;
  mhd_logf ("update complete: hdr=%p, hdr->value=%p, hdr->value_size=%zu, response->flags_auto=0x%08zx",
            (void *) hdr, (void *) hdr->value, hdr->value_size, (size_t) response->flags_auto);
  return MHD_YES;
}
