#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper: appends a line to /tmp/logs/217.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/217.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Log entry state */
  mhd_probe_log("enter add_user_headers: ppos=%p, *ppos=%zu, buf_size=%zu, filter_transf_enc=%d, filter_content_len=%d, add_close=%d, add_keep_alive=%d, response=%p",
                (void *)ppos, (ppos ? *ppos : (size_t)0), buf_size,
                (int)filter_transf_enc, (int)filter_content_len,
                (int)add_close, (int)add_keep_alive, (void *)response);

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
  {
    filter_transf_enc = false;   /* No such header */
    mhd_probe_log("cleared filter_transf_enc because no TRANS_ENC_CHUNKED flag (now %d)", (int)filter_transf_enc);
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
  {
    filter_content_len = false;  /* No such header */
    mhd_probe_log("cleared filter_content_len because no CONTENT_LENGTH flag (now %d)", (int)filter_content_len);
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    /* Log the intended modification of the formal parameter add_close */
    mhd_probe_log("about to set add_close=false and add_keep_alive=false because no CONNECTION_HDR (old add_close=%d, add_keep_alive=%d)", (int)add_close, (int)add_keep_alive);

    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */

    mhd_probe_log("after setting: add_close=%d, add_keep_alive=%d", (int)add_close, (int)add_keep_alive);
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    mhd_probe_log("flags_auto indicates CONNECTION_CLOSE; about to set add_close=false (old add_close=%d)", (int)add_close);
    add_close = false;          /* "close" token was already set */
    mhd_probe_log("after setting add_close=%d", (int)add_close);
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    mhd_probe_log("loop header: hdr=%p, kind=%d, header_size=%zu, value_size=%zu, initial_pos=%zu, add_close=%d, add_keep_alive=%d",
                  (void *)hdr, (int)hdr->kind, hdr->header_size, hdr->value_size, initial_pos,
                  (int)add_close, (int)add_keep_alive);

    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        mhd_probe_log("found Transfer-Encoding header; clearing filter_transf_enc and skipping header");
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        mhd_probe_log("found Content-Length header; set filter_transf_enc=%d and skipping header", (int)filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      mhd_probe_log("returning false: insufficient buf_size (buf_size=%zu, *ppos=%zu, el_size=%zu)", buf_size, *ppos, el_size);
      return false;
    }
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        mhd_probe_log("writing Connection: close, ... (add_close was true)");
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          mhd_probe_log("returning false: insufficient buf_size for connection close addition (buf_size=%zu, initial_pos=%zu, el_size=%zu)", buf_size, initial_pos, el_size);
          return false;
        }
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        mhd_probe_log("writing Connection: Keep-Alive, ... (add_close was false, add_keep_alive=%d)", (int)add_keep_alive);
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          mhd_probe_log("returning false: insufficient buf_size for connection keep-alive addition (buf_size=%zu, initial_pos=%zu, el_size=%zu)", buf_size, initial_pos, el_size);
          return false;
        }
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      /* Log that we are about to clear the local flags that control Connection header
         (these are formal parameters modified within the function) */
      mhd_probe_log("about to clear add_close and add_keep_alive after writing Connection tokens (before: add_close=%d, add_keep_alive=%d)", (int)add_close, (int)add_keep_alive));

      add_close = false;
      add_keep_alive = false;

      mhd_probe_log("after clearing: add_close=%d, add_keep_alive=%d", (int)add_close, (int)add_keep_alive));
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));

    mhd_probe_log("finished header: new *ppos=%zu (expected %zu)", *ppos, initial_pos + el_size);
  }

  mhd_probe_log("exit add_user_headers: returning true, final *ppos=%zu", *ppos);
  return true;
}
