#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>
#include <stddef.h>
#include <string.h>

/* Probe logging helper - append-only to /tmp/logs/212.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/212.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fputc('\n', f);
  va_end (ap);
  fclose (f);
}

static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  mhd_probe_log("add_user_headers: enter - filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d flags_auto=0x%08x flags=0x%08x first_header=%p buf_size=%zu *ppos=%zu",
                (int)filter_transf_enc, (int)filter_content_len,
                (int)add_close, (int)add_keep_alive,
                (unsigned)r->flags_auto, (unsigned)r->flags,
                (void *)r->first_header, buf_size, *ppos);

  mhd_assert (! add_close || ! add_keep_alive);
  mhd_probe_log("add_user_headers: mhd_assert passed (mutually exclusive add_close/add_keep_alive)");

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
  {
    filter_transf_enc = false;   /* No such header */
    mhd_probe_log("filter_transf_enc cleared due to flags_auto (no TRANS_ENC_CHUNKED)");
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
  {
    filter_content_len = false;  /* No such header */
    mhd_probe_log("filter_content_len cleared due to flags_auto (no CONTENT_LENGTH)");
  }
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
    mhd_probe_log("no CONNECTION header auto-flag: add_close and add_keep_alive set to false");
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    add_close = false;          /* "close" token was already set */
    mhd_probe_log("CONNECTION_CLOSE auto-flag present: add_close set to false");
  }

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;
    mhd_probe_log("header loop: hdr=%p kind=%d header_size=%zu value_size=%zu initial_pos=%zu",
                  (void *)hdr, (int)hdr->kind, hdr->header_size, hdr->value_size, initial_pos);

    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        mhd_probe_log("skipping Transfer-Encoding header and clearing filter_transf_enc (hdr=%p)", (void *)hdr);
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        mhd_probe_log("skipping Content-Length header: new filter_transf_enc=%d", (int)filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
      return false;
    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      mhd_probe_log("Connection token insertion branch entered: add_close=%d add_keep_alive=%d initial_pos=%zu el_size(before token)=%zu",
                    (int)add_close, (int)add_keep_alive, initial_pos, el_size);

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
          return false;
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
        mhd_probe_log("Inserted token: \"close, \" at pos=%zu", *ppos - MHD_STATICSTR_LEN_("close, "));
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
          return false;
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        mhd_probe_log("Inserted token: \"Keep-Alive, \" at pos=%zu", *ppos - MHD_STATICSTR_LEN_("Keep-Alive, "));
      }

      /* Probe: log values immediately before resetting both flags. This is the site
         reported as containing redundant assignments: exactly one of add_close/add_keep_alive
         can be true, so setting both to false makes one assignment dead. */
      mhd_probe_log("Before resetting flags: add_close=%d add_keep_alive=%d", (int)add_close, (int)add_keep_alive);
      add_close = false;
      add_keep_alive = false;
      mhd_probe_log("After resetting flags: add_close=%d add_keep_alive=%d", (int)add_close, (int)add_keep_alive));
    }
    if (0 != hdr->value_size)
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_probe_log("header appended: initial_pos=%zu expected_end=%zu actual_end=%zu",
                  initial_pos, initial_pos + el_size, *ppos);
    mhd_assert (initial_pos + el_size == (*ppos));
  }
  mhd_probe_log("add_user_headers: exit - returning true, final *ppos=%zu", *ppos);
  return true;
}
