#include <stdio.h>
#include <stdarg.h>
#include <stddef.h>
#include <stdbool.h>

/* Probe helper: append a formatted message to /tmp/logs/219.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/219.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Log entry state */
  mhd_probe_log("add_user_headers: entry: r=%p, buf=%p, *ppos=%zu, buf_size=%zu, filter_transf_enc=%d, filter_content_len=%d, add_close=%d, add_keep_alive=%d",
                (void *) r, (void *) buf, (size_t) (*ppos), (size_t) buf_size,
                (int) filter_transf_enc, (int) filter_content_len,
                (int) add_close, (int) add_keep_alive);

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
    add_close = false;          /* "close" token was already set */

  /* Log resolved flags from response */
  mhd_probe_log("add_user_headers: resolved r->flags=0x%zx, r->flags_auto=0x%zx",
                (size_t) r->flags, (size_t) r->flags_auto);

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;

    /* Log header pointer and basic fields to help reproduce bounds issues */
    mhd_probe_log("iter header: hdr=%p, hdr->kind=%d, hdr->header=%p, hdr->header_size=%zu, hdr->value=%p, hdr->value_size=%zu, initial_pos=%zu",
                  (void *) hdr, (int) hdr->kind,
                  (void *) hdr->header, (size_t) hdr->header_size,
                  (void *) hdr->value, (size_t) hdr->value_size,
                  (size_t) initial_pos);

    if (MHD_HEADER_KIND != hdr->kind)
      continue;
    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        mhd_probe_log("filtered Transfer-Encoding header at hdr=%p", (void *) hdr);
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        mhd_probe_log("filtered Content-Length header at hdr=%p, new filter_transf_enc=%d",
                      (void *) hdr, (int) filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;

    mhd_probe_log("computed sizes: hdr=%p, header_size=%zu, value_size=%zu, el_size=%zu, buf_size=%zu, *ppos=%zu",
                  (void *) hdr, (size_t) hdr->header_size, (size_t) hdr->value_size,
                  (size_t) el_size, (size_t) buf_size, (size_t) (*ppos));

    if (buf_size < *ppos + el_size)
    {
      mhd_probe_log("buffer too small: need %zu bytes at pos %zu, buf_size=%zu - returning false",
                    (size_t) el_size, (size_t) (*ppos), (size_t) buf_size);
      return false;
    }

    /* Log just before memcpy of header name */
    mhd_probe_log("about to memcpy header name: dest=%p, dest_offset=%zu, src=%p, src_size=%zu",
                  (void *) (buf + *ppos), (size_t) (*ppos), (void *) hdr->header, (size_t) hdr->header_size);

    memcpy (buf + *ppos, hdr->header, hdr->header_size);
    (*ppos) += hdr->header_size;
    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
          return false;

        mhd_probe_log("about to memcpy add_close token at dest=%p, dest_offset=%zu, token=\"close, \", token_len=%zu",
                      (void *) (buf + *ppos), (size_t) (*ppos), (size_t) MHD_STATICSTR_LEN_("close, "));

        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
          return false;

        mhd_probe_log("about to memcpy add_keep_alive token at dest=%p, dest_offset=%zu, token=\"Keep-Alive, \", token_len=%zu",
                      (void *) (buf + *ppos), (size_t) (*ppos), (size_t) MHD_STATICSTR_LEN_("Keep-Alive, "));

        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      add_close = false;
      add_keep_alive = false;
    }

    if (0 != hdr->value_size)
    {
      mhd_probe_log("about to memcpy header value: dest=%p, dest_offset=%zu, src=%p, src_size=%zu",
                    (void *) (buf + *ppos), (size_t) (*ppos), (void *) hdr->value, (size_t) hdr->value_size);

      memcpy (buf + *ppos, hdr->value, hdr->value_size);
    }
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';

    mhd_probe_log("after writing header: hdr=%p, new *ppos=%zu, expected_end=%zu",
                  (void *) hdr, (size_t) (*ppos), (size_t) (initial_pos + el_size));

    mhd_assert (initial_pos + el_size == (*ppos));
  }
  mhd_probe_log("add_user_headers: exit true, final *ppos=%zu", (size_t) (*ppos));
  return true;
}
