#include <stdio.h>
#include <stdarg.h>

/* Probe logger: append textual logs to /tmp/logs/213.txt */
static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/213.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static bool
add_user_headers (char *buf,
                  size_t *ppos,
                  size_t buf_size,
                  struct MHD_Response *response,
                  bool filter_transf_enc,
                  bool filter_content_len,
                  bool add_close,
                  bool add_keep_alive)
{
  struct MHD_Response *const r = response; /**< a short alias */
  struct MHD_HTTP_Res_Header *hdr; /**< Iterates through User-specified headers */
  size_t el_size; /**< the size of current element to be added to the @a buf */

  /* Log entry state */
  mhd_log_probe("add_user_headers ENTRY: buf=%p ppos_ptr=%p ppos_val=%zu buf_size=%zu response=%p flags_auto=0x%lx flags=0x%lx filter_transf_enc=%d filter_content_len=%d add_close=%d add_keep_alive=%d",
                (void *)buf, (void *)ppos, (NULL != ppos) ? *ppos : (size_t)0,
                buf_size, (void *)response,
                (unsigned long)r->flags_auto, (unsigned long)r->flags,
                (int)filter_transf_enc, (int)filter_content_len,
                (int)add_close, (int)add_keep_alive);

  mhd_assert (! add_close || ! add_keep_alive);

  if (0 == (r->flags_auto & MHD_RAF_HAS_TRANS_ENC_CHUNKED))
    filter_transf_enc = false;   /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONTENT_LENGTH))
    filter_content_len = false;  /* No such header */
  if (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_HDR))
  {
    add_close = false;          /* No such header */
    add_keep_alive = false;     /* No such header */
  }
  else if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
    add_close = false;          /* "close" token was already set */

  for (hdr = r->first_header; NULL != hdr; hdr = hdr->next)
  {
    size_t initial_pos = *ppos;

    /* Log each header iteration state */
    mhd_log_probe("HEADER ITER: hdr=%p kind=%d header_ptr=%p header_size=%zu value_ptr=%p value_size=%zu next=%p initial_pos=%zu",
                  (void *)hdr, (int)hdr->kind,
                  (void *)hdr->header, (size_t)hdr->header_size,
                  (void *)hdr->value, (size_t)hdr->value_size,
                  (void *)hdr->next, initial_pos);

    if (MHD_HEADER_KIND != hdr->kind)
      continue;

    if (filter_transf_enc)
    { /* Need to filter-out "Transfer-Encoding" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_TRANSFER_ENCODING) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_TRANSFER_ENCODING,
                                          hdr->header, hdr->header_size)) )
      {
        filter_transf_enc = false; /* There is the only one such header */
        mhd_log_probe("HEADER SKIP: Transfer-Encoding header skipped (hdr=%p)", (void *)hdr);
        continue; /* Skip "Transfer-Encoding" header */
      }
    }
    if (filter_content_len)
    { /* Need to filter-out "Content-Length" */
      if ((MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_CONTENT_LENGTH) ==
           hdr->header_size) &&
          (MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_CONTENT_LENGTH,
                                          hdr->header, hdr->header_size)) )
      {
        /* Reset filter flag if only one header is allowed */
        filter_transf_enc =
          (0 == (r->flags & MHD_RF_INSANITY_HEADER_CONTENT_LENGTH));
        mhd_log_probe("HEADER SKIP: Content-Length header skipped (hdr=%p) new filter_transf_enc=%d",
                      (void *)hdr, (int)filter_transf_enc);
        continue; /* Skip "Content-Length" header */
      }
    }

    /* Add user header */
    el_size = hdr->header_size + 2 + hdr->value_size + 2;
    if (buf_size < *ppos + el_size)
    {
      mhd_log_probe("RETURN false: insufficient buffer for header hdr=%p buf_size=%zu required_min=%zu current_ppos=%zu",
                    (void *)hdr, buf_size, (*ppos + el_size), *ppos);
      return false;
    }

    /* Log the memcpy about to copy header name - this is the read-only use */
    mhd_log_probe("BEFORE memcpy(header): dest=%p src=%p header_size=%zu ppos=%zu initial_pos=%zu buf_size=%zu",
                  (void *)(buf + *ppos), (void *)hdr->header,
                  (size_t)hdr->header_size, *ppos, initial_pos, buf_size);

    memcpy (buf + *ppos, hdr->header, hdr->header_size);

    (*ppos) += hdr->header_size;
    mhd_log_probe("AFTER memcpy(header): new_ppos=%zu", *ppos);

    buf[(*ppos)++] = ':';
    buf[(*ppos)++] = ' ';
    if (add_close || add_keep_alive)
    {
      /* "Connection:" header must be always the first one */
      mhd_assert (MHD_str_equal_caseless_n_ (hdr->header, \
                                             MHD_HTTP_HEADER_CONNECTION, \
                                             hdr->header_size));

      if (add_close)
      {
        el_size += MHD_STATICSTR_LEN_ ("close, ");
        if (buf_size < initial_pos + el_size)
        {
          mhd_log_probe("RETURN false: insufficient buffer for close token hdr=%p buf_size=%zu required_min=%zu initial_pos=%zu",
                        (void *)hdr, buf_size, (initial_pos + el_size), initial_pos);
          return false;
        }
        mhd_log_probe("ADDING token: 'close, ' at ppos=%zu", *ppos);
        memcpy (buf + *ppos, "close, ",
                MHD_STATICSTR_LEN_ ("close, "));
        *ppos += MHD_STATICSTR_LEN_ ("close, ");
      }
      else
      {
        el_size += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
        if (buf_size < initial_pos + el_size)
        {
          mhd_log_probe("RETURN false: insufficient buffer for Keep-Alive token hdr=%p buf_size=%zu required_min=%zu initial_pos=%zu",
                        (void *)hdr, buf_size, (initial_pos + el_size), initial_pos);
          return false;
        }
        mhd_log_probe("ADDING token: 'Keep-Alive, ' at ppos=%zu", *ppos);
        memcpy (buf + *ppos, "Keep-Alive, ",
                MHD_STATICSTR_LEN_ ("Keep-Alive, "));
        *ppos += MHD_STATICSTR_LEN_ ("Keep-Alive, ");
      }
      add_close = false;
      add_keep_alive = false;
    }
    if (0 != hdr->value_size)
    {
      mhd_log_probe("BEFORE memcpy(value): dest=%p src=%p value_size=%zu ppos=%zu",
                    (void *)(buf + *ppos), (void *)hdr->value, (size_t)hdr->value_size, *ppos);
      memcpy (buf + *ppos, hdr->value, hdr->value_size);
      mhd_log_probe("AFTER memcpy(value): advanced_by=%zu new_ppos=%zu", (size_t)hdr->value_size, (*ppos + hdr->value_size));
    }
    *ppos += hdr->value_size;
    buf[(*ppos)++] = '\r';
    buf[(*ppos)++] = '\n';
    mhd_assert (initial_pos + el_size == (*ppos));

    mhd_log_probe("HEADER ADDED: hdr=%p final_ppos=%zu expected_final=%zu",
                  (void *)hdr, *ppos, (initial_pos + el_size));
  }

  mhd_log_probe("add_user_headers EXIT: returning true final_ppos=%zu", (NULL != ppos) ? *ppos : (size_t)0);
  return true;
}
