#include <stdio.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stddef.h>
#include <stdint.h>

/* Probe logging helper: append human-readable traces to /tmp/logs/2649.txt */
static void
mhd_probe_log (const char *fmt, ...)
{
  FILE *f = fopen ("/tmp/logs/2649.txt", "a");
  if (!f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  fprintf (f, "\n");
  va_end (ap);
  fclose (f);
}

static bool
calculate_add_nonce_with_retry (struct MHD_Connection *const connection,
                                const char *realm,
                                struct DigestAlgorithm *da,
                                char *nonce)
{
  const uint64_t timestamp1 = MHD_monotonic_msec_counter ();

  /* Log entry and pointer state before any further action */
  mhd_probe_log (
    "ENTER calculate_add_nonce_with_retry: connection=%p, realm=%p, nonce=%p, timestamp1=%" PRIu64,
    (void *) connection, (const void *) realm, (void *) nonce, timestamp1);

  const size_t realm_len = strlen (realm);

  /* Log result of strlen (note: strlen itself may read beyond bounds if realm is not NUL-terminated; this log records what happened) */
  mhd_probe_log ("realm pointer=%p, realm_len=%zu", (const void *) realm, realm_len);

  mhd_assert (! da->hashing);

#ifdef HAVE_MESSAGES
  if (0 == MHD_get_master (connection->daemon)->digest_auth_rand_size)
  {
    mhd_probe_log ("NOTICE: Random value not initialised (digest_auth_rand_size==0)");
    MHD_DLOG (connection->daemon,
              _ ("Random value was not initialised by " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM or " \
                 "MHD_OPTION_DIGEST_AUTH_RANDOM_COPY, generated nonces " \
                 "are predictable.\n"));
  }
#endif

  mhd_probe_log ("Calling calculate_add_nonce (first try) with timestamp=%" PRIu64 ", realm_len=%zu",
                 timestamp1, realm_len);

  if (! calculate_add_nonce (connection, timestamp1, realm, realm_len, da,
                             nonce))
  {
    mhd_probe_log ("calculate_add_nonce: first attempt failed");

    const size_t digest_size = digest_get_size (da);

    /* Log digest_size and anticipated nonce standard length. */
    mhd_probe_log ("digest_size=%zu, computed NONCE_STD_LEN(digest_size)=%zu",
                   digest_size, (size_t) NONCE_STD_LEN (digest_size));

    char nonce2[NONCE_STD_LEN (MAX_DIGEST) + 1];
    uint64_t timestamp2;
#ifdef MHD_DIGEST_HAS_EXT_ERROR
    if (digest_ext_error (da))
    {
      mhd_probe_log ("digest_ext_error: returning false (no retry)");
      return false; /* No need to re-try */
    }
#endif /* MHD_DIGEST_HAS_EXT_ERROR */
    if (0 == MHD_get_master (connection->daemon)->nonce_nc_size)
    {
      mhd_probe_log ("nonce_nc_size == 0: returning false (no retry)");
      return false; /* No need to re-try */
    }

    timestamp2 = MHD_monotonic_msec_counter ();
    mhd_probe_log ("timestamp1=%" PRIu64 ", timestamp2(initial)=%" PRIu64, timestamp1, timestamp2);

    if (timestamp1 == timestamp2)
    {
      mhd_probe_log ("timestamps equal, generating arbitrary difference");
      /* The timestamps are equal, need to generate some arbitrary
       * difference for nonce. */
      /* As the number is needed only to differentiate clients, weak
       * pseudo-random generators could be used. Seeding is not needed. */
      uint64_t base1;
      uint32_t base2;
      uint16_t base3;
      uint8_t base4;
#ifdef HAVE_RANDOM
      base1 = ((uint64_t) random ()) ^ UINT64_C (0x54a5acff5be47e63);
      base4 = 0xb8;
#elif defined(HAVE_RAND)
      base1 = ((uint64_t) rand ()) ^ UINT64_C (0xc4bcf553b12f3965);
      base4 = 0x92;
#else
      /* Monotonic msec counter alone does not really help here as it is already
         known that this value is not unique. */
      base1 = ((uint64_t) (uintptr_t) nonce2) ^ UINT64_C (0xf2e1b21bc6c92655);
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTR32 (base2, 4);
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      base1 = ((uint64_t) MHD_monotonic_msec_counter ())
              ^ UINT64_C (0xccab93f72cf5b15);
#endif
      base2 = ((uint32_t) (base1 >> 32)) ^ ((uint32_t) base1);
      base2 = _MHD_ROTL32 (base2, (((base4 >> 4) ^ base4) % 32));
      base3 = ((uint16_t) (base2 >> 16)) ^ ((uint16_t) base2);
      base4 = ((uint8_t) (base3 >> 8)) ^ ((uint8_t) base3);
      /* Use up to 127 ms difference */
      timestamp2 -= (base4 & DAUTH_JUMPBACK_MAX);
      if (timestamp1 == timestamp2)
      {
        timestamp2 -= 2; /* Fallback value */
        mhd_probe_log ("timestamp fallback applied, timestamp2=%" PRIu64, timestamp2);
      }
    }
    digest_reset (da);

    mhd_probe_log ("Calling calculate_add_nonce (second try) with timestamp=%" PRIu64 ", realm_len=%zu",
                   timestamp2, realm_len);

    if (! calculate_add_nonce (connection, timestamp2, realm, realm_len, da,
                               nonce2))
    {
      /* No free slot has been found. Re-tries are expensive, just use
       * the generated nonce. As it is not stored in nonce-nc map array,
       * the next request of the client will be recognized as valid, but 'stale'
       * so client should re-try automatically. */
      mhd_probe_log ("calculate_add_nonce: second attempt failed, returning false (use generated nonce)");
      return false;
    }

    /* Log memcpy details: destination pointer, source pointer, and length. */
    size_t copy_len = (size_t) NONCE_STD_LEN (digest_size);
    mhd_probe_log ("About to memcpy: dest=%p, src=%p, len=%zu", (void *) nonce, (void *) nonce2, copy_len);

    memcpy (nonce, nonce2, copy_len);

    mhd_probe_log ("memcpy completed: dest=%p (first_bytes_not_logged), src=%p, len=%zu",
                   (void *) nonce, (void *) nonce2, copy_len);
  }

  mhd_probe_log ("Exiting calculate_add_nonce_with_retry: returning true");
  return true;
}
