#include <stdio.h>
#include <stdint.h>
#include <stdarg.h>
#include <inttypes.h>
#include <stddef.h>
#include <string.h>

/* Probe helpers that log control flow and state to /tmp/logs/2653.txt */
static void
probe_log(const char *fmt, ...)
{
  va_list ap;
  FILE *f = fopen("/tmp/logs/2653.txt", "a");
  if (NULL == f)
    return;
  va_start(ap, fmt);
  vfprintf(f, fmt, ap);
  va_end(ap);
  fputc('\n', f);
  fclose(f);
}

static void
probe_log_enter_calculate_nonce(uint64_t nonce_time,
                                enum MHD_HTTP_Method mthd_e,
                                const char *method,
                                const char *rnd,
                                size_t rnd_size,
                                const struct sockaddr_storage *saddr,
                                size_t saddr_size,
                                const char *uri,
                                size_t uri_len,
                                const struct MHD_HTTP_Req_Header *first_header,
                                const char *realm,
                                size_t realm_len,
                                unsigned int bind_options,
                                struct DigestAlgorithm *da,
                                char *nonce)
{
  probe_log("[calculate_nonce] ENTER");
  probe_log("nonce_time=%llu, mthd_e=%d, method_ptr=%p, method_str='%s'",
            (unsigned long long) nonce_time,
            (int) mthd_e,
            (const void *) method,
            (method != NULL) ? method : "(null)");
  probe_log("rnd_ptr=%p, rnd_size=%zu, saddr_ptr=%p, saddr_size=%zu",
            (const void *) rnd,
            rnd_size,
            (const void *) saddr,
            saddr_size);
  probe_log("uri_ptr=%p, uri_len=%zu, realm_ptr=%p, realm_len=%zu",
            (const void *) uri,
            uri_len,
            (const void *) realm,
            realm_len);
  probe_log("bind_options=0x%08x, da_ptr=%p, nonce_ptr=%p",
            bind_options,
            (const void *) da,
            (void *) nonce);
}

static void
probe_log_branch(const char *msg)
{
  probe_log("[calculate_nonce] BRANCH: %s", msg);
}

static void
probe_log_before_digest_update_str(struct DigestAlgorithm *da, const char *method, enum MHD_HTTP_Method mthd_e)
{
  /* Log the exact call site and contents that may trigger the const-discard scenario. */
  probe_log("[calculate_nonce] About to call digest_update_str: da=%p, method_ptr=%p, method_str='%s', mthd_e=%d",
            (void *) da,
            (const void *) method,
            (method != NULL) ? method : "(null)",
            (int) mthd_e);
}

static void
probe_log_exit_calculate_nonce(char *nonce, struct DigestAlgorithm *da)
{
  /* Log the resulting nonce buffer (safe up to a reasonable length). */
  if (NULL != nonce)
  {
    /* Limit the amount of the nonce string we log to avoid huge logs. */
    char buf[256] = { 0 };
    size_t i;
    for (i = 0; i + 1 < sizeof(buf) && nonce[i] != '\0'; ++i)
      buf[i] = nonce[i];
    buf[i] = '\0';
    probe_log("[calculate_nonce] EXIT: nonce_ptr=%p, nonce_str='%s', da=%p",
              (void *) nonce,
              buf,
              (void *) da);
  }
  else
  {
    probe_log("[calculate_nonce] EXIT: nonce_ptr=(null), da=%p", (void *) da);
  }
}

/* Modified calculate_nonce with probe logging inserted. */
static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  /* Log entry and initial state */
  probe_log_enter_calculate_nonce(nonce_time, mthd_e, method, rnd, rnd_size,
                                  saddr, saddr_size, uri, uri_len,
                                  first_header, realm, realm_len,
                                  bind_options, da, nonce);

  mhd_assert (! da->hashing);
  if (1)
  {
    probe_log_branch("timestamp block");
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
    /* If the nonce_time is milliseconds, then the same 48 bit value will repeat
     * every 8 919 years, which is more than enough to mitigate a replay attack */
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));
  }
  if (rnd_size > 0)
  {
    probe_log_branch("rnd_size > 0");
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    probe_log_branch("bind none && saddr_size != 0 (full client address)");
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    probe_log_branch("bind client ip && saddr_size != 0 (client IP)");
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    probe_log_branch("include method in hash");
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));
    }
    else
    {
      /* Probe before calling digest_update_str to capture the situation where a const
       * pointer (method) is passed to digest_update_str. This is the point of the
       * reported MISRA Rule 8.13 violation if digest_update_str's prototype is not
       * const-correct. */
      probe_log_before_digest_update_str(da, method, mthd_e);
      digest_update_str (da, method);
    }
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    probe_log_branch("include URI in hash");
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    probe_log_branch("include URI params in hash");
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;
      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
        digest_update (da, h->value, h->value_size);
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    probe_log_branch("include realm in hash");
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);
  }
  if (1)
  {
    probe_log_branch("finalize hash and hex encode");
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);
  }

  /* Log exit state including resulting nonce */
  probe_log_exit_calculate_nonce(nonce, da);
}
