#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <inttypes.h>
#include <string.h>

/* Probe logging helper — writes appended log lines to /tmp/logs/2653.txt */
static void
log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/2653.txt", "a");
  if (NULL == f)
    return;

  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fputc('\n', f);
  fclose (f);
}

static void
calculate_nonce (uint64_t nonce_time,
                 enum MHD_HTTP_Method mthd_e,
                 const char *method,
                 const char *rnd,
                 size_t rnd_size,
                 const struct sockaddr_storage *saddr,
                 size_t saddr_size,
                 const char *uri,
                 size_t uri_len,
                 const struct MHD_HTTP_Req_Header *first_header,
                 const char *realm,
                 size_t realm_len,
                 unsigned int bind_options,
                 struct DigestAlgorithm *da,
                 char *nonce)
{
  mhd_assert (! da->hashing);

  /* Log entry and key inputs */
  log_probe ("calculate_nonce: entry nonce_time=%" PRIu64 ", mthd_e=%d, method=%p, rnd=%p, rnd_size=%zu, saddr=%p, saddr_size=%zu, uri=%p, uri_len=%zu, first_header=%p, realm=%p, realm_len=%zu, bind_options=0x%x, da=%p, nonce=%p",
             nonce_time, (int)mthd_e, (const void *)method, (const void *)rnd, rnd_size,
             (const void *)saddr, saddr_size, (const void *)uri, uri_len,
             (const void *)first_header, (const void *)realm, realm_len, bind_options,
             (void *)da, (void *)nonce);

  if (1)
  {
    /* Add the timestamp to the hash calculation */
    uint8_t timestamp[TIMESTAMP_BIN_SIZE];
#if TIMESTAMP_BIN_SIZE != 6
#error The code needs to be updated here
#endif
    timestamp[0] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 0)));
    timestamp[1] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 1)));
    timestamp[2] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 2)));
    timestamp[3] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 3)));
    timestamp[4] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 4)));
    timestamp[5] = (uint8_t) (nonce_time >> (8 * (TIMESTAMP_BIN_SIZE - 1 - 5)));
    MHD_bin_to_hex (timestamp,
                    sizeof (timestamp),
                    nonce + digest_get_size (da) * 2);
    digest_update (da,
                   timestamp,
                   sizeof (timestamp));

    /* Log timestamp bytes that were added */
    log_probe ("timestamp bytes added: %02x %02x %02x %02x %02x %02x",
               timestamp[0], timestamp[1], timestamp[2], timestamp[3], timestamp[4], timestamp[5]);
  }
  if (rnd_size > 0)
  {
    /* Add the unique random value to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   rnd,
                   rnd_size);

    /* Log rnd usage */
    log_probe ("rnd present: rnd=%p, rnd_size=%zu", (const void *)rnd, rnd_size);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) &&
       (0 != saddr_size) )
  {
    /* Add full client address including source port to make unique nonces
     * for requests received exactly at the same time */
    digest_update_with_colon (da);
    digest_update (da,
                   saddr,
                   saddr_size);

    log_probe ("bound to full saddr: saddr=%p, saddr_size=%zu, bind_options=0x%x",
               (const void *)saddr, saddr_size, bind_options);
  }
  if ( (0 != (bind_options & MHD_DAUTH_BIND_NONCE_CLIENT_IP)) &&
       (0 != saddr_size) )
  {
    /* Add the client's IP address to the hash calculation */
    digest_update_with_colon (da);
    if (AF_INET == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in *) saddr)->sin_addr,
                     sizeof(((const struct sockaddr_in *) saddr)->sin_addr));
#ifdef HAVE_INET6
    else if (AF_INET6 == saddr->ss_family)
      digest_update (da,
                     &((const struct sockaddr_in6 *) saddr)->sin6_addr,
                     sizeof(((const struct sockaddr_in6 *) saddr)->sin6_addr));
#endif /* HAVE_INET6 */

    log_probe ("bound to client IP: saddr=%p, family=%d", (const void *)saddr, (int)saddr->ss_family);
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI)))
  {
    /* Add the request method to the hash calculation */
    digest_update_with_colon (da);
    if (MHD_HTTP_MTHD_OTHER != mthd_e)
    {
      uint8_t mthd_for_hash;
      if (MHD_HTTP_MTHD_HEAD != mthd_e)
        mthd_for_hash = (uint8_t) mthd_e;
      else /* Treat HEAD method in the same way as GET method */
        mthd_for_hash = (uint8_t) MHD_HTTP_MTHD_GET;
      digest_update (da,
                     &mthd_for_hash,
                     sizeof(mthd_for_hash));

      log_probe ("method used as enum: mthd_e=%d, mthd_for_hash=%u", (int)mthd_e, (unsigned)mthd_for_hash);
    }
    else
      digest_update_str (da, method);

    if (MHD_HTTP_MTHD_OTHER == mthd_e)
      log_probe ("method used as string: method=%p", (const void *)method);
  }

  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI))
  {
    /* Add the request URI to the hash calculation */
    digest_update_with_colon (da);

    digest_update (da,
                   uri,
                   uri_len);

    log_probe ("uri added: uri=%p, uri_len=%zu", (const void *)uri, uri_len);
  }
  if (0 != (bind_options & MHD_DAUTH_BIND_NONCE_URI_PARAMS))
  {
    /* Add the request URI parameters to the hash calculation */
    const struct MHD_HTTP_Req_Header *h;

    digest_update_with_colon (da);

    size_t header_index = 0;
    for (h = first_header; NULL != h; h = h->next)
    {
      if (MHD_GET_ARGUMENT_KIND != h->kind)
        continue;

      /* Log header metadata before digest updates */
      {
        char header_buf[129];
        size_t hs = (h->header_size < 128) ? h->header_size : 128;
        if (0 != hs && NULL != h->header)
          memcpy (header_buf, h->header, hs);
        header_buf[hs] = '\0';

        char value_buf[129];
        size_t vs = (h->value_size < 128) ? h->value_size : 128;
        if (0 != vs && NULL != h->value)
          memcpy (value_buf, h->value, vs);
        value_buf[vs] = '\0';

        log_probe ("header[%zu]: ptr=%p, kind=%d, header_size=%zu, header_preview='%s', value_size=%zu, value_ptr=%p, value_preview='%s'",
                   header_index, (const void *)h, (int)h->kind, h->header_size, header_buf, h->value_size, (const void *)h->value, value_buf);
      }

      digest_update (da, "\0", 2);
      if (0 != h->header_size)
        digest_update (da, h->header, h->header_size);
      digest_update (da, "", 1);
      if (0 != h->value_size)
      {
        /* Log the read-only usage of h->value — the potential violation scenario probe */
        log_probe ("digest_update will be called with header[%zu].value ptr=%p, value_size=%zu (read-only use)", header_index, (const void *)h->value, h->value_size);

        digest_update (da, h->value, h->value_size);

        /* After call, log that digest_update returned and the pointer was not modified here */
        log_probe ("digest_update returned for header[%zu].value ptr=%p (no modification performed in calculate_nonce)", header_index, (const void *)h->value);
      }

      header_index++;
    }
  }
  if ( (MHD_DAUTH_BIND_NONCE_NONE == bind_options) ||
       (0 != (bind_options & MHD_DAUTH_BIND_NONCE_REALM)))
  {
    /* Add the realm to the hash calculation */
    digest_update_with_colon (da);
    digest_update (da,
                   realm,
                   realm_len);

    log_probe ("realm added: realm=%p, realm_len=%zu", (const void *)realm, realm_len);
  }
  if (1)
  {
    uint8_t hash[MAX_DIGEST];
    digest_calc_hash (da, hash);
    MHD_bin_to_hex (hash,
                    digest_get_size (da),
                    nonce);

    /* Log the final nonce hex output as computed */
    size_t hex_len = digest_get_size (da) * 2;
    /* nonce may not be null-terminated; create a local copy for safe logging */
    {
      size_t cap = (hex_len < 256) ? hex_len : 255;
      char outbuf[256];
      if (cap > 0 && nonce != NULL)
        memcpy (outbuf, nonce, cap);
      outbuf[cap] = '\0';
      log_probe ("final nonce hex (len=%zu): %s", hex_len, outbuf);
    }
  }

  log_probe ("calculate_nonce: exit");
}
