#include <stdio.h>
#include <stdbool.h>

/* Probe logging helper: append a single line with relevant state info. */
static void
probe_log(const char *where,
          struct MHD_Connection *con,
          bool read_ready,
          bool write_ready,
          bool force_close,
          bool states_info_processed,
          bool on_fasttrack,
          int ret_val)
{
  FILE *f = fopen("/tmp/logs/1042.txt", "a");
  if (!f)
    return;
  /* Log pointer and key integer fields that are guaranteed to exist. */
  fprintf(f, "%s: con=%p state=%d event_loop_info=0x%x sk_nonblck=%d daemon_options=0x%x read_ready=%d write_ready=%d force_close=%d states_info_processed=%d on_fasttrack=%d ret=%d",
          where,
          (void *)con,
          (int)con->state,
          (unsigned int)con->event_loop_info,
          (int)con->sk_nonblck,
          (int)con->daemon->options,
          (int)read_ready,
          (int)write_ready,
          (int)force_close,
          (int)states_info_processed,
          (int)on_fasttrack,
          ret_val);
#ifdef HTTPS_SUPPORT
  fprintf(f, " tls_read_ready=%d", (int)con->tls_read_ready);
#endif
  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_Result
call_handlers (struct MHD_Connection *con,
               bool read_ready,
               bool write_ready,
               bool force_close)
{
  enum MHD_Result ret;
  bool states_info_processed = false;
  /* Fast track flag */
  bool on_fasttrack = (con->state == MHD_CONNECTION_INIT);
  ret = MHD_YES;

  /* Log initial entry and the initial assignment to 'ret' */
  probe_log("entry_after_init_ret", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);

  mhd_assert ((0 == (con->daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_valid_ID_ (con->tid)));
  mhd_assert ((0 != (con->daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (! MHD_thread_handle_ID_is_valid_ID_ (con->tid)));
  mhd_assert ((0 == (con->daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_current_thread_ (con->tid)));

#ifdef HTTPS_SUPPORT
  if (con->tls_read_ready)
    read_ready = true;
#endif /* HTTPS_SUPPORT */
  if ( (0 != (MHD_EVENT_LOOP_INFO_READ & con->event_loop_info)) &&
       (read_ready || (force_close && con->sk_nonblck)) )
  {
    MHD_connection_handle_read (con, force_close);
    mhd_assert (! force_close || MHD_CONNECTION_CLOSED == con->state);
    ret = MHD_connection_handle_idle (con);
    /* Log assignment to 'ret' produced by idle after read */
    probe_log("after_read_idle_assign", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);
    if (force_close) {
      /* Log imminent return of 'ret' when force_close after read */
      probe_log("before_return_after_read_force_close", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);
      return ret;
    }
    states_info_processed = true;
  }
  if (! force_close)
  {
    /* No need to check value of 'ret' here as closed connection
     * cannot be in MHD_EVENT_LOOP_INFO_WRITE state. */
    if ( (MHD_EVENT_LOOP_INFO_WRITE == con->event_loop_info) &&
         write_ready)
    {
      MHD_connection_handle_write (con);
      ret = MHD_connection_handle_idle (con);
      /* Log assignment to 'ret' produced by idle after write */
      probe_log("after_write_idle_assign", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);
      states_info_processed = true;
    }
  }
  else
  {
    /* Log before close/direct return branch */
    probe_log("before_close_branch", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, -1);
    MHD_connection_close_ (con,
                           MHD_REQUEST_TERMINATED_WITH_ERROR);
    /* Preserve behavior while allowing logging of the returned value. */
    enum MHD_Result tmp = MHD_connection_handle_idle (con);
    probe_log("before_direct_return_after_close", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)tmp);
    return tmp;
  }

  if (! states_info_processed)
  {   /* Connection is not read or write ready, but external conditions
       * may be changed and need to be processed. */
    ret = MHD_connection_handle_idle (con);
    /* Log assignment to 'ret' when idle is called due to no states processed */
    probe_log("after_idle_when_no_states_processed", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);
  }
  /* Fast track for fast connections. */
  /* If full request was read by single read_handler() invocation
     and headers were completely prepared by single MHD_connection_handle_idle()
     then try not to wait for next sockets polling and send response
     immediately.
     As writeability of socket was not checked and it may have
     some data pending in system buffers, use this optimization
     only for non-blocking sockets. */
  /* No need to check 'ret' as connection is always in
   * MHD_CONNECTION_CLOSED state if 'ret' is equal 'MHD_NO'. */
  else if (on_fasttrack && con->sk_nonblck)
  {
    if (MHD_CONNECTION_HEADERS_SENDING == con->state)
    {
      MHD_connection_handle_write (con);
      /* Always call 'MHD_connection_handle_idle()' after each read/write. */
      ret = MHD_connection_handle_idle (con);
      probe_log("after_fasttrack_headers_sending_idle", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);
    }
    /* If all headers were sent by single write_handler() and
     * response body is prepared by single MHD_connection_handle_idle()
     * call - continue. */
    if ((MHD_CONNECTION_NORMAL_BODY_READY == con->state) ||
        (MHD_CONNECTION_CHUNKED_BODY_READY == con->state))
    {
      MHD_connection_handle_write (con);
      ret = MHD_connection_handle_idle (con);
      probe_log("after_fasttrack_body_ready_idle", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);
    }
  }

  /* All connection's data and states are processed for this turn.
   * If connection already has more data to be processed - use
   * zero timeout for next select()/poll(). */
  /* Thread-per-connection do not need global zero timeout as
   * connections are processed individually. */
  /* Note: no need to check for read buffer availability for
   * TLS read-ready connection in 'read info' state as connection
   * without space in read buffer will be marked as 'info block'. */
  if ( (! con->daemon->data_already_pending) &&
       (! MHD_D_IS_USING_THREAD_PER_CONN_ (con->daemon)) )
  {
    if (0 != (MHD_EVENT_LOOP_INFO_PROCESS & con->event_loop_info))
      con->daemon->data_already_pending = true;
#ifdef HTTPS_SUPPORT
    else if ( (con->tls_read_ready) &&
              (0 != (MHD_EVENT_LOOP_INFO_READ & con->event_loop_info)) )
      con->daemon->data_already_pending = true;
#endif /* HTTPS_SUPPORT */
  }

  /* Log final return value and state before returning */
  probe_log("final_return", con, read_ready, write_ready, force_close, states_info_processed, on_fasttrack, (int)ret);
  return ret;
}
