#include <stdio.h>

/* Probe helper: logs control flow and state to /tmp/logs/2733.txt. */
static void
digest_probe_log_state (const struct DigestAlgorithm *da, const char *stage)
{
  FILE *f = fopen("/tmp/logs/2733.txt", "a");
  if (f == NULL)
    return; /* best-effort logging; do not change program behavior if logging fails */

  fprintf(f, "[digest_get_size] stage=%s, da=%p\n", stage, (void *)da);
  if (da != NULL)
  {
    /* Print fields used by the function to capture state for the violation scenario. */
    fprintf(f, "  uninitialised=%d, algo_selected=%d, algo=%u\n",
            (int) da->uninitialised,
            (int) da->algo_selected,
            (unsigned) da->algo);
  }
  fclose(f);
}

_MHD_static_inline unsigned int
digest_get_size (struct DigestAlgorithm *da)
{
  /* Log entry and relevant state before assertions/logic */
  digest_probe_log_state(da, "entry");

  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);

  /* Log state after asserts to show they passed (if reached) */
  digest_probe_log_state(da, "after_asserts");

#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
  {
    digest_probe_log_state(da, "branch_md5");
    return MD5_DIGEST_SIZE;
  }
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
  {
    digest_probe_log_state(da, "branch_sha256");
    return SHA256_DIGEST_SIZE;
  }
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
  {
    digest_probe_log_state(da, "branch_sha512_256");
    return SHA512_256_DIGEST_SIZE;
  }
#endif /* MHD_SHA512_256_SUPPORT */
  /* Log unexpected path and assert failure to provide evidence */
  digest_probe_log_state(da, "unexpected_path");
  mhd_assert (0); /* May not happen */
  return 0;
}
