#include <stdio.h>

/* Probe function: record control flow and key fields of 'da' to a log file.
   Uses a const pointer parameter to avoid modifying the target pointer, while
   preserving the original function signature of digest_get_size (non-const).
*/
static void
digest_probe_log (const struct DigestAlgorithm *da, const char *place)
{
  FILE *f = fopen("/tmp/logs/2733.txt", "a");
  if (f != NULL)
    {
      /* Log pointer address and the fields read by digest_get_size */
      fprintf(f, "%s: da=%p uninitialised=%d algo_selected=%d algo=%u\n",
              place,
              (void *) da,
              (int) da->uninitialised,
              (int) da->algo_selected,
              (unsigned) da->algo);
      fclose(f);
    }
}

_MHD_static_inline unsigned int
digest_get_size (struct DigestAlgorithm *da)
{
  /* Log initial entry and read-only fields that demonstrate the violation scenario */
  digest_probe_log(da, "entry");

  mhd_assert (! da->uninitialised);
  mhd_assert (da->algo_selected);

  /* Log after assertions to capture state when assertions pass */
  digest_probe_log(da, "after_asserts");

#ifdef MHD_MD5_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_MD5 == da->algo)
    {
      digest_probe_log(da, "branch_md5");
      return MD5_DIGEST_SIZE;
    }
#endif /* MHD_MD5_SUPPORT */
#ifdef MHD_SHA256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA256 == da->algo)
    {
      digest_probe_log(da, "branch_sha256");
      return SHA256_DIGEST_SIZE;
    }
#endif /* MHD_SHA256_SUPPORT */
#ifdef MHD_SHA512_256_SUPPORT
  if (MHD_DIGEST_BASE_ALGO_SHA512_256 == da->algo)
    {
      digest_probe_log(da, "branch_sha512_256");
      return SHA512_256_DIGEST_SIZE;
    }
#endif /* MHD_SHA512_256_SUPPORT */

  digest_probe_log(da, "unexpected_path_before_assert0");
  mhd_assert (0); /* May not happen */
  return 0;
}
