#include <stdio.h>
#include <stdint.h>
#include <limits.h>
#include <stddef.h>

/* Probe helper: append human-readable logs about control flow and state. */
static void
file_reader_log_probe(const char *location,
                      uint64_t pos,
                      long long offset64_ll,
                      size_t max,
                      int fd,
                      size_t sizeof_off_t_sz,
                      size_t sizeof_uint64_sz,
                      int cond_left,
                      int cond_right)
{
  FILE *f = fopen("/tmp/logs/2215.txt", "a");
  if (f != NULL)
  {
    /* Note: cast to unsigned long long / long long for portability of format. */
    fprintf(f,
            "location=%s pos=%llu offset64=%lld max=%zu fd=%d sizeof(off_t)=%zu sizeof(uint64_t)=%zu cond_left=%d cond_right=%d\n",
            location,
            (unsigned long long) pos,
            (long long) offset64_ll,
            max,
            fd,
            sizeof_off_t_sz,
            sizeof_uint64_sz,
            cond_left,
            cond_right);
    fclose(f);
  }
}

static ssize_t
file_reader (void *cls,
             uint64_t pos,
             char *buf,
             size_t max)
{
  struct MHD_Response *response = cls;
#if ! defined(_WIN32) || defined(__CYGWIN__)
  ssize_t n;
#else  /* _WIN32 && !__CYGWIN__ */
  const HANDLE fh = (HANDLE) (uintptr_t) _get_osfhandle (response->fd);
#endif /* _WIN32 && !__CYGWIN__ */
  const int64_t offset64 = (int64_t) (pos + response->fd_off);

  /* Log entry state */
  file_reader_log_probe("entry",
                        pos,
                        (long long) offset64,
                        max,
                        response ? response->fd : -1,
                        sizeof(off_t),
                        sizeof(uint64_t),
                        (int) (sizeof(off_t) < sizeof(uint64_t)),
                        (int) (offset64 > (uint64_t) INT32_MAX));

  if (offset64 < 0)
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */

#if ! defined(_WIN32) || defined(__CYGWIN__)
  if (max > SSIZE_MAX)
    max = SSIZE_MAX; /* Clamp to maximum return value. */

#if defined(HAVE_PREAD64)
  n = pread64 (response->fd, buf, max, offset64);
#elif defined(HAVE_PREAD)
  /* Probe the operands used in the controlling expression. */
  {
    int cond_left = (int) (sizeof(off_t) < sizeof(uint64_t));
    int cond_right = (int) (offset64 > (uint64_t) INT32_MAX);
    file_reader_log_probe("HAVE_PREAD_check",
                          pos,
                          (long long) offset64,
                          max,
                          response->fd,
                          sizeof(off_t),
                          sizeof(uint64_t),
                          cond_left,
                          cond_right);
  }

  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Read at required position is not possible. */

  n = pread (response->fd, buf, max, (off_t) offset64);
#else  /* ! HAVE_PREAD */
#if defined(HAVE_LSEEK64)
  if (lseek64 (response->fd,
               offset64,
               SEEK_SET) != offset64)
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
#else  /* ! HAVE_LSEEK64 */
  /* Probe the operands used in the controlling expression. */
  {
    int cond_left = (int) (sizeof(off_t) < sizeof(uint64_t));
    int cond_right = (int) (offset64 > (uint64_t) INT32_MAX);
    file_reader_log_probe("LSEEK_check",
                          pos,
                          (long long) offset64,
                          max,
                          response->fd,
                          sizeof(off_t),
                          sizeof(uint64_t),
                          cond_left,
                          cond_right);
  }

  if ( (sizeof(off_t) < sizeof (uint64_t)) &&
       (offset64 > (uint64_t) INT32_MAX) )
    return MHD_CONTENT_READER_END_WITH_ERROR; /* seek to required position is not possible */

  if (lseek (response->fd,
             (off_t) offset64,
             SEEK_SET) != (off_t) offset64)
    return MHD_CONTENT_READER_END_WITH_ERROR; /* can't seek to required position */
#endif /* ! HAVE_LSEEK64 */
  n = read (response->fd,
            buf,
            max);

#endif /* ! HAVE_PREAD */
  if (0 == n)
  {
    file_reader_log_probe("end_of_stream",
                          pos,
                          (long long) offset64,
                          max,
                          response->fd,
                          sizeof(off_t),
                          sizeof(uint64_t),
                          0,
                          0);
    return MHD_CONTENT_READER_END_OF_STREAM;
  }
  if (n < 0)
  {
    file_reader_log_probe("read_error",
                          pos,
                          (long long) offset64,
                          max,
                          response->fd,
                          sizeof(off_t),
                          sizeof(uint64_t),
                          0,
                          0);
    return MHD_CONTENT_READER_END_WITH_ERROR;
  }
  file_reader_log_probe("return_bytes",
                        pos,
                        (long long) offset64,
                        (size_t) n,
                        response->fd,
                        sizeof(off_t),
                        sizeof(uint64_t),
                        0,
                        0);
  return n;
#else /* _WIN32 && !__CYGWIN__ */
  if (INVALID_HANDLE_VALUE == fh)
    return MHD_CONTENT_READER_END_WITH_ERROR; /* Value of 'response->fd' is not valid. */
  else
  {
    OVERLAPPED f_ol = {0, 0, {{0, 0}}, 0};   /* Initialize to zero. */
    ULARGE_INTEGER pos_uli;
    DWORD toRead = (max > INT32_MAX) ? INT32_MAX : (DWORD) max;
    DWORD resRead;

    pos_uli.QuadPart = (uint64_t) offset64;   /* Simple transformation 64bit -> 2x32bit. */
    f_ol.Offset = pos_uli.LowPart;
    f_ol.OffsetHigh = pos_uli.HighPart;
    if (! ReadFile (fh, (void *) buf, toRead, &resRead, &f_ol))
      return MHD_CONTENT_READER_END_WITH_ERROR;   /* Read error. */
    if (0 == resRead)
      return MHD_CONTENT_READER_END_OF_STREAM;
    return (ssize_t) resRead;
  }
#endif /* _WIN32 && !__CYGWIN__ */
}
