#include <stdio.h>
#include <stdarg.h>
#include <stdbool.h>
#include <string.h>

/* Probe logging helper that appends human-readable information to the
   evidence file. Keeps signature and behaviour of get_no_space_err_status_code
   unchanged; only records state prior to critical checks and returns. */
static void
mhd_log_probe (const char *fmt, ...)
{
  FILE *f = fopen("/tmp/logs/382.txt", "a");
  if (NULL == f)
    return;
  va_list ap;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

static unsigned int
get_no_space_err_status_code (struct MHD_Connection *c,
                              enum MHD_ProcRecvDataStage stage,
                              const char *add_element,
                              size_t add_element_size)
{
  size_t method_size;
  size_t uri_size;
  size_t opt_headers_size;
  size_t host_field_line_size;

  /* Log entry and initial parameter/state snapshot */
  mhd_log_probe ("enter: c=%p, c->state=%d, stage=%d, add_element=%p, add_element_size=%zu",
                 (void *) c,
                 (int) (c ? c->state : -1),
                 (int) stage,
                 (void *) add_element,
                 add_element_size);

  /* Log the assertion conditions before executing the mhd_assert calls so
     that a build in which mhd_assert is a no-op still records that the
     assertion would have been evaluated here. */
  {
    bool cond0 = (MHD_CONNECTION_REQ_LINE_RECEIVED < c->state);
    mhd_log_probe ("mhd_assert 0 condition: %d (MHD_CONNECTION_REQ_LINE_RECEIVED < c->state)", cond0);
  }
  mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVED < c->state);

  {
    bool cond1 = (MHD_PROC_RECV_HEADERS <= stage);
    mhd_log_probe ("mhd_assert 1 condition: %d (MHD_PROC_RECV_HEADERS <= stage)", cond1);
  }
  mhd_assert (MHD_PROC_RECV_HEADERS <= stage);

  {
    bool cond2 = ((0 == add_element_size) || (NULL != add_element));
    mhd_log_probe ("mhd_assert 2 condition: %d ((0==add_element_size) || (NULL!=add_element))", cond2);
  }
  mhd_assert ((0 == add_element_size) || (NULL != add_element));

  if (MHD_CONNECTION_HEADERS_RECEIVED > c->state)
  {
    {
      bool cond3 = (NULL != c->rq.field_lines.start);
      mhd_log_probe ("branch: headers not received yet; mhd_assert 3 condition: %d (NULL != c->rq.field_lines.start)", cond3);
    }
    mhd_assert (NULL != c->rq.field_lines.start);
    opt_headers_size =
      (size_t) ((c->read_buffer + c->read_buffer_offset)
                - c->rq.field_lines.start);
    mhd_log_probe ("computed opt_headers_size (partial read buffer): %zu", opt_headers_size);
  }
  else
  {
    opt_headers_size = c->rq.field_lines.size;
    mhd_log_probe ("computed opt_headers_size (full): %zu", opt_headers_size);
  }

  /* The read buffer is fully used by the request line, the field lines
     (headers) and internal information.
     The return status code works as a suggestion for the client to reduce
     one of the request elements. */

  if ((MHD_PROC_RECV_BODY_CHUNKED == stage) &&
      (MHD_MIN_REASONABLE_REQ_CHUNK_LINE_LENGTH_ < add_element_size))
  {
    mhd_log_probe ("branch: chunked body and add_element_size > MHD_MIN_REASONABLE_REQ_CHUNK_LINE_LENGTH_, returning MHD_HTTP_CONTENT_TOO_LARGE (add_element_size=%zu)", add_element_size);
    return MHD_HTTP_CONTENT_TOO_LARGE;
  }

  host_field_line_size = 0;
  /* The "Host:" field line is mandatory.
     The total size of the field lines (headers) cannot be smaller than
     the size of the "Host:" field line. */
  if ((MHD_PROC_RECV_HEADERS == stage)
      && (0 != add_element_size))
  {
    static const size_t header_host_key_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    const bool is_host_header =
      (header_host_key_len + 1 <= add_element_size)
      && ( (0 == add_element[header_host_key_len])
           || (':' == add_element[header_host_key_len]) )
      && MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_HOST,
                                        add_element,
                                        header_host_key_len);
    mhd_log_probe ("checking candidate host header: is_host_header=%d, header_host_key_len=%zu, add_element_size=%zu",
                   is_host_header,
                   header_host_key_len,
                   add_element_size);
    if (is_host_header)
    {
      const bool is_parsed = ! (
        (MHD_CONNECTION_HEADERS_RECEIVED > c->state) &&
        (add_element_size == c->read_buffer_offset) &&
        (c->read_buffer == add_element) );
      size_t actual_element_size;

      {
        bool cond4 = (! is_parsed) || (0 == add_element[header_host_key_len]);
        mhd_log_probe ("mhd_assert 4 condition: %d (!is_parsed || add_element[header_host_key_len]==0)", cond4);
      }
      mhd_assert (! is_parsed || (0 == add_element[header_host_key_len]));

      /* The actual size should be larger due to CRLF or LF chars,
         however the exact termination sequence is not known here and
         as perfect precision is not required, to simplify the code
         assume the minimal length. */
      if (is_parsed)
        actual_element_size = add_element_size + 1;  /* "1" for LF */
      else
        actual_element_size = add_element_size;

      host_field_line_size = actual_element_size;
      {
        bool cond5 = (opt_headers_size >= actual_element_size);
        mhd_log_probe ("mhd_assert 5 condition: %d (opt_headers_size >= actual_element_size) opt_headers_size=%zu actual_element_size=%zu",
                       cond5,
                       opt_headers_size,
                       actual_element_size);
      }
      mhd_assert (opt_headers_size >= actual_element_size);
      opt_headers_size -= actual_element_size;
      mhd_log_probe ("after removing host_field_line_size, opt_headers_size=%zu", opt_headers_size);
    }
  }
  if (0 == host_field_line_size)
  {
    static const size_t host_field_name_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    size_t host_field_name_value_len;
    if (MHD_NO != MHD_lookup_connection_value_n (c,
                                                 MHD_HEADER_KIND,
                                                 MHD_HTTP_HEADER_HOST,
                                                 host_field_name_len,
                                                 NULL,
                                                 &host_field_name_value_len))
    {
      /* Calculate the minimal size of the field line: no space between
         colon and the field value, line terminated by LR */
      host_field_line_size =
        host_field_name_len + host_field_name_value_len + 2; /* "2" for ':' and LF */

      mhd_log_probe ("found Host header via lookup: host_field_line_size=%zu, host_field_name_value_len=%zu", host_field_line_size, host_field_name_value_len);

      /* The "Host:" field could be added by application */
      if (opt_headers_size >= host_field_line_size)
      {
        opt_headers_size -= host_field_line_size;
        /* Take into account typical space after colon and CR at the end of the line */
        if (opt_headers_size >= 2)
          opt_headers_size -= 2;
        mhd_log_probe ("Host header accounted for in opt_headers_size, now opt_headers_size=%zu", opt_headers_size);
      }
      else
      {
        host_field_line_size = 0; /* No "Host:" field line set by the client */
        mhd_log_probe ("Host header indicated by lookup but not enough opt_headers_size; clearing host_field_line_size and leaving opt_headers_size=%zu", opt_headers_size);
      }
    }
  }

  uri_size = c->rq.req_target_len;
  if (MHD_HTTP_MTHD_OTHER != c->rq.http_mthd)
    method_size = 0; /* Do not recommend shorter request method */
  else
  {
    {
      bool cond6 = (NULL != c->rq.method);
      mhd_log_probe ("mhd_assert 6 condition: %d (NULL != c->rq.method)", cond6);
    }
    mhd_assert (NULL != c->rq.method);
    method_size = strlen (c->rq.method);
  }

  mhd_log_probe ("sizes: method_size=%zu, uri_size=%zu, opt_headers_size=%zu, host_field_line_size=%zu",
                 method_size, uri_size, opt_headers_size, host_field_line_size);

  if ((size_t) MHD_MAX_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    /* Typically the easiest way to reduce request header size is
       a removal of some optional headers. */
    if (opt_headers_size > (uri_size / 8))
    {
      if ((opt_headers_size / 2) > method_size)
      {
        mhd_log_probe ("returning MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE (opt_headers_size=%zu, method_size=%zu)", opt_headers_size, method_size);
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        mhd_log_probe ("returning MHD_HTTP_NOT_IMPLEMENTED (method size unreasonably large) opt_headers_size=%zu method_size=%zu", opt_headers_size, method_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is MUCH larger than headers */
      if ((uri_size / 16) > method_size)
      {
        mhd_log_probe ("returning MHD_HTTP_URI_TOO_LONG (uri_size=%zu, method_size=%zu)", uri_size, method_size);
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        mhd_log_probe ("returning MHD_HTTP_NOT_IMPLEMENTED (method size unreasonably large) uri_size=%zu method_size=%zu", uri_size, method_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MAX_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    /* If request target size if larger than maximum reasonable size
       recommend client to reduce the request target size (length). */
    if ((uri_size / 16) > method_size)
    {
      mhd_log_probe ("returning MHD_HTTP_URI_TOO_LONG (uri_size > max_reasonable_req_target) uri_size=%zu method_size=%zu", uri_size, method_size);
      return MHD_HTTP_URI_TOO_LONG;     /* Request target is MUCH larger than headers */
    }
    else
    {
      mhd_log_probe ("returning MHD_HTTP_NOT_IMPLEMENTED (method size unreasonably large) uri_size=%zu method_size=%zu", uri_size, method_size);
      return MHD_HTTP_NOT_IMPLEMENTED;  /* The length of the HTTP request method is unreasonably large */
    }
  }

  /* The read buffer is too small to handle reasonably large requests */

  if ((size_t) MHD_MIN_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    /* Recommend application to retry with minimal headers */
    if ((opt_headers_size * 4) > uri_size)
    {
      if (opt_headers_size > method_size)
      {
        mhd_log_probe ("returning MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE (opt_headers_size*4 > uri_size) opt_headers_size=%zu method_size=%zu uri_size=%zu",
                       opt_headers_size, method_size, uri_size);
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        mhd_log_probe ("returning MHD_HTTP_NOT_IMPLEMENTED (method size unreasonably large) opt_headers_size=%zu method_size=%zu uri_size=%zu",
                       opt_headers_size, method_size, uri_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is significantly larger than headers */
      if (uri_size > method_size * 4)
      {
        mhd_log_probe ("returning MHD_HTTP_URI_TOO_LONG (uri_size > method_size*4) uri_size=%zu method_size=%zu", uri_size, method_size);
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        mhd_log_probe ("returning MHD_HTTP_NOT_IMPLEMENTED (method size unreasonably large) uri_size=%zu method_size=%zu", uri_size, method_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MIN_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    /* Recommend application to retry with a shorter request target */
    if (uri_size > method_size * 4)
    {
      mhd_log_probe ("returning MHD_HTTP_URI_TOO_LONG (uri_size > min_reasonable_req_target) uri_size=%zu method_size=%zu", uri_size, method_size);
      return MHD_HTTP_URI_TOO_LONG;
    }
    else
    {
      mhd_log_probe ("returning MHD_HTTP_NOT_IMPLEMENTED (method size unreasonably large) uri_size=%zu method_size=%zu", uri_size, method_size);
      return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
    }
  }

  if ((size_t) MHD_MIN_REASONABLE_REQ_METHOD_SIZE_ < method_size)
  {
    /* The request target (URI) and headers are (reasonably) very small.
       Some non-standard long request method is used. */
    /* The last resort response as it means "the method is not supported
       by the server for any URI". */
    mhd_log_probe ("returning MHD_HTTP_NOT_IMPLEMENTED (method size > min_reasonable_req_method_size) method_size=%zu", method_size);
    return MHD_HTTP_NOT_IMPLEMENTED;
  }

  /* The almost impossible situation: all elements are small, but cannot
     fit the buffer. The application set the buffer size to
     critically low value? */

  if ((1 < opt_headers_size) || (1 < uri_size))
  {
    if (opt_headers_size >= uri_size)
    {
      mhd_log_probe ("returning MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE (opt_headers_size>=uri_size) opt_headers_size=%zu uri_size=%zu", opt_headers_size, uri_size);
      return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
    }
    else
    {
      mhd_log_probe ("returning MHD_HTTP_URI_TOO_LONG (uri_size>opt_headers_size) opt_headers_size=%zu uri_size=%zu", opt_headers_size, uri_size);
      return MHD_HTTP_URI_TOO_LONG;
    }
  }

  /* Nothing to reduce in the request.
     Reply with some status. */
  if (0 != host_field_line_size)
  {
    mhd_log_probe ("returning MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE (host_field_line_size!=0) host_field_line_size=%zu", host_field_line_size);
    return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
  }

  mhd_log_probe ("returning MHD_HTTP_URI_TOO_LONG (default)");
  return MHD_HTTP_URI_TOO_LONG;
}
