static void
mhd_probe_log (const char *fmt, ...)
{
  /* Simple logging helper: append human-readable traces to file. */
  FILE *f = fopen("/tmp/logs/383.txt", "a");
  if (f)
    {
      va_list ap;
      va_start (ap, fmt);
      vfprintf (f, fmt, ap);
      va_end (ap);
      fprintf (f, "\n");
      fclose (f);
    }
}

static unsigned int
get_no_space_err_status_code (struct MHD_Connection *c,
                              enum MHD_ProcRecvDataStage stage,
                              const char *add_element,
                              size_t add_element_size)
{
  size_t method_size;
  size_t uri_size;
  size_t opt_headers_size;
  size_t host_field_line_size;

  /* Entry log: capture key inputs and buffer pointers */
  mhd_probe_log ("ENTER get_no_space_err_status_code: c=%p state=%d stage=%d add_element=%p add_element_size=%zu read_buffer=%p read_buffer_offset=%zu rq.field_lines.start=%p rq.field_lines.size=%zu rq.req_target_len=%zu rq.http_mthd=%d",
                 (void *) c,
                 (int) c->state,
                 (int) stage,
                 (const void *) add_element,
                 add_element_size,
                 (void *) c->read_buffer,
                 (size_t) c->read_buffer_offset,
                 (void *) c->rq.field_lines.start,
                 (size_t) c->rq.field_lines.size,
                 (size_t) c->rq.req_target_len,
                 (int) c->rq.http_mthd);

  mhd_assert (MHD_CONNECTION_REQ_LINE_RECEIVED < c->state);
  mhd_assert (MHD_PROC_RECV_HEADERS <= stage);
  mhd_assert ((0 == add_element_size) || (NULL != add_element));

  if (MHD_CONNECTION_HEADERS_RECEIVED > c->state)
  {
    mhd_assert (NULL != c->rq.field_lines.start);
    opt_headers_size =
      (size_t) ((c->read_buffer + c->read_buffer_offset)
                - c->rq.field_lines.start);
    mhd_probe_log ("computed opt_headers_size (headers in buffer): %zu (read_buffer=%p + offset=%zu - start=%p)",
                   opt_headers_size,
                   (void *) c->read_buffer,
                   (size_t) c->read_buffer_offset,
                   (void *) c->rq.field_lines.start);
  }
  else
  {
    opt_headers_size = c->rq.field_lines.size;
    mhd_probe_log ("opt_headers_size from rq.field_lines.size: %zu",
                   opt_headers_size);
  }

  /* The read buffer is fully used by the request line, the field lines
     (headers) and internal information.
     The return status code works as a suggestion for the client to reduce
     one of the request elements. */

  if ((MHD_PROC_RECV_BODY_CHUNKED == stage) &&
      (MHD_MIN_REASONABLE_REQ_CHUNK_LINE_LENGTH_ < add_element_size))
  {
    /* Request could be re-tried easily with smaller chunk sizes */
    mhd_probe_log ("branch: chunked body and add_element_size=%zu > %d -> return MHD_HTTP_CONTENT_TOO_LARGE",
                   add_element_size,
                   (int) MHD_MIN_REASONABLE_REQ_CHUNK_LINE_LENGTH_);
    return MHD_HTTP_CONTENT_TOO_LARGE;
  }

  host_field_line_size = 0;
  /* The "Host:" field line is mandatory.
     The total size of the field lines (headers) cannot be smaller than
     the size of the "Host:" field line. */
  if ((MHD_PROC_RECV_HEADERS == stage)
      && (0 != add_element_size))
  {
    static const size_t header_host_key_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    const bool is_host_header =
      (header_host_key_len + 1 <= add_element_size)
      && ( (0 == add_element[header_host_key_len])
           || (':' == add_element[header_host_key_len]) )
      && MHD_str_equal_caseless_bin_n_ (MHD_HTTP_HEADER_HOST,
                                        add_element,
                                        header_host_key_len);

    mhd_probe_log ("evaluating potential Host header: header_host_key_len=%zu add_element_size=%zu add_element[%zu]=%d is_host_header=%d",
                   header_host_key_len,
                   add_element_size,
                   header_host_key_len,
                   (int) add_element[header_host_key_len],
                   (int) is_host_header);

    if (is_host_header)
    {
      const bool is_parsed = ! (
        (MHD_CONNECTION_HEADERS_RECEIVED > c->state) &&
        (add_element_size == c->read_buffer_offset) &&
        (c->read_buffer == add_element) );

      /* Log detailed components used to compute is_parsed; this includes
         the pointer comparison that mixes a const-qualified pointer
         (add_element) with a non-const pointer (c->read_buffer) which is
         the subject of the reported MISRA 8.13 concern. */
      mhd_probe_log ("is_parsed components: (MHD_CONNECTION_HEADERS_RECEIVED > c->state)=%d, add_element_size==c->read_buffer_offset? %d ( %zu == %zu ), pointer_equality (c->read_buffer == add_element)=%d, is_parsed=%d",
                     (int) (MHD_CONNECTION_HEADERS_RECEIVED > c->state),
                     (int) (add_element_size == c->read_buffer_offset),
                     add_element_size,
                     (size_t) c->read_buffer_offset,
                     (int) (c->read_buffer == add_element),
                     (int) is_parsed);

      size_t actual_element_size;

      mhd_assert (! is_parsed || (0 == add_element[header_host_key_len]));
      /* The actual size should be larger due to CRLF or LF chars,
         however the exact termination sequence is not known here and
         as perfect precision is not required, to simplify the code
         assume the minimal length. */
      if (is_parsed)
        actual_element_size = add_element_size + 1;  /* "1" for LF */
      else
        actual_element_size = add_element_size;

      host_field_line_size = actual_element_size;
      mhd_assert (opt_headers_size >= actual_element_size);
      opt_headers_size -= actual_element_size;

      mhd_probe_log ("host header parsed: actual_element_size=%zu host_field_line_size=%zu opt_headers_size(after removal)=%zu",
                     actual_element_size,
                     host_field_line_size,
                     opt_headers_size);
    }
  }
  if (0 == host_field_line_size)
  {
    static const size_t host_field_name_len =
      MHD_STATICSTR_LEN_ (MHD_HTTP_HEADER_HOST);
    size_t host_field_name_value_len;
    if (MHD_NO != MHD_lookup_connection_value_n (c,
                                                 MHD_HEADER_KIND,
                                                 MHD_HTTP_HEADER_HOST,
                                                 host_field_name_len,
                                                 NULL,
                                                 &host_field_name_value_len))
    {
      /* Calculate the minimal size of the field line: no space between
         colon and the field value, line terminated by LR */
      host_field_line_size =
        host_field_name_len + host_field_name_value_len + 2; /* "2" for ':' and LF */

      /* The "Host:" field could be added by application */
      if (opt_headers_size >= host_field_line_size)
      {
        opt_headers_size -= host_field_line_size;
        /* Take into account typical space after colon and CR at the end of the line */
        if (opt_headers_size >= 2)
          opt_headers_size -= 2;
        mhd_probe_log ("Host present via lookup: host_field_line_size=%zu opt_headers_size(after heuristics)=%zu",
                       host_field_line_size,
                       opt_headers_size);
      }
      else
      {
        host_field_line_size = 0; /* No "Host:" field line set by the client */
        mhd_probe_log ("Host lookup exists but not enough opt_headers_size (%zu) for minimal host_field_line_size (%zu)",
                       opt_headers_size + host_field_line_size,
                       host_field_line_size);
      }
    }
  }

  uri_size = c->rq.req_target_len;
  if (MHD_HTTP_MTHD_OTHER != c->rq.http_mthd)
    method_size = 0; /* Do not recommend shorter request method */
  else
  {
    mhd_assert (NULL != c->rq.method);
    method_size = strlen (c->rq.method);
  }

  mhd_probe_log ("sizes: uri_size=%zu method_size=%zu opt_headers_size=%zu host_field_line_size=%zu",
                 uri_size,
                 method_size,
                 opt_headers_size,
                 host_field_line_size);

  if ((size_t) MHD_MAX_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    /* Typically the easiest way to reduce request header size is
       a removal of some optional headers. */
    if (opt_headers_size > (uri_size / 8))
    {
      if ((opt_headers_size / 2) > method_size)
      {
        mhd_probe_log ("branch: opt_headers_size > max_reasonable and > uri/8 and /2 > method_size -> REQUEST_HEADER_FIELDS_TOO_LARGE (opt_headers_size=%zu method_size=%zu uri_size=%zu)",
                       opt_headers_size,
                       method_size,
                       uri_size);
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        mhd_probe_log ("branch: opt_headers_size > max_reasonable and > uri/8 but /2 <= method_size -> NOT_IMPLEMENTED (method too large) (opt_headers_size=%zu method_size=%zu)",
                       opt_headers_size,
                       method_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is MUCH larger than headers */
      if ((uri_size / 16) > method_size)
      {
        mhd_probe_log ("branch: opt_headers_size > max_reasonable but opt_headers_size <= uri/8 and uri/16 > method_size -> URI_TOO_LONG (uri_size=%zu method_size=%zu)",
                       uri_size,
                       method_size);
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        mhd_probe_log ("branch: opt_headers_size > max_reasonable but uri/16 <= method_size -> NOT_IMPLEMENTED (method too large) (uri_size=%zu method_size=%zu)",
                       uri_size,
                       method_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MAX_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    /* If request target size if larger than maximum reasonable size
       recommend client to reduce the request target size (length). */
    if ((uri_size / 16) > method_size)
    {
      mhd_probe_log ("branch: uri_size > max_reasonable_req_target_size and uri/16 > method_size -> URI_TOO_LONG (uri_size=%zu method_size=%zu)",
                     uri_size,
                     method_size);
      return MHD_HTTP_URI_TOO_LONG;     /* Request target is MUCH larger than headers */
    }
    else
    {
      mhd_probe_log ("branch: uri_size > max_reasonable_req_target_size but uri/16 <= method_size -> NOT_IMPLEMENTED (uri_size=%zu method_size=%zu)",
                     uri_size,
                     method_size);
      return MHD_HTTP_NOT_IMPLEMENTED;  /* The length of the HTTP request method is unreasonably large */
    }
  }

  /* The read buffer is too small to handle reasonably large requests */

  if ((size_t) MHD_MIN_REASONABLE_HEADERS_SIZE_ < opt_headers_size)
  {
    /* Recommend application to retry with minimal headers */
    if ((opt_headers_size * 4) > uri_size)
    {
      if (opt_headers_size > method_size)
      {
        mhd_probe_log ("branch: opt_headers_size reasonably large and *4 > uri_size and opt_headers_size > method_size -> REQUEST_HEADER_FIELDS_TOO_LARGE (opt_headers_size=%zu method_size=%zu uri_size=%zu)",
                       opt_headers_size,
                       method_size,
                       uri_size);
        return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
      }
      else
      {
        mhd_probe_log ("branch: opt_headers_size reasonably large and *4 > uri_size but opt_headers_size <= method_size -> NOT_IMPLEMENTED (opt_headers_size=%zu method_size=%zu)",
                       opt_headers_size,
                       method_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
    else
    { /* Request target is significantly larger than headers */
      if (uri_size > method_size * 4)
      {
        mhd_probe_log ("branch: min_reasonable headers condition else and uri_size > method_size*4 -> URI_TOO_LONG (uri_size=%zu method_size=%zu)",
                       uri_size,
                       method_size);
        return MHD_HTTP_URI_TOO_LONG;
      }
      else
      {
        mhd_probe_log ("branch: min_reasonable headers condition else but uri_size <= method_size*4 -> NOT_IMPLEMENTED (uri_size=%zu method_size=%zu)",
                       uri_size,
                       method_size);
        return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
      }
    }
  }
  if ((size_t) MHD_MIN_REASONABLE_REQ_TARGET_SIZE_ < uri_size)
  {
    /* Recommend application to retry with a shorter request target */
    if (uri_size > method_size * 4)
    {
      mhd_probe_log ("branch: uri_size > min_reasonable_req_target_size and uri_size > method_size*4 -> URI_TOO_LONG (uri_size=%zu method_size=%zu)",
                     uri_size,
                     method_size);
      return MHD_HTTP_URI_TOO_LONG;
    }
    else
    {
      mhd_probe_log ("branch: uri_size > min_reasonable_req_target_size but uri_size <= method_size*4 -> NOT_IMPLEMENTED (uri_size=%zu method_size=%zu)",
                     uri_size,
                     method_size);
      return MHD_HTTP_NOT_IMPLEMENTED; /* The length of the HTTP request method is unreasonably large */
    }
  }

  if ((size_t) MHD_MIN_REASONABLE_REQ_METHOD_SIZE_ < method_size)
  {
    /* The request target (URI) and headers are (reasonably) very small.
       Some non-standard long request method is used. */
    /* The last resort response as it means "the method is not supported
       by the server for any URI". */
    mhd_probe_log ("branch: method_size > min_reasonable_req_method_size -> NOT_IMPLEMENTED (method_size=%zu)",
                   method_size);
    return MHD_HTTP_NOT_IMPLEMENTED;
  }

  /* The almost impossible situation: all elements are small, but cannot
     fit the buffer. The application set the buffer size to
     critically low value? */

  if ((1 < opt_headers_size) || (1 < uri_size))
  {
    if (opt_headers_size >= uri_size)
    {
      mhd_probe_log ("branch: final small-elements check opt_headers_size >= uri_size -> REQUEST_HEADER_FIELDS_TOO_LARGE (opt_headers_size=%zu uri_size=%zu)",
                     opt_headers_size,
                     uri_size);
      return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
    }
    else
    {
      mhd_probe_log ("branch: final small-elements check opt_headers_size < uri_size -> URI_TOO_LONG (opt_headers_size=%zu uri_size=%zu)",
                     opt_headers_size,
                     uri_size);
      return MHD_HTTP_URI_TOO_LONG;
    }
  }

  /* Nothing to reduce in the request.
     Reply with some status. */
  if (0 != host_field_line_size)
  {
    mhd_probe_log ("nothing to reduce but host_field_line_size != 0 -> REQUEST_HEADER_FIELDS_TOO_LARGE (host_field_line_size=%zu)",
                   host_field_line_size);
    return MHD_HTTP_REQUEST_HEADER_FIELDS_TOO_LARGE;
  }

  mhd_probe_log ("nothing to reduce and no host field line -> URI_TOO_LONG");
  return MHD_HTTP_URI_TOO_LONG;
}
