#include <stdio.h>
#include <stddef.h>
#include <stdint.h>

/* Probe logging helper - writes details to /tmp/logs/409.txt.
   Does not modify program state; only inspects pointers and sizes safely. */
static void
mhd_probe_log_header_state (struct MHD_Connection *c,
                            const char *stage,
                            enum MHD_HdrLineReadRes_ res,
                            const struct _MHD_str_w_len *hdr_name,
                            const struct _MHD_str_w_len *hdr_value)
{
  FILE *f = fopen("/tmp/logs/409.txt", "a");
  if (NULL == f)
    return;

  /* Basic connection/frame state */
  fprintf (f, "STAGE=%s res=%d conn_state=%d read_buffer=%p read_buffer_size=%zu read_buffer_offset=%zu\n",
           (stage ? stage : "(null)"), (int) res, (int) c->state,
           (void *) c->read_buffer,
           (size_t) c->read_buffer_size,
           (size_t) c->read_buffer_offset);

  /* Log header name/value pointers and lengths */
  if (NULL != hdr_name)
    fprintf (f, "hdr_name: str=%p len=%zu\n",
             (void *) hdr_name->str, (size_t) hdr_name->len);
  else
    fprintf (f, "hdr_name: (null)\n");

  if (NULL != hdr_value)
    fprintf (f, "hdr_value: str=%p len=%zu\n",
             (void *) hdr_value->str, (size_t) hdr_value->len);
  else
    fprintf (f, "hdr_value: (null)\n");

  /* If hdr_name->str lies within the connection read buffer, compute available bytes
     we can safely inspect. Otherwise report out-of-range. */
  if (NULL != hdr_name && NULL != hdr_name->str && NULL != c->read_buffer)
    {
      const char *rb_start = c->read_buffer;
      const char *rb_end = c->read_buffer + c->read_buffer_size;

      if (hdr_name->str >= rb_start && hdr_name->str < rb_end)
        {
          size_t available = (size_t) (rb_end - hdr_name->str);
          size_t to_dump = hdr_name->len + 1; /* try to show one byte after claimed len */
          if (to_dump > available)
            to_dump = available;
          /* limit dump to a reasonable size */
          if (to_dump > 64)
            to_dump = 64;

          fprintf (f, "hdr_name available_in_read_buffer=%zu dump_len=%zu\n",
                   (size_t) available, (size_t) to_dump);

          /* Print bytes in hex and printable ascii for the portion we can safely read */
          fprintf (f, "hdr_name_bytes:");
          for (size_t i = 0; i < to_dump; ++i)
            {
              unsigned char ch = (unsigned char) hdr_name->str[i];
              if (ch >= 32 && ch < 127)
                fprintf (f, " %c", (char) ch);
              else
                fprintf (f, " %02x", ch);
            }
          fprintf (f, "\n");

          /* Indicate whether a NUL appears within the available bytes */
          size_t seen_null = 0;
          for (size_t i = 0; i < to_dump; ++i)
            if (hdr_name->str[i] == '\0')
              { seen_null = 1; break; }
          fprintf (f, "hdr_name_null_within_dump=%d\n", (int) seen_null);
        }
      else
        {
          fprintf (f, "hdr_name.str is outside read_buffer range (or read_buffer not set)\n");
        }
    }
  else
    {
      fprintf (f, "hdr_name or read_buffer is NULL; cannot inspect bytes safely\n");
    }

  /* Similar, but smaller, inspection for hdr_value (optional) */
  if (NULL != hdr_value && NULL != hdr_value->str && NULL != c->read_buffer)
    {
      const char *rb_start = c->read_buffer;
      const char *rb_end = c->read_buffer + c->read_buffer_size;

      if (hdr_value->str >= rb_start && hdr_value->str < rb_end)
        {
          size_t available = (size_t) (rb_end - hdr_value->str);
          size_t to_dump = hdr_value->len > 0 ? (hdr_value->len > 16 ? 16 : hdr_value->len) : 0;
          if (to_dump > available)
            to_dump = available;
          fprintf (f, "hdr_value available_in_read_buffer=%zu dump_len=%zu\n",
                   (size_t) available, (size_t) to_dump);
          fprintf (f, "hdr_value_bytes:");
          for (size_t i = 0; i < to_dump; ++i)
            {
              unsigned char ch = (unsigned char) hdr_value->str[i];
              if (ch >= 32 && ch < 127)
                fprintf (f, " %c", (char) ch);
              else
                fprintf (f, " %02x", ch);
            }
          fprintf (f, "\n");
        }
      else
        fprintf (f, "hdr_value.str is outside read_buffer range (or read_buffer not set)\n");
    }

  fprintf (f, "----\n");
  fclose (f);
}

static bool
get_req_headers (struct MHD_Connection *c, bool process_footers)
{
  do
  {
    struct _MHD_str_w_len hdr_name;
    struct _MHD_str_w_len hdr_value;
    enum MHD_HdrLineReadRes_ res;

    mhd_assert ((process_footers ? MHD_CONNECTION_FOOTERS_RECEIVING : \
                 MHD_CONNECTION_REQ_HEADERS_RECEIVING) == \
                c->state);

    #ifdef _DEBUG
    hdr_name.str = NULL;
    hdr_value.str = NULL;
    #endif /* _DEBUG */
    res = get_req_header (c, process_footers, &hdr_name, &hdr_value);

    /* Probe after attempting to read one header line */
    mhd_probe_log_header_state (c, "after_get_req_header", res, &hdr_name, &hdr_value);

    if (MHD_HDR_LINE_READING_GOT_HEADER == res)
    {
      mhd_assert ((process_footers ? MHD_CONNECTION_FOOTERS_RECEIVING : \
                   MHD_CONNECTION_REQ_HEADERS_RECEIVING) == \
                  c->state);
      mhd_assert (NULL != hdr_name.str);
      mhd_assert (NULL != hdr_value.str);

      /* Insert an extra probe right before strlen checks which are the
         subject of the reported potential buffer overrun. This captures
         the pointer, claimed length, available bytes in the read buffer
         and a small byte dump for evidence. */
      mhd_probe_log_header_state (c, "before_strlen_checks", (enum MHD_HdrLineReadRes_) -1, &hdr_name, &hdr_value);

      /* Values must be zero-terminated and must not have binary zeros */
      mhd_assert (strlen (hdr_name.str) == hdr_name.len);
      mhd_assert (strlen (hdr_value.str) == hdr_value.len);
      /* Values must not have whitespaces at the start or at the end */
      mhd_assert ((hdr_name.len == 0) || (hdr_name.str[0] != ' '));
      mhd_assert ((hdr_name.len == 0) || (hdr_name.str[0] != '\t'));
      mhd_assert ((hdr_name.len == 0) || \
                  (hdr_name.str[hdr_name.len - 1] != ' '));
      mhd_assert ((hdr_name.len == 0) || \
                  (hdr_name.str[hdr_name.len - 1] != '\t'));
      mhd_assert ((hdr_value.len == 0) || (hdr_value.str[0] != ' '));
      mhd_assert ((hdr_value.len == 0) || (hdr_value.str[0] != '\t'));
      mhd_assert ((hdr_value.len == 0) || \
                  (hdr_value.str[hdr_value.len - 1] != ' '));
      mhd_assert ((hdr_value.len == 0) || \
                  (hdr_value.str[hdr_value.len - 1] != '\t'));

      if (MHD_NO ==
          MHD_set_connection_value_n_nocheck_ (c,
                                               (! process_footers) ?
                                               MHD_HEADER_KIND :
                                               MHD_FOOTER_KIND,
                                               hdr_name.str, hdr_name.len,
                                               hdr_value.str, hdr_value.len))
      {
        size_t add_element_size;

        mhd_assert (hdr_name.str < hdr_value.str);

#ifdef HAVE_MESSAGES
        MHD_DLOG (c->daemon,
                  _ ("Failed to allocate memory in the connection memory " \
                     "pool to store %s.\n"),
                  (! process_footers) ? _ ("header") : _ ("footer"));
#endif /* HAVE_MESSAGES */

        add_element_size = hdr_value.len
                           + (size_t) (hdr_value.str - hdr_name.str);

        if (! process_footers)
          handle_req_headers_no_space (c, hdr_name.str, add_element_size);
        else
          handle_req_footers_no_space (c, hdr_name.str, add_element_size);

        mhd_assert (MHD_CONNECTION_FULL_REQ_RECEIVED < c->state);
        return true;
      }
      /* Reset processing state */
      reset_rq_header_processing_state (c);
      mhd_assert ((process_footers ? MHD_CONNECTION_FOOTERS_RECEIVING : \
                   MHD_CONNECTION_REQ_HEADERS_RECEIVING) == \
                  c->state);
      /* Read the next header (field) line */
      continue;
    }
    else if (MHD_HDR_LINE_READING_NEED_MORE_DATA == res)
    {
      mhd_assert ((process_footers ? MHD_CONNECTION_FOOTERS_RECEIVING : \
                   MHD_CONNECTION_REQ_HEADERS_RECEIVING) == \
                  c->state);
      return false;
    }
    else if (MHD_HDR_LINE_READING_DATA_ERROR == res)
    {
      mhd_assert ((process_footers ? \
                   MHD_CONNECTION_FOOTERS_RECEIVING : \
                   MHD_CONNECTION_REQ_HEADERS_RECEIVING) < c->state);
      mhd_assert (c->stop_with_error);
      mhd_assert (c->discard_request);
      return true;
    }
    mhd_assert (MHD_HDR_LINE_READING_GOT_END_OF_HEADER == res);
    break;
  } while (1);

#ifdef HAVE_MESSAGES
  if (1 == c->rq.num_cr_sp_replaced)
  {
    MHD_DLOG (c->daemon,
              _ ("One bare CR character has been replaced with space " \
                 "in %s.\n"),
              (! process_footers) ?
              _ ("the request line or in the request headers") :
              _ ("the request footers"));
  }
  else if (0 != c->rq.num_cr_sp_replaced)
  {
    MHD_DLOG (c->daemon,
              _ ("%" PRIu64 " bare CR characters have been replaced with " \
                 "spaces in the request line and/or in the request %s.\n"),
              (uint64_t) c->rq.num_cr_sp_replaced,
              (! process_footers) ? _ ("headers") : _ ("footers"));
  }
  if (1 == c->rq.skipped_broken_lines)
  {
    MHD_DLOG (c->daemon,
              _ ("One %s line without colon has been skipped.\n"),
              (! process_footers) ? _ ("header") : _ ("footer"));
  }
  else if (0 != c->rq.skipped_broken_lines)
  {
    MHD_DLOG (c->daemon,
              _ ("%" PRIu64 " %s lines without colons has been skipped.\n"),
              (uint64_t) c->rq.skipped_broken_lines,
              (! process_footers) ? _ ("header") : _ ("footer"));
  }
#endif /* HAVE_MESSAGES */

  mhd_assert (c->rq.method < c->read_buffer);
  if (! process_footers)
  {
    c->rq.header_size = (size_t) (c->read_buffer - c->rq.method);
    mhd_assert (NULL != c->rq.field_lines.start);
    c->rq.field_lines.size =
      (size_t) ((c->read_buffer - c->rq.field_lines.start) - 1);
    if ('\r' == *(c->read_buffer - 2))
      c->rq.field_lines.size--;
    c->state = MHD_CONNECTION_HEADERS_RECEIVED;

    if (MHD_BUF_INC_SIZE > c->read_buffer_size)
    {
      /* Try to re-use some of the last bytes of the request header */
      /* Do this only if space in the read buffer is limited AND
         amount of read ahead data is small. */
      /**
       *  The position of the terminating NUL after the last character of
       *  the last header element.
       */
      const char *last_elmnt_end;
      size_t shift_back_size;
      if (NULL != c->rq.headers_received_tail)
        last_elmnt_end =
          c->rq.headers_received_tail->value
          + c->rq.headers_received_tail->value_size;
      else
        last_elmnt_end = c->rq.version + HTTP_VER_LEN;
      mhd_assert ((last_elmnt_end + 1) < c->read_buffer);
      shift_back_size = (size_t) (c->read_buffer - (last_elmnt_end + 1));
      if (0 != c->read_buffer_offset)
        memmove (c->read_buffer - shift_back_size,
                 c->read_buffer,
                 c->read_buffer_offset);
      c->read_buffer -= shift_back_size;
      c->read_buffer_size += shift_back_size;
    }
  }
  else
    c->state = MHD_CONNECTION_FOOTERS_RECEIVED;

  return true;
}
