#include <stdio.h>
#include <stdint.h>

/* Probe logging helper - writes concise state and control-path info to /tmp/logs/2429.txt */
static void
mhd_log_probe(const char *stage,
              const struct MHD_RqDAuthParam *const algo_param,
              const char *token_name)
{
  FILE *f = fopen("/tmp/logs/2429.txt", "a");
  if (NULL == f)
    return;

  uintptr_t ap_ptr = (uintptr_t)algo_param;
  uintptr_t str_ptr = 0;
  size_t len = 0;
  int quoted = -1;

  if (NULL != algo_param)
  {
    str_ptr = (uintptr_t)algo_param->value.str;
    len = (size_t)algo_param->value.len;
    quoted = (int)algo_param->quoted;
  }

  fprintf(f, "%s: algo_param=%#" PRIxPTR " quoted=%d value.str=%#" PRIxPTR " value.len=%zu token=%s\n",
          stage, ap_ptr, quoted, str_ptr, len, (token_name ? token_name : "(null)"));
  fclose(f);
}

static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  /* Log entry and basic state */
  mhd_log_probe("ENTRY", algo_param, "(none)");

  if (NULL == algo_param->value.str)
  {
    mhd_log_probe("NULL_VALUE_STR", algo_param, "(none)");
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    mhd_log_probe("BRANCH_QUOTED", algo_param, "(start quoted comparisons)");

    mhd_log_probe("COMPARE", algo_param, "_MHD_MD5_TOKEN");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_MD5_TOKEN))
    {
      mhd_log_probe("MATCH", algo_param, "_MHD_MD5_TOKEN");
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }

    mhd_log_probe("COMPARE", algo_param, "_MHD_SHA256_TOKEN");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA256_TOKEN))
    {
      mhd_log_probe("MATCH", algo_param, "_MHD_SHA256_TOKEN");
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }

    mhd_log_probe("COMPARE", algo_param, "_MHD_MD5_TOKEN _MHD_SESS_TOKEN");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      mhd_log_probe("MATCH", algo_param, "_MHD_MD5_TOKEN _MHD_SESS_TOKEN");
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }

    mhd_log_probe("COMPARE", algo_param, "_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA512_256_TOKEN \
                                               _MHD_SESS_TOKEN))

      /* Algorithms below are not supported by MHD for authentication */

      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;

    mhd_log_probe("COMPARE", algo_param, "_MHD_SHA256_TOKEN _MHD_SESS_TOKEN");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA256_TOKEN \
                                               _MHD_SESS_TOKEN))
    {
      mhd_log_probe("MATCH", algo_param, "_MHD_SHA256_TOKEN _MHD_SESS_TOKEN");
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }

    mhd_log_probe("COMPARE", algo_param, "_MHD_SHA512_256_TOKEN");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str, \
                                               algo_param->value.len, \
                                               _MHD_SHA512_256_TOKEN))
    {
      mhd_log_probe("MATCH", algo_param, "_MHD_SHA512_256_TOKEN");
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    mhd_log_probe("NO_MATCH_QUOTED", algo_param, "(none)");
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  mhd_log_probe("BRANCH_NOT_QUOTED", algo_param, "(start non-quoted comparisons)");

  mhd_log_probe("COMPARE", algo_param, "_MHD_MD5_TOKEN (non-quoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_log_probe("MATCH", algo_param, "_MHD_MD5_TOKEN (non-quoted)");
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }

  mhd_log_probe("COMPARE", algo_param, "_MHD_SHA256_TOKEN (non-quoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_log_probe("MATCH", algo_param, "_MHD_SHA256_TOKEN (non-quoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }

  mhd_log_probe("COMPARE", algo_param, "_MHD_SHA512_256_TOKEN (non-quoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_log_probe("MATCH", algo_param, "_MHD_SHA512_256_TOKEN (non-quoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */
  mhd_log_probe("COMPARE", algo_param, "_MHD_MD5_TOKEN _MHD_SESS_TOKEN (non-quoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_log_probe("MATCH", algo_param, "_MHD_MD5_TOKEN _MHD_SESS_TOKEN (non-quoted)");
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }

  mhd_log_probe("COMPARE", algo_param, "_MHD_SHA256_TOKEN _MHD_SESS_TOKEN (non-quoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_log_probe("MATCH", algo_param, "_MHD_SHA256_TOKEN _MHD_SESS_TOKEN (non-quoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }

  mhd_log_probe("COMPARE", algo_param, "_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN (non-quoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN, \
                                       algo_param->value.str, \
                                       algo_param->value.len))
  {
    mhd_log_probe("MATCH", algo_param, "_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN (non-quoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  mhd_log_probe("NO_MATCH_NOT_QUOTED", algo_param, "(none)");
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
