static void probe_log_event(const struct MHD_RqDAuthParam *const algo_param, const char *event)
{
  FILE *f = fopen("/tmp/logs/2429.txt", "a");
  if (NULL == f)
    return; /* best-effort logging only */

  /* Log the event, pointer values and meta-data only. Do not dereference
   * the value.str buffer contents to avoid introducing additional out-of-
   * bounds reads. */
  fprintf(f, "EVENT: %s\n", event);
  fprintf(f, "  algo_param=%p\n", (const void *)algo_param);
  if (NULL == algo_param)
  {
    fprintf(f, "  algo_param is NULL\n\n");
    fclose(f);
    return;
  }
  fprintf(f, "  value.str=%p\n", (const void *)algo_param->value.str);
  fprintf(f, "  value.len=%zu\n", (size_t)algo_param->value.len);
  fprintf(f, "  quoted=%d\n\n", (int)algo_param->quoted);

  fclose(f);
}

static enum MHD_DigestAuthAlgo3
get_rq_dauth_algo (const struct MHD_RqDAuthParam *const algo_param)
{
  probe_log_event(algo_param, "entry: get_rq_dauth_algo");

  if (NULL == algo_param->value.str)
  {
    probe_log_event(algo_param, "branch: value.str is NULL -> default MD5");
    return MHD_DIGEST_AUTH_ALGO3_MD5; /* Assume MD5 by default */
  }

  if (algo_param->quoted)
  {
    probe_log_event(algo_param, "branch: quoted == true");
    probe_log_event(algo_param, "compare: _MHD_MD5_TOKEN (quoted)");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN))
    {
      probe_log_event(algo_param, "match: MD5 (quoted)");
      return MHD_DIGEST_AUTH_ALGO3_MD5;
    }
    probe_log_event(algo_param, "compare: _MHD_SHA256_TOKEN (quoted)");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN))
    {
      probe_log_event(algo_param, "match: SHA256 (quoted)");
      return MHD_DIGEST_AUTH_ALGO3_SHA256;
    }
    probe_log_event(algo_param, "compare: _MHD_MD5_TOKEN _MHD_SESS_TOKEN (quoted)");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_MD5_TOKEN _MHD_SESS_TOKEN))
    {
      probe_log_event(algo_param, "match: SHA512_256 (quoted via MD5+SESS)");
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
    }
    probe_log_event(algo_param, "compare: _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN (quoted)");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      /* Algorithms below are not supported by MHD for authentication */
      probe_log_event(algo_param, "match: MD5_SESSION (quoted)");
      return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
    }
    probe_log_event(algo_param, "compare: _MHD_SHA256_TOKEN _MHD_SESS_TOKEN (quoted)");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA256_TOKEN
                                               _MHD_SESS_TOKEN))
    {
      probe_log_event(algo_param, "match: SHA256_SESSION (quoted)");
      return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
    }
    probe_log_event(algo_param, "compare: _MHD_SHA512_256_TOKEN (quoted)");
    if (MHD_str_equal_caseless_quoted_s_bin_n (algo_param->value.str,
                                               algo_param->value.len,
                                               _MHD_SHA512_256_TOKEN))
    {
      probe_log_event(algo_param, "match: SHA512_256_SESSION (quoted)");
      return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
    }

    /* No known algorithm has been detected */
    probe_log_event(algo_param, "no known algorithm detected (quoted) -> INVALID");
    return MHD_DIGEST_AUTH_ALGO3_INVALID;
  }

  /* The algorithm value is not quoted */
  probe_log_event(algo_param, "branch: quoted == false");
  probe_log_event(algo_param, "compare: _MHD_MD5_TOKEN (unquoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    probe_log_event(algo_param, "match: MD5 (unquoted)");
    return MHD_DIGEST_AUTH_ALGO3_MD5;
  }
  probe_log_event(algo_param, "compare: _MHD_SHA256_TOKEN (unquoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    probe_log_event(algo_param, "match: SHA256 (unquoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA256;
  }
  probe_log_event(algo_param, "compare: _MHD_SHA512_256_TOKEN (unquoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    probe_log_event(algo_param, "match: SHA512_256 (unquoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256;
  }

  /* Algorithms below are not supported by MHD for authentication */
  probe_log_event(algo_param, "compare: _MHD_MD5_TOKEN _MHD_SESS_TOKEN (unquoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_MD5_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    probe_log_event(algo_param, "match: MD5_SESSION (unquoted)");
    return MHD_DIGEST_AUTH_ALGO3_MD5_SESSION;
  }
  probe_log_event(algo_param, "compare: _MHD_SHA256_TOKEN _MHD_SESS_TOKEN (unquoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    probe_log_event(algo_param, "match: SHA256_SESSION (unquoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA256_SESSION;
  }
  probe_log_event(algo_param, "compare: _MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN (unquoted)");
  if (MHD_str_equal_caseless_s_bin_n_ (_MHD_SHA512_256_TOKEN _MHD_SESS_TOKEN,
                                       algo_param->value.str,
                                       algo_param->value.len))
  {
    probe_log_event(algo_param, "match: SHA512_256_SESSION (unquoted)");
    return MHD_DIGEST_AUTH_ALGO3_SHA512_256_SESSION;
  }

  /* No known algorithm has been detected */
  probe_log_event(algo_param, "no known algorithm detected (unquoted) -> INVALID");
  return MHD_DIGEST_AUTH_ALGO3_INVALID;
}
