#include <stdio.h>
#include <sys/stat.h>
#include <sys/types.h>

/* Probe helper: ensure log directory exists and append messages. */
static void
mhd_probe_ensure_dir (void)
{
  /* ignore errors; directory may already exist */
  (void) mkdir("/tmp/logs", 0777);
}

static void
mhd_probe_log_fmt (const char *fmt, ...)
{
  va_list ap;
  FILE *f;

  mhd_probe_ensure_dir ();
  f = fopen("/tmp/logs/1112.txt", "a");
  if (NULL == f)
    return;

  va_start (ap, fmt);
  (void) vfprintf (f, fmt, ap);
  va_end (ap);
  (void) fputc('\n', f);
  (void) fclose (f);
}

/* The target function with inserted probes. */
static enum MHD_Result
internal_add_connection (struct MHD_Daemon *daemon,
                         MHD_socket client_socket,
                         const struct sockaddr_storage *addr,
                         socklen_t addrlen,
                         bool external_add,
                         bool non_blck,
                         bool sk_spipe_supprs,
                         enum MHD_tristate sk_is_nonip)
{
  struct MHD_Connection *connection;

#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
  /* Direct add to master daemon could never happen. */
  mhd_assert (NULL == daemon->worker_pool);

  /* Probe: explicitly log that we reached the assertion site and record
   * the value of daemon->worker_pool and daemon pointer. This ensures
   * that even if mhd_assert is compiled out (no-op), we still capture
   * the state read that is reported as dead code in the violation
   * scenario. */
  mhd_probe_log_fmt ("[1112] internal_add_connection: at assertion site: daemon=%p, daemon->worker_pool=%p",
                     (void *) daemon,
                     (void *) daemon->worker_pool);
#endif

  if (MHD_D_IS_USING_SELECT_ (daemon) &&
      (! MHD_D_DOES_SCKT_FIT_FDSET_ (client_socket, daemon)) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("New connection socket descriptor (%d) is not less " \
                 "than FD_SETSIZE (%d).\n"),
              (int) client_socket,
              (int) MHD_D_GET_FD_SETSIZE_ (daemon));
#endif
    mhd_probe_log_fmt ("[1112] internal_add_connection: socket %d >= FD_SETSIZE, closing",
                       (int) client_socket);
    MHD_socket_close_chk_ (client_socket);
#if defined(ENFILE) && (ENFILE + 0 != 0)
    errno = ENFILE;
#endif
    return MHD_NO;
  }

  if (MHD_D_IS_USING_EPOLL_ (daemon) &&
      (! non_blck) )
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Epoll mode supports only non-blocking sockets\n"));
#endif
    mhd_probe_log_fmt ("[1112] internal_add_connection: epoll mode requires non-blocking socket; closing socket %d",
                       (int) client_socket);
    MHD_socket_close_chk_ (client_socket);
#if defined(EINVAL) && (EINVAL + 0 != 0)
    errno = EINVAL;
#endif
    return MHD_NO;
  }

  connection = new_connection_prepare_ (daemon,
                                        client_socket,
                                        addr, addrlen,
                                        external_add,
                                        non_blck,
                                        sk_spipe_supprs,
                                        sk_is_nonip);
  if (NULL == connection)
  {
    mhd_probe_log_fmt ("[1112] internal_add_connection: new_connection_prepare_ returned NULL for socket %d",
                       (int) client_socket);
    return MHD_NO;
  }

  if ((external_add) &&
      MHD_D_IS_THREAD_SAFE_ (daemon))
  {
    /* Connection is added externally and MHD is thread safe mode. */
    mhd_probe_log_fmt ("[1112] internal_add_connection: external_add && thread_safe - enqueuing connection %p",
                       (void *) connection);
    MHD_mutex_lock_chk_ (&daemon->new_connections_mutex);
    DLL_insert (daemon->new_connections_head,
                daemon->new_connections_tail,
                connection);
    daemon->have_new = true;
    MHD_mutex_unlock_chk_ (&daemon->new_connections_mutex);

    /* The rest of connection processing must be handled in
     * the daemon thread. */
    if ((MHD_ITC_IS_VALID_ (daemon->itc)) &&
        (! MHD_itc_activate_ (daemon->itc, "n")))
    {
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Failed to signal new connection via inter-thread " \
                   "communication channel.\n"));
#endif
      mhd_probe_log_fmt ("[1112] internal_add_connection: failed to signal ITC for connection %p",
                         (void *) connection);
    }
    return MHD_YES;
  }

  mhd_probe_log_fmt ("[1112] internal_add_connection: processing connection %p in-line",
                     (void *) connection);
  return new_connection_process_ (daemon, connection);
}
