static void internal_run_log(const char *fmt, ...)
{
  FILE *fp = fopen("/tmp/logs/1133.txt", "a");
  if (NULL == fp)
    return;
  va_list ap;
  va_start(ap, fmt);
  vfprintf(fp, fmt, ap);
  fprintf(fp, "\n");
  va_end(ap);
  fclose(fp);
}

static enum MHD_Result
internal_run_from_select (struct MHD_Daemon *daemon,
                          const fd_set *read_fd_set,
                          const fd_set *write_fd_set,
                          const fd_set *except_fd_set,
                          int fd_setsize)
{
  MHD_socket ds;
#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  struct MHD_UpgradeResponseHandle *urh;
  struct MHD_UpgradeResponseHandle *urhn;
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */

  /* Small probe at function entry */
  internal_run_log("internal_run_from_select: entry: daemon=%p, read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, fd_setsize=%d",
                   (void *) daemon, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, fd_setsize);

  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 != (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (! MHD_thread_handle_ID_is_valid_ID_ (daemon->tid)));
  mhd_assert ((0 == (daemon->options & MHD_USE_SELECT_INTERNALLY)) || \
              (MHD_thread_handle_ID_is_current_thread_ (daemon->tid)));

  mhd_assert (0 < fd_setsize);
  (void) fd_setsize; /* Mute compiler warning */
#ifndef HAS_FD_SETSIZE_OVERRIDABLE
  (void) fd_setsize; /* Mute compiler warning */
  mhd_assert (((int) FD_SETSIZE) <= fd_setsize);
  fd_setsize = FD_SETSIZE; /* Help compiler to optimise */
#endif /* ! HAS_FD_SETSIZE_OVERRIDABLE */

  /* Clear ITC to avoid spinning select */
  /* Do it before any other processing so new signals
     will trigger select again and will be processed */
  if (MHD_ITC_IS_VALID_ (daemon->itc))
  { /* Have ITC */
    bool need_to_clear_itc = true; /* ITC is always non-blocking, it is safe to clear even if ITC not activated */
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (MHD_itc_r_fd_ (daemon->itc),
                                         NULL, fd_setsize))
    {
      int itc_fd = MHD_itc_r_fd_ (daemon->itc);
      fd_set *itc_read_set = (fd_set *) _MHD_DROP_CONST (read_fd_set);
      int itc_is_set = FD_ISSET (itc_fd, itc_read_set); /* Skip clearing, if not needed */

      /* Log the cast-away-const usage and the result of FD_ISSET */
      internal_run_log("internal_run_from_select: ITC check: itc_fd=%d, read_fd_set_ptr=%p (casted to %p), FD_ISSET=%d",
                       itc_fd, (const void *) read_fd_set, (void *) itc_read_set, itc_is_set);

      need_to_clear_itc = itc_is_set;
    }
    if (need_to_clear_itc)
      MHD_itc_clear_ (daemon->itc);
  }

  /* Reset. New value will be set when connections are processed. */
  /* Note: no-op for thread-per-connection as it is always false in that mode. */
  daemon->data_already_pending = false;

  /* Process externally added connection if any */
  if (daemon->have_new)
    new_connections_list_process_ (daemon);

  /* select connection thread handling type */
  ds = daemon->listen_fd;
  if ( (MHD_INVALID_SOCKET != ds) &&
       (! daemon->was_quiesced) )
  {
    bool need_to_accept;
    if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (ds, NULL, fd_setsize))
    {
      fd_set *read_cast = (fd_set *) _MHD_DROP_CONST (read_fd_set);
      int accept_is_set = FD_ISSET (ds, read_cast);
      internal_run_log("internal_run_from_select: listen_fd check: listen_fd=%d, read_fd_set=%p (casted=%p), FD_ISSET=%d",
                       ds, (const void *) read_fd_set, (void *) read_cast, accept_is_set);
      need_to_accept = accept_is_set;
    }
    else                                       /* Cannot check whether new connection are pending */
      need_to_accept = daemon->listen_nonblk;  /* Try to accept if non-blocking */

    if (need_to_accept)
      (void) MHD_accept_connection (daemon);
  }

  if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
  {
    /* do not have a thread per connection, process all connections now */
    struct MHD_Connection *pos;
    for (pos = daemon->connections_tail; NULL != pos; pos = pos->prev)
    {
      MHD_socket cs;
      bool r_ready;
      bool w_ready;
      bool has_err;

      cs = pos->socket_fd;
      if (MHD_INVALID_SOCKET == cs)
        continue;

      if (MHD_SCKT_FD_FITS_FDSET_SETSIZE_ (cs, NULL, fd_setsize))
      {
        fd_set *r_set = (fd_set *) _MHD_DROP_CONST (read_fd_set);
        fd_set *w_set = (fd_set *) _MHD_DROP_CONST (write_fd_set);
        fd_set *e_set = (fd_set *) _MHD_DROP_CONST (except_fd_set);

        int r_is = FD_ISSET (cs, r_set);
        int w_is = FD_ISSET (cs, w_set);
        int e_is = (NULL != except_fd_set) ? FD_ISSET (cs, e_set) : 0;

        /* Log each FD_ISSET call and the cast that was performed */
        internal_run_log("internal_run_from_select: conn=%p cs=%d: read_fd_set=%p (casted=%p) FD_ISSET=%d",
                         (void *) pos, cs, (const void *) read_fd_set, (void *) r_set, r_is);
        internal_run_log("internal_run_from_select: conn=%p cs=%d: write_fd_set=%p (casted=%p) FD_ISSET=%d",
                         (void *) pos, cs, (const void *) write_fd_set, (void *) w_set, w_is);
        internal_run_log("internal_run_from_select: conn=%p cs=%d: except_fd_set=%p (casted=%p) FD_ISSET=%d",
                         (void *) pos, cs, (const void *) except_fd_set, (void *) e_set, e_is);

        r_ready = r_is;
        w_ready = w_is;
        has_err = (0 != e_is);
      }
      else
      { /* Cannot check the real readiness */
        r_ready = pos->sk_nonblck;
        w_ready = r_ready;
        has_err = false;
      }
      call_handlers (pos,
                     r_ready,
                     w_ready,
                     has_err);
    }
  }

#if defined(HTTPS_SUPPORT) && defined(UPGRADE_SUPPORT)
  /* handle upgraded HTTPS connections */
  for (urh = daemon->urh_tail; NULL != urh; urh = urhn)
  {
    urhn = urh->prev;
    /* update urh state based on select() output */
    /* Log that urh_from_fdset is being called with original const pointers */
    internal_run_log("internal_run_from_select: urh_from_fdset: urh=%p, read_fd_set=%p, write_fd_set=%p, except_fd_set=%p, fd_setsize=%d",
                     (void *) urh, (const void *) read_fd_set, (const void *) write_fd_set, (const void *) except_fd_set, fd_setsize);

    urh_from_fdset (urh,
                    read_fd_set,
                    write_fd_set,
                    except_fd_set,
                    fd_setsize);
    /* call generic forwarding function for passing data */
    process_urh (urh);
    /* Finished forwarding? */
    if ( (0 == urh->in_buffer_size) &&
         (0 == urh->out_buffer_size) &&
         (0 == urh->in_buffer_used) &&
         (0 == urh->out_buffer_used) )
    {
      MHD_connection_finish_forward_ (urh->connection);
      urh->clean_ready = true;
      /* Resuming will move connection to cleanup list. */
      MHD_resume_connection (urh->connection);
    }
  }
#endif /* HTTPS_SUPPORT && UPGRADE_SUPPORT */
  MHD_cleanup_connections (daemon);

  internal_run_log("internal_run_from_select: exit: daemon=%p returning MHD_YES", (void *) daemon);
  return MHD_YES;
}
