#include <stdio.h>

/* Probe helper: logs control flow and key state to /tmp/logs/493.txt */
static void mhd_log_reply_needed(const char *stage, struct MHD_Connection *const c, unsigned int rcode)
{
  FILE *f = fopen("/tmp/logs/493.txt", "a");
  if (NULL == f)
    return;

  /* Log stage, connection pointer and response code */
  (void) fprintf(f, "[is_reply_body_needed] %s: connection=%p, rcode=%u\n", stage, (void *) c, rcode);

  /* Attempt to log method if connection pointer is valid. This check is only for
   * safe logging and does not affect the original decision logic. */
  if (NULL != c)
  {
    (void) fprintf(f, "[is_reply_body_needed] %s: http_mthd=%d\n", stage, c->rq.http_mthd);
  }

  (void) fclose(f);
}

static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */

  /* Log entry state */
  mhd_log_reply_needed("entry", c, rcode);

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  if (199 >= rcode)
  {
    mhd_log_reply_needed("rcode<=199 -> RP_BODY_NONE", c, rcode);
    return RP_BODY_NONE;
  }

  if (MHD_HTTP_NO_CONTENT == rcode)
  {
    mhd_log_reply_needed("MHD_HTTP_NO_CONTENT -> RP_BODY_NONE", c, rcode);
    return RP_BODY_NONE;
  }

#if 0
  /* This check is not needed as upgrade handler is used only with code 101 */
#ifdef UPGRADE_SUPPORT
  if (NULL != rp.response->upgrade_handler)
    return RP_BODY_NONE;
#endif /* UPGRADE_SUPPORT */
#endif

#if 0
  /* CONNECT is not supported by MHD */
  /* Successful responses for connect requests are filtered by
   * MHD_queue_response() */
  if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) &&
       (2 == rcode / 100) )
    return false; /* Actually pass-through CONNECT is not supported by MHD */
#endif

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
  {
    mhd_log_reply_needed("HEAD method -> RP_BODY_HEADERS_ONLY", c, rcode);
    return RP_BODY_HEADERS_ONLY;
  }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
  {
    mhd_log_reply_needed("MHD_HTTP_NOT_MODIFIED -> RP_BODY_HEADERS_ONLY", c, rcode);
    return RP_BODY_HEADERS_ONLY;
  }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  mhd_log_reply_needed("default -> RP_BODY_SEND", c, rcode);
  return RP_BODY_SEND;
}
