#include <stdio.h>

/* Probe helper: append a concise log entry to /tmp/logs/493.txt.
   Keep the helper minimal to avoid changing program logic. */
static void probe_log(const char *stage,
                      struct MHD_Connection *const c,
                      unsigned int rcode,
                      unsigned int http_mthd,
                      const char *decision)
{
  FILE *f = fopen("/tmp/logs/493.txt", "a");
  if (NULL == f)
    return;

  fprintf(f, "%s: conn=%p rcode=%u http_mthd=%u decision=%s\n",
          stage, (void *) c, rcode, http_mthd, (decision != NULL) ? decision : "(pending)");
  fclose(f);
}

static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */

  /* Capture entry state. Avoid dereferencing c when NULL. */
  unsigned int http_mthd_val = 0U;
  if (NULL != c)
    http_mthd_val = (unsigned int) c->rq.http_mthd;
  probe_log("ENTRY", c, rcode, http_mthd_val, NULL);

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  if (199 >= rcode)
  {
    probe_log("BRANCH", c, rcode, http_mthd_val, "RP_BODY_NONE (rcode<=199)");
    return RP_BODY_NONE;
  }

  if (MHD_HTTP_NO_CONTENT == rcode)
  {
    probe_log("BRANCH", c, rcode, http_mthd_val, "RP_BODY_NONE (204)");
    return RP_BODY_NONE;
  }

#if 0
  /* This check is not needed as upgrade handler is used only with code 101 */
#ifdef UPGRADE_SUPPORT
  if (NULL != rp.response->upgrade_handler)
    return RP_BODY_NONE;
#endif /* UPGRADE_SUPPORT */
#endif

#if 0
  /* CONNECT is not supported by MHD */
  /* Successful responses for connect requests are filtered by
   * MHD_queue_response() */
  if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) &&
       (2 == rcode / 100) )
    return false; /* Actually pass-through CONNECT is not supported by MHD */
#endif

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
  {
    probe_log("BRANCH", c, rcode, (unsigned int) c->rq.http_mthd, "RP_BODY_HEADERS_ONLY (HEAD)");
    return RP_BODY_HEADERS_ONLY;
  }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
  {
    probe_log("BRANCH", c, rcode, (unsigned int) c->rq.http_mthd, "RP_BODY_HEADERS_ONLY (304)");
    return RP_BODY_HEADERS_ONLY;
  }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  probe_log("BRANCH", c, rcode, (unsigned int) c->rq.http_mthd, "RP_BODY_SEND (default)");
  return RP_BODY_SEND;
}
