#include <stdio.h>
#include <time.h>

/* Probe helper: log control flow and relevant state to /tmp/logs/493.txt */
static void
probe_log_decision (const struct MHD_Connection *conn,
                    unsigned int rcode,
                    const char *decision)
{
  FILE *f = fopen("/tmp/logs/493.txt", "a");
  if (NULL == f)
    return;

  time_t t = time(NULL);
  /* Print timestamp, decision label, connection pointer and key state. */
  fprintf(f, "[%ld] decision=%s addr=%p rcode=%u http_mthd=%d\n",
          (long)t,
          (decision != NULL) ? decision : "(null)",
          (const void *) conn,
          (unsigned) rcode,
          (int) conn->rq.http_mthd);
  fclose(f);
}

static enum replyBodyUse
is_reply_body_needed (struct MHD_Connection *connection,
                      unsigned int rcode)
{
  struct MHD_Connection *const c = connection; /**< a short alias */

  mhd_assert (100 <= rcode);
  mhd_assert (999 >= rcode);

  /* Log initial state before making decisions */
  probe_log_decision(c, rcode, "enter_is_reply_body_needed");

  if (199 >= rcode)
  {
    probe_log_decision(c, rcode, "RP_BODY_NONE:rcode<=199");
    return RP_BODY_NONE;
  }

  if (MHD_HTTP_NO_CONTENT == rcode)
  {
    probe_log_decision(c, rcode, "RP_BODY_NONE:NO_CONTENT");
    return RP_BODY_NONE;
  }

#if 0
  /* This check is not needed as upgrade handler is used only with code 101 */
#ifdef UPGRADE_SUPPORT
  if (NULL != rp.response->upgrade_handler)
    return RP_BODY_NONE;
#endif /* UPGRADE_SUPPORT */
#endif

#if 0
  /* CONNECT is not supported by MHD */
  /* Successful responses for connect requests are filtered by
   * MHD_queue_response() */
  if ( (MHD_HTTP_MTHD_CONNECT == c->rq.http_mthd) &&
       (2 == rcode / 100) )
    return false; /* Actually pass-through CONNECT is not supported by MHD */
#endif

  /* Reply body headers could be used.
   * Check whether reply body itself must be used. */

  if (MHD_HTTP_MTHD_HEAD == c->rq.http_mthd)
  {
    probe_log_decision(c, rcode, "RP_BODY_HEADERS_ONLY:HEAD");
    return RP_BODY_HEADERS_ONLY;
  }

  if (MHD_HTTP_NOT_MODIFIED == rcode)
  {
    probe_log_decision(c, rcode, "RP_BODY_HEADERS_ONLY:NOT_MODIFIED");
    return RP_BODY_HEADERS_ONLY;
  }

  /* Reply body must be sent. The body may have zero length, but body size
   * must be indicated by headers ('Content-Length:' or
   * 'Transfer-Encoding: chunked'). */
  probe_log_decision(c, rcode, "RP_BODY_SEND");
  return RP_BODY_SEND;
}
