#include <stdio.h>
#include <time.h>

static void log_keepalive_state(const char *stage, const struct MHD_Connection *c)
{
  FILE *f = fopen("/tmp/logs/503.txt", "a");
  if (NULL == f)
    return;

  /* Timestamp for easier sequencing */
  {
    time_t t = time(NULL);
    struct tm tm_buf;
    if (NULL != localtime_r(&t, &tm_buf))
    {
      char timestr[64];
      if (0 < strftime(timestr, sizeof(timestr), "%Y-%m-%d %H:%M:%S", &tm_buf))
        fprintf(f, "%s ", timestr);
    }
  }

  fprintf(f, "keepalive_possible: %s\n", stage);
  if (NULL == c)
  {
    fprintf(f, "  connection: NULL\n\n");
    fclose(f);
    return;
  }

  /* Print main connection-level fields used in decision logic. */
  fprintf(f, "  connection=%p\n", (void *) c);
  fprintf(f, "  keepalive=%u\n", (unsigned int) c->keepalive);
  fprintf(f, "  read_closed=%u\n", (unsigned int) c->read_closed);
  fprintf(f, "  discard_request=%u\n", (unsigned int) c->discard_request);
  fprintf(f, "  stop_with_error=%u\n", (unsigned int) c->stop_with_error);
  fprintf(f, "  rq.http_ver=%u\n", (unsigned int) c->rq.http_ver);

  /* Response pointer and selected response fields */
  if (NULL != c->rp.response)
  {
    const struct MHD_Response *r = c->rp.response;
    fprintf(f, "  rp.response=%p\n", (void *) r);
    fprintf(f, "  response->flags=0x%08x\n", (unsigned int) r->flags);
    fprintf(f, "  response->flags_auto=0x%08x\n", (unsigned int) r->flags_auto);
    fprintf(f, "  response->upgrade_handler=%p\n", (void *) r->upgrade_handler);
  }
  else
  {
    fprintf(f, "  rp.response=NULL\n");
  }

  fprintf(f, "\n");
  fclose(f);
}

static enum MHD_ConnKeepAlive
keepalive_possible (struct MHD_Connection *connection)
{
  struct MHD_Connection *const c = connection; /**< a short alias */
  struct MHD_Response *const r = c->rp.response;  /**< a short alias */

  /* Log entry state */
  log_keepalive_state("entry", c);

  mhd_assert (NULL != r);
  if (MHD_CONN_MUST_CLOSE == c->keepalive)
  {
    log_keepalive_state("must_close_due_to_keepalive_flag", c);
    return MHD_CONN_MUST_CLOSE;
  }

#ifdef UPGRADE_SUPPORT
  /* TODO: Move below the next check when MHD stops closing connections
   * when response is queued in first callback */
  if (NULL != r->upgrade_handler)
  {
    /* No "close" token is enforced by 'add_response_header_connection()' */
    mhd_assert (0 == (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE));
    /* Valid HTTP version is enforced by 'MHD_queue_response()' */
    mhd_assert (MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver));
    mhd_assert (! c->stop_with_error);

    log_keepalive_state("must_upgrade_due_to_upgrade_handler", c);
    return MHD_CONN_MUST_UPGRADE;
  }
#endif /* UPGRADE_SUPPORT */

  mhd_assert ( (! c->stop_with_error) || (c->discard_request));
  if ((c->read_closed) || (c->discard_request))
  {
    log_keepalive_state("must_close_due_to_read_closed_or_discard", c);
    return MHD_CONN_MUST_CLOSE;
  }

  if (0 != (r->flags & MHD_RF_HTTP_1_0_COMPATIBLE_STRICT))
  {
    log_keepalive_state("must_close_due_to_1_0_compatible_strict", c);
    return MHD_CONN_MUST_CLOSE;
  }
  if (0 != (r->flags_auto & MHD_RAF_HAS_CONNECTION_CLOSE))
  {
    log_keepalive_state("must_close_due_to_connection_close_header", c);
    return MHD_CONN_MUST_CLOSE;
  }

  if (! MHD_IS_HTTP_VER_SUPPORTED (c->rq.http_ver))
  {
    log_keepalive_state("must_close_due_to_http_version_not_supported", c);
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_lookup_header_s_token_ci (c,
                                    MHD_HTTP_HEADER_CONNECTION,
                                    "close"))
  {
    log_keepalive_state("must_close_due_to_connection_header_close", c);
    return MHD_CONN_MUST_CLOSE;
  }

  if ((MHD_HTTP_VER_1_0 == connection->rq.http_ver) ||
      (0 != (connection->rp.response->flags & MHD_RF_HTTP_1_0_SERVER)))
  {
    if (MHD_lookup_header_s_token_ci (connection,
                                      MHD_HTTP_HEADER_CONNECTION,
                                      "Keep-Alive"))
    {
      log_keepalive_state("use_keepalive_http_1_0_with_header", c);
      return MHD_CONN_USE_KEEPALIVE;
    }

    log_keepalive_state("must_close_http_1_0_no_keepalive_header", c);
    return MHD_CONN_MUST_CLOSE;
  }

  if (MHD_IS_HTTP_VER_1_1_COMPAT (c->rq.http_ver))
  {
    log_keepalive_state("use_keepalive_http_1_1", c);
    return MHD_CONN_USE_KEEPALIVE;
  }

  log_keepalive_state("must_close_default", c);
  return MHD_CONN_MUST_CLOSE;
}
