static void
mhd_log_probe_ (const char *stage,
                 struct MHD_Daemon *daemon,
                 struct MHD_Connection *connection,
                 int eno)
{
  /* Lightweight logging helper that appends a single-line record to
   * /tmp/logs/1172.txt. Implemented via system() to avoid requiring
   * inclusion of stdio.h or other headers in this snippet. We declare
   * the used functions as extern to keep this snippet self-contained. */
  extern int sprintf (char *, const char *, ...);
  extern int system (const char *);

  char line[512];
  char esc[1024];
  char cmd[2048];
  char *p = esc;
  const char *s;

  /* build a descriptive line containing pointers and key ints */
  (void) sprintf (line,
                  "stage=%s daemon=%p connection=%p socket_fd=%d pool=%p eno=%d\n",
                  stage,
                  (void *) daemon,
                  (void *) connection,
                  (NULL != connection) ? connection->socket_fd : -1,
                  (NULL != connection) ? (void *) connection->pool : (void *)0,
                  eno);

  /* escape single quotes for safe embedding into a single-quoted shell printf */
  for (s = line; *s != '\0' && (size_t) (p - esc) < sizeof (esc) - 6; ++s)
  {
    if ('\'' == *s)
    {
      /* close-quote, embed single-quote, reopen single-quote: '\'' -> '\"'\''\"' (shell safe) */
      /* Simpler portable escape: replace '\'' by '\\'\'' so resulting single-quoted printf will work */
      *p++ = '\\';
      *p++ = '\'';
    }
    else
      *p++ = *s;
  }
  *p = '\0';

  /* Ensure log directory exists and append the escaped line */
  (void) sprintf (cmd,
                  "mkdir -p /tmp/logs 2>/dev/null && printf '%s' >> /tmp/logs/1172.txt 2>/dev/null",
                  esc);
  /* fire-and-forget; ignore return value */
  (void) system (cmd);
}

static enum MHD_Result
new_connection_process_ (struct MHD_Daemon *daemon,
                         struct MHD_Connection *connection)
{
  int eno = 0;

  mhd_assert (connection->daemon == daemon);

#ifdef MHD_USE_THREADS
  /* Function manipulate connection and timeout DL-lists,
   * must be called only within daemon thread. */
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Log entry into the function */
  mhd_log_probe_("ENTRY", daemon, connection, eno);

  /* Allocate memory pool in the processing thread so
   * intensively used memory area is allocated in "good"
   * (for the thread) memory region. It is important with
   * NUMA and/or complex cache hierarchy. */
  connection->pool = MHD_pool_create (daemon->pool_size);
  if (NULL == connection->pool)
  { /* 'pool' creation failed */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Error allocating memory: %s\n"),
              MHD_strerror_ (errno));
#endif
#if defined(ENOMEM) && (ENOMEM + 0 != 0)
    eno = ENOMEM;
#endif
    (void) 0; /* Mute possible compiler warning */

    /* Log pool allocation failure */
    mhd_log_probe_("POOL_CREATE_FAILED", daemon, connection, eno);
  }
  else
  { /* 'pool' creation succeed */
    mhd_log_probe_("POOL_CREATED", daemon, connection, eno);

    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
    /* Firm check under lock. */
    if (daemon->connections >= daemon->connection_limit)
    { /* Connections limit */
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Server reached connection limit. "
                   "Closing inbound connection.\n"));
#endif
#if defined(ENFILE) && (ENFILE + 0 != 0)
      eno = ENFILE;
#endif
      (void) 0; /* Mute possible compiler warning */

      /* Log connection limit reached */
      mhd_log_probe_("CONNECTION_LIMIT", daemon, connection, eno);
    }
    else
    { /* Have space for new connection */
      daemon->connections++;
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_insert (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);

      MHD_connection_set_initial_state_ (connection);

      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_STARTED);
#ifdef MHD_USE_THREADS
      if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        mhd_assert (! MHD_D_IS_USING_EPOLL_ (daemon));
        if (! MHD_create_named_thread_ (&connection->tid,
                                        "MHD-connection",
                                        daemon->thread_stack_size,
                                        &thread_main_handle_connection,
                                        connection))
        {
          eno = errno;
#ifdef HAVE_MESSAGES
#ifdef EAGAIN
          if (EAGAIN == eno)
            MHD_DLOG (daemon,
                      _ ("Failed to create a new thread because it would "
                         "have exceeded the system limit on the number of "
                         "threads or no system resources available.\n"));
          else
#endif /* EAGAIN */
          MHD_DLOG (daemon,
                    _ ("Failed to create a thread: %s\n"),
                    MHD_strerror_ (eno));
#endif /* HAVE_MESSAGES */

          /* Log thread creation failure */
          mhd_log_probe_("THREAD_CREATE_FAILED", daemon, connection, eno);
        }
        else               /* New thread has been created successfully */
        {
          /* Log thread creation success and imminent return */
          mhd_log_probe_("THREAD_CREATED_RETURN_YES", daemon, connection, 0);
          return MHD_YES;  /* *** Function success exit point *** */
        }
      }
      else
#else  /* ! MHD_USE_THREADS */
      if (1)
#endif /* ! MHD_USE_THREADS */
      { /* No 'thread-per-connection' */
        /* Log that we are entering the non-thread-per-connection path.
         * In the configuration where MHD_USE_THREADS is NOT defined, the
         * preprocessor yields "if (1) { ... }" and this constant
         * conditional is executed. We log this to evidence the scenario. */
        mhd_log_probe_("ENTER_NO_THREAD_PER_CONN_if_1", daemon, connection, eno);

#ifdef MHD_USE_THREADS
        connection->tid = daemon->tid;
#endif /* MHD_USE_THREADS */
#ifdef EPOLL_SUPPORT
        if (MHD_D_IS_USING_EPOLL_ (daemon))
        {
          if (0 == (daemon->options & MHD_USE_TURBO))
          {
            struct epoll_event event;

            event.events = EPOLLIN | EPOLLOUT | EPOLLPRI | EPOLLET | EPOLLRDHUP;
            event.data.ptr = connection;
            if (0 != epoll_ctl (daemon->epoll_fd,
                                EPOLL_CTL_ADD,
                                connection->socket_fd,
                                &event))
            {
              eno = errno;
#ifdef HAVE_MESSAGES
              MHD_DLOG (daemon,
                        _ ("Call to epoll_ctl failed: %s\n"),
                        MHD_socket_last_strerr_ ());
#endif

              /* Log epoll_ctl failure */
              mhd_log_probe_("EPOLL_CTL_FAILED", daemon, connection, eno);
            }
            else
            { /* 'socket_fd' has been added to 'epool' */
              connection->epoll_state |= MHD_EPOLL_STATE_IN_EPOLL_SET;

              /* Log success and return */
              mhd_log_probe_("EPOLL_ADDED_RETURN_YES", daemon, connection, 0);
              return MHD_YES;  /* *** Function success exit point *** */
            }
          }
          else
          {
            connection->epoll_state |= MHD_EPOLL_STATE_READ_READY
                                       | MHD_EPOLL_STATE_WRITE_READY
                                       | MHD_EPOLL_STATE_IN_EREADY_EDLL;
            EDLL_insert (daemon->eready_head,
                         daemon->eready_tail,
                         connection);

            /* Log turbo mode path and return */
            mhd_log_probe_("EPOLL_TURBO_INSERT_RETURN_YES", daemon, connection, 0);
            return MHD_YES;  /* *** Function success exit point *** */
          }
        }
        else /* No 'epoll' */
#endif /* EPOLL_SUPPORT */
        {
          /* Log the plain no-epoll, no-thread-per-connection success return */
          mhd_log_probe_("NO_THREAD_NO_EPOLL_RETURN_YES", daemon, connection, 0);
          return MHD_YES;    /* *** Function success exit point *** */
        }
      }

      /* ** Below is a cleanup path ** */
      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_CLOSED);

      /* Log that cleanup path is taken */
      mhd_log_probe_("CLEANUP_PATH", daemon, connection, eno);

      MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_remove (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      DLL_remove (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      daemon->connections--;
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    }
    MHD_pool_destroy (connection->pool);
  }
  /* Free resources allocated before the call of this functions */
#ifdef HTTPS_SUPPORT
  if (NULL != connection->tls_session)
    gnutls_deinit (connection->tls_session);
#endif /* HTTPS_SUPPORT */
  MHD_ip_limit_del (daemon,
                    connection->addr,
                    connection->addr_len);
  if (NULL != connection->addr)
    free (connection->addr);
  MHD_socket_close_chk_ (connection->socket_fd);
  free (connection);
  if (0 != eno)
    errno = eno;
#ifdef EINVAL
  else
    errno = EINVAL;
#endif /* EINVAL */

  /* Log final failure return together with errno */
  mhd_log_probe_("RETURN_NO", daemon, connection, eno);
  return MHD_NO;  /* *** Function failure exit point *** */
}
