#include <stdio.h>
#include <stdint.h>
#include <time.h>
#include <unistd.h>
#include <sys/types.h>
#include <sys/stat.h>
#include <string.h>

/* Probe helpers: write diagnostics to /tmp/logs/1173.txt to help reproduce
 * and capture the format-string pointer and context when MHD_DLOG is invoked.
 * These helpers are designed to avoid changing the original logic. */
static void
probe_log_event (const char *label, const char *fmt_arg, int eno, const char *context)
{
  FILE *f = NULL;
  char tbuf[64];
  time_t now = time (NULL);
  struct stat st;

  /* Ensure directory exists (ignore errors if it does not). */
  if (0 != stat("/tmp/logs", &st))
  {
    (void) mkdir("/tmp/logs", 0777);
  }

  f = fopen("/tmp/logs/1173.txt", "a");
  if (NULL == f)
    return;

  if (NULL != ctime_r (&now, tbuf))
  {
    size_t len = strlen (tbuf);
    if (len > 0 && tbuf[len - 1] == '\n')
      tbuf[len - 1] = '\0';
  }
  else
    tbuf[0] = '\0';

  /* Write a concise single-line record: timestamp, pid, label, context,
   * pointer value and a short prefix of the string (safe printing). */
  (void) fprintf (f, "%s pid=%ld label=%s context=%s fmt_ptr=%p eno=%d\n",
                  tbuf, (long) getpid (), label, context,
                  (const void *) fmt_arg, eno);

  /* Print up to first 256 characters of the string (escaped newline). */
  if (NULL != fmt_arg)
  {
    char buf[257];
    size_t i;
    for (i = 0; i < 256 && fmt_arg[i] != '\0'; ++i)
    {
      char c = fmt_arg[i];
      if (c == '\n' || c == '\r')
        buf[i] = ' ';
      else
        buf[i] = c;
    }
    buf[i] = '\0';
    (void) fprintf (f, "  fmt_preview=\"%s\"\n", buf);
  }

  (void) fflush (f);
  (void) fclose (f);
}

static enum MHD_Result
new_connection_process_ (struct MHD_Daemon *daemon,
                         struct MHD_Connection *connection)
{
  int eno = 0;

  mhd_assert (connection->daemon == daemon);

#ifdef MHD_USE_THREADS
  /* Function manipulate connection and timeout DL-lists,
   * must be called only within daemon thread. */
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Allocate memory pool in the processing thread so
   * intensively used memory area is allocated in "good"
   * (for the thread) memory region. It is important with
   * NUMA and/or complex cache hierarchy. */
  connection->pool = MHD_pool_create (daemon->pool_size);
  if (NULL == connection->pool)
  { /* 'pool' creation failed */
#ifdef HAVE_MESSAGES
    /* Probe: capture the format pointer produced by _() and current errno. */
    const char *probe_fmt_pool = _ ("Error allocating memory: %s\n");
    probe_log_event ("MHD_DLOG_before_pool_fail", probe_fmt_pool, errno,
                     "pool_create_failed");

    MHD_DLOG (daemon,
              probe_fmt_pool,
              MHD_strerror_ (errno));
#endif
#if defined(ENOMEM) && (ENOMEM + 0 != 0)
    eno = ENOMEM;
#endif
    (void) 0; /* Mute possible compiler warning */
  }
  else
  { /* 'pool' creation succeed */
    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
    /* Firm check under lock. */
    if (daemon->connections >= daemon->connection_limit)
    { /* Connections limit */
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#ifdef HAVE_MESSAGES
      const char *probe_fmt_connlim = _ ("Server reached connection limit. "
                                         "Closing inbound connection.\n");
      probe_log_event ("MHD_DLOG_before_conn_limit", probe_fmt_connlim,
                       eno, "connection_limit");

      MHD_DLOG (daemon,
                probe_fmt_connlim);
#endif
#if defined(ENFILE) && (ENFILE + 0 != 0)
      eno = ENFILE;
#endif
      (void) 0; /* Mute possible compiler warning */
    }
    else
    { /* Have space for new connection */
      daemon->connections++;
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_insert (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);

      MHD_connection_set_initial_state_ (connection);

      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_STARTED);
#ifdef MHD_USE_THREADS
      if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        mhd_assert (! MHD_D_IS_USING_EPOLL_ (daemon));
        if (! MHD_create_named_thread_ (&connection->tid,
                                        "MHD-connection",
                                        daemon->thread_stack_size,
                                        &thread_main_handle_connection,
                                        connection))
        {
          eno = errno;
#ifdef HAVE_MESSAGES
          /* EAGAIN branch message specific probe */
#ifdef EAGAIN
          if (EAGAIN == eno)
          {
            const char *probe_fmt_thread_eagain = _ ("Failed to create a new thread because it would "
                                                     "have exceeded the system limit on the number of "
                                                     "threads or no system resources available.\n");
            probe_log_event ("MHD_DLOG_before_thread_eagain", probe_fmt_thread_eagain,
                             eno, "thread_create_eagain");

            MHD_DLOG (daemon,
                      probe_fmt_thread_eagain);
          }
          else
#endif /* EAGAIN */
          {
            const char *probe_fmt_thread_fail = _ ("Failed to create a thread: %s\n");
            probe_log_event ("MHD_DLOG_before_thread_fail", probe_fmt_thread_fail,
                             eno, "thread_create_fail");

            MHD_DLOG (daemon,
                      probe_fmt_thread_fail,
                      MHD_strerror_ (eno));
          }
#endif /* HAVE_MESSAGES */
        }
        else               /* New thread has been created successfully */
          return MHD_YES;  /* *** Function success exit point *** */
      }
      else
#else  /* ! MHD_USE_THREADS */
      if (1)
#endif /* ! MHD_USE_THREADS */
      { /* No 'thread-per-connection' */
#ifdef MHD_USE_THREADS
        connection->tid = daemon->tid;
#endif /* MHD_USE_THREADS */
#ifdef EPOLL_SUPPORT
        if (MHD_D_IS_USING_EPOLL_ (daemon))
        {
          if (0 == (daemon->options & MHD_USE_TURBO))
          {
            struct epoll_event event;

            event.events = EPOLLIN | EPOLLOUT | EPOLLPRI | EPOLLET | EPOLLRDHUP;
            event.data.ptr = connection;
            if (0 != epoll_ctl (daemon->epoll_fd,
                                EPOLL_CTL_ADD,
                                connection->socket_fd,
                                &event))
            {
              eno = errno;
#ifdef HAVE_MESSAGES
              const char *probe_fmt_epoll = _ ("Call to epoll_ctl failed: %s\n");
              probe_log_event ("MHD_DLOG_before_epoll_ctl_fail", probe_fmt_epoll,
                               eno, "epoll_ctl_failed");

              MHD_DLOG (daemon,
                        probe_fmt_epoll,
                        MHD_socket_last_strerr_ ());
#endif
            }
            else
            { /* 'socket_fd' has been added to 'epool' */
              connection->epoll_state |= MHD_EPOLL_STATE_IN_EPOLL_SET;

              return MHD_YES;  /* *** Function success exit point *** */
            }
          }
          else
          {
            connection->epoll_state |= MHD_EPOLL_STATE_READ_READY
                                       | MHD_EPOLL_STATE_WRITE_READY
                                       | MHD_EPOLL_STATE_IN_EREADY_EDLL;
            EDLL_insert (daemon->eready_head,
                         daemon->eready_tail,
                         connection);

            return MHD_YES;  /* *** Function success exit point *** */
          }
        }
        else /* No 'epoll' */
#endif /* EPOLL_SUPPORT */
        return MHD_YES;    /* *** Function success exit point *** */
      }

      /* ** Below is a cleanup path ** */
      if (NULL != daemon->notify_connection)
        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_CLOSED);
      MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_remove (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      DLL_remove (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      daemon->connections--;
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    }
    MHD_pool_destroy (connection->pool);
  }
  /* Free resources allocated before the call of this functions */
#ifdef HTTPS_SUPPORT
  if (NULL != connection->tls_session)
    gnutls_deinit (connection->tls_session);
#endif /* HTTPS_SUPPORT */
  MHD_ip_limit_del (daemon,
                    connection->addr,
                    connection->addr_len);
  if (NULL != connection->addr)
    free (connection->addr);
  MHD_socket_close_chk_ (connection->socket_fd);
  free (connection);
  if (0 != eno)
    errno = eno;
#ifdef EINVAL
  else
    errno = EINVAL;
#endif /* EINVAL */
  return MHD_NO;  /* *** Function failure exit point *** */
}
