#include <stdio.h>
#include <stdarg.h>
#include <errno.h>
#include <time.h>

/* Probe logging helpers. These are intentionally minimal and preserve errno. */
static void
mhd_probe_log (const char *fmt, ...)
{
  int saved_errno = errno;
  FILE *f = fopen ("/tmp/logs/1173.txt", "a");
  if (NULL != f)
    {
      time_t t = time (NULL);
      (void) fprintf (f, "[%ld] ", (long) t);
      va_list ap;
      va_start (ap, fmt);
      (void) vfprintf (f, fmt, ap);
      va_end (ap);
      (void) fprintf (f, "\n");
      (void) fclose (f);
    }
  errno = saved_errno;
}

static enum MHD_Result
new_connection_process_ (struct MHD_Daemon *daemon,
                         struct MHD_Connection *connection)
{
  int eno = 0;

  /* Log entry and relevant state for reproducing pointer/const scenario. */
  mhd_probe_log ("ENTER new_connection_process_: daemon=%p, connection=%p, socket_context_addr=%p, notify_connection=%p, notify_connection_cls=%p, socket_fd=%d",
                 (void *) daemon,
                 (void *) connection,
                 (void *) &connection->socket_context,
                 (void *) (daemon ? daemon->notify_connection : NULL),
                 (void *) (daemon ? daemon->notify_connection_cls : NULL),
                 (int) connection->socket_fd);

  mhd_assert (connection->daemon == daemon);

#ifdef MHD_USE_THREADS
  /* Function manipulate connection and timeout DL-lists,
   * must be called only within daemon thread. */
  mhd_assert ( (! MHD_D_IS_USING_THREADS_ (daemon)) || \
               MHD_thread_handle_ID_is_current_thread_ (daemon->tid) );
  mhd_assert (NULL == daemon->worker_pool);
#endif /* MHD_USE_THREADS */

  /* Allocate memory pool in the processing thread so
   * intensively used memory area is allocated in "good"
   * (for the thread) memory region. It is important with
   * NUMA and/or complex cache hierarchy. */
  connection->pool = MHD_pool_create (daemon->pool_size);
  if (NULL == connection->pool)
  { /* 'pool' creation failed */
#ifdef HAVE_MESSAGES
    MHD_DLOG (daemon,
              _ ("Error allocating memory: %s\n"),
              MHD_strerror_ (errno));
#endif
#if defined(ENOMEM) && (ENOMEM + 0 != 0)
    eno = ENOMEM;
#endif
    (void) 0; /* Mute possible compiler warning */

    mhd_probe_log ("POOL_CREATION_FAILED: connection=%p, errno=%d, eno=%d",
                   (void *) connection, errno, eno);
  }
  else
  { /* 'pool' creation succeed */
    mhd_probe_log ("POOL_CREATED: connection=%p, pool=%p",
                   (void *) connection, (void *) connection->pool);

    MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
    /* Firm check under lock. */
    if (daemon->connections >= daemon->connection_limit)
    { /* Connections limit */
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
#ifdef HAVE_MESSAGES
      MHD_DLOG (daemon,
                _ ("Server reached connection limit. "
                   "Closing inbound connection.\n"));
#endif
#if defined(ENFILE) && (ENFILE + 0 != 0)
      eno = ENFILE;
#endif
      (void) 0; /* Mute possible compiler warning */

      mhd_probe_log ("CONNECTION_LIMIT_REACHED: connections=%u, connection_limit=%u, connection=%p",
                     (unsigned) daemon->connections,
                     (unsigned) daemon->connection_limit,
                     (void *) connection);
    }
    else
    { /* Have space for new connection */
      daemon->connections++;
      DLL_insert (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_insert (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);

      MHD_connection_set_initial_state_ (connection);

      if (NULL != daemon->notify_connection)
      {
        /* Log the parameters passed to the callback to allow reproducing
         * the scenario where a non-const pointer is used where a const one
         * would suffice. */
        mhd_probe_log ("CALL_NOTIFY_STARTED: notify_fn=%p, notify_cls=%p, passed_socket_ctx_addr=%p, connection=%p, event=%d",
                       (void *) daemon->notify_connection,
                       (void *) daemon->notify_connection_cls,
                       (void *) &connection->socket_context,
                       (void *) connection,
                       MHD_CONNECTION_NOTIFY_STARTED);

        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_STARTED);

        mhd_probe_log ("RETURNED_FROM_NOTIFY_STARTED: notify_fn=%p, connection=%p",
                       (void *) daemon->notify_connection,
                       (void *) connection);
      }
#ifdef MHD_USE_THREADS
      if (MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        mhd_assert (! MHD_D_IS_USING_EPOLL_ (daemon));
        if (! MHD_create_named_thread_ (&connection->tid,
                                        "MHD-connection",
                                        daemon->thread_stack_size,
                                        &thread_main_handle_connection,
                                        connection))
        {
          eno = errno;
#ifdef HAVE_MESSAGES
#ifdef EAGAIN
          if (EAGAIN == eno)
            MHD_DLOG (daemon,
                      _ ("Failed to create a new thread because it would "
                         "have exceeded the system limit on the number of "
                         "threads or no system resources available.\n"));
          else
#endif /* EAGAIN */
          MHD_DLOG (daemon,
                    _ ("Failed to create a thread: %s\n"),
                    MHD_strerror_ (eno));
#endif /* HAVE_MESSAGES */

          mhd_probe_log ("THREAD_CREATE_FAILED: connection=%p, errno=%d, eno=%d",
                         (void *) connection, errno, eno);
        }
        else               /* New thread has been created successfully */
        {
          mhd_probe_log ("THREAD_CREATED: connection=%p, tid=%lu -> returning MHD_YES",
                         (void *) connection,
                         (unsigned long) connection->tid);
          return MHD_YES;  /* *** Function success exit point *** */
        }
      }
      else
#else  /* ! MHD_USE_THREADS */
      if (1)
#endif /* ! MHD_USE_THREADS */
      { /* No 'thread-per-connection' */
#ifdef MHD_USE_THREADS
        connection->tid = daemon->tid;
#endif /* MHD_USE_THREADS */
#ifdef EPOLL_SUPPORT
        if (MHD_D_IS_USING_EPOLL_ (daemon))
        {
          if (0 == (daemon->options & MHD_USE_TURBO))
          {
            struct epoll_event event;

            event.events = EPOLLIN | EPOLLOUT | EPOLLPRI | EPOLLET | EPOLLRDHUP;
            event.data.ptr = connection;
            if (0 != epoll_ctl (daemon->epoll_fd,
                                EPOLL_CTL_ADD,
                                connection->socket_fd,
                                &event))
            {
              eno = errno;
#ifdef HAVE_MESSAGES
              MHD_DLOG (daemon,
                        _ ("Call to epoll_ctl failed: %s\n"),
                        MHD_socket_last_strerr_ ());
#endif
              mhd_probe_log ("EPOLL_CTL_ADD_FAILED: epoll_fd=%d, socket_fd=%d, errno=%d",
                             daemon->epoll_fd,
                             connection->socket_fd,
                             eno);
            }
            else
            { /* 'socket_fd' has been added to 'epool' */
              connection->epoll_state |= MHD_EPOLL_STATE_IN_EPOLL_SET;

              mhd_probe_log ("EPOLL_ADD_SUCCESS: connection=%p -> returning MHD_YES",
                             (void *) connection);

              return MHD_YES;  /* *** Function success exit point *** */
            }
          }
          else
          {
            connection->epoll_state |= MHD_EPOLL_STATE_READ_READY
                                       | MHD_EPOLL_STATE_WRITE_READY
                                       | MHD_EPOLL_STATE_IN_EREADY_EDLL;
            EDLL_insert (daemon->eready_head,
                         daemon->eready_tail,
                         connection);

            mhd_probe_log ("TURBO_EPOLL_MODE: connection=%p -> returning MHD_YES",
                           (void *) connection);

            return MHD_YES;  /* *** Function success exit point *** */
          }
        }
        else /* No 'epoll' */
#endif /* EPOLL_SUPPORT */
        {
          mhd_probe_log ("NO_EPOLL_OR_NOT_USING_THREAD_PER_CONN: connection=%p -> returning MHD_YES",
                         (void *) connection);
          return MHD_YES;    /* *** Function success exit point *** */
        }
      }

      /* ** Below is a cleanup path ** */
      if (NULL != daemon->notify_connection)
      {
        mhd_probe_log ("CALL_NOTIFY_CLOSED: notify_fn=%p, notify_cls=%p, passed_socket_ctx_addr=%p, connection=%p, event=%d",
                       (void *) daemon->notify_connection,
                       (void *) daemon->notify_connection_cls,
                       (void *) &connection->socket_context,
                       (void *) connection,
                       MHD_CONNECTION_NOTIFY_CLOSED);

        daemon->notify_connection (daemon->notify_connection_cls,
                                   connection,
                                   &connection->socket_context,
                                   MHD_CONNECTION_NOTIFY_CLOSED);

        mhd_probe_log ("RETURNED_FROM_NOTIFY_CLOSED: notify_fn=%p, connection=%p",
                       (void *) daemon->notify_connection,
                       (void *) connection);
      }
      MHD_mutex_lock_chk_ (&daemon->cleanup_connection_mutex);
      if (! MHD_D_IS_USING_THREAD_PER_CONN_ (daemon))
      {
        XDLL_remove (daemon->normal_timeout_head,
                     daemon->normal_timeout_tail,
                     connection);
      }
      DLL_remove (daemon->connections_head,
                  daemon->connections_tail,
                  connection);
      daemon->connections--;
      MHD_mutex_unlock_chk_ (&daemon->cleanup_connection_mutex);
    }
    MHD_pool_destroy (connection->pool);
  }
  /* Free resources allocated before the call of this functions */
#ifdef HTTPS_SUPPORT
  if (NULL != connection->tls_session)
    gnutls_deinit (connection->tls_session);
#endif /* HTTPS_SUPPORT */
  MHD_ip_limit_del (daemon,
                    connection->addr,
                    connection->addr_len);
  if (NULL != connection->addr)
    free (connection->addr);
  MHD_socket_close_chk_ (connection->socket_fd);
  free (connection);

  mhd_probe_log ("CLEANUP_DONE: final eno=%d, connection freed, returning MHD_NO",
                 eno);

  if (0 != eno)
    errno = eno;
#ifdef EINVAL
  else
    errno = EINVAL;
#endif /* EINVAL */
  return MHD_NO;  /* *** Function failure exit point *** */
}
