static void ppm_log_state(struct MHD_PostProcessor *pp,
                              const char *buf,
                              const char *where,
                              size_t poff,
                              size_t post_data_len,
                              size_t ioff,
                              size_t max)
{
  FILE *f = fopen("/tmp/logs/2329.txt", "a");
  if (!f)
    return;
  /* print basic numeric state */
  fprintf(f, "[%s] pp=%p buffer_pos=%zu buffer_size=%zu poff=%zu post_data_len=%zu ioff=%zu max=%zu state=%d skip_rn=%d dash_state=%d\n",
          where,
          (void *) pp,
          pp->buffer_pos,
          pp->buffer_size,
          poff,
          post_data_len,
          ioff,
          max,
          (int) pp->state,
          (int) pp->skip_rn,
          (int) pp->dash_state);
  /* print a couple of bytes from buffer if available (safe access) */
  if (buf != NULL)
  {
    if (pp->buffer_pos > 0)
      fprintf(f, "  buf[0]=0x%02x (%c)\n", (unsigned char) buf[0], (buf[0] >= 32 && buf[0] < 127) ? buf[0] : '.');
    if (pp->buffer_pos > 1)
      fprintf(f, "  buf[1]=0x%02x (%c)\n", (unsigned char) buf[1], (buf[1] >= 32 && buf[1] < 127) ? buf[1] : '.');
  }
  fflush(f);
  fclose(f);
}

static void ppm_log_event(struct MHD_PostProcessor *pp,
                          const char *msg,
                          size_t poff,
                          size_t post_data_len,
                          size_t ioff)
{
  FILE *f = fopen("/tmp/logs/2329.txt", "a");
  if (!f)
    return;
  fprintf(f, "[EVENT] %s pp=%p buffer_pos=%zu buffer_size=%zu poff=%zu post_data_len=%zu ioff=%zu state=%d\n",
          msg,
          (void *) pp,
          pp->buffer_pos,
          pp->buffer_size,
          poff,
          post_data_len,
          ioff,
          (int) pp->state);
  fflush(f);
  fclose(f);
}

static enum MHD_Result
post_process_multipart (struct MHD_PostProcessor *pp,
                        const char *post_data,
                        size_t post_data_len)
{
  char *buf;
  size_t max;
  size_t ioff;
  size_t poff;
  int state_changed;

  buf = (char *) &pp[1];
  ioff = 0;
  poff = 0;
  state_changed = 1;

  ppm_log_state(pp, buf, "ENTRY", poff, post_data_len, ioff, (size_t) -1);

  while ( (poff < post_data_len) ||
          ( (pp->buffer_pos > 0) &&
            (0 != state_changed) ) )
  {
    /* first, move as much input data
       as possible to our internal buffer */
    /* log values before computing max */
    ppm_log_state(pp, buf, "BEFORE_MAX", poff, post_data_len, ioff, (size_t) -1);

    max = pp->buffer_size - pp->buffer_pos;
    if (max > post_data_len - poff)
      max = post_data_len - poff;

    /* log computed max and pointers just before memcpy */
    {
      FILE *f = fopen("/tmp/logs/2329.txt", "a");
      if (f)
      {
        fprintf(f, "[BEFORE_MEMCPY] pp=%p buf_addr=%p buf_offset=%zu post_data_addr=%p post_offset=%zu max=%zu\n",
                (void *) pp,
                (void *) buf,
                pp->buffer_pos,
                (void *) post_data,
                poff,
                max);
        fflush(f);
        fclose(f);
      }
    }

    /* perform the copy (original logic preserved) */
    memcpy (&buf[pp->buffer_pos],
            &post_data[poff],
            max);

    poff += max;
    pp->buffer_pos += max;

    ppm_log_state(pp, buf, "AFTER_MEMCPY", poff, post_data_len, ioff, max);

    if ( (0 == max) &&
         (0 == state_changed) &&
         (poff < post_data_len) )
    {
      ppm_log_event(pp, "OUT_OF_MEMORY_CONDITION", poff, post_data_len, ioff);
      pp->state = PP_Error;
      return MHD_NO;            /* out of memory */
    }
    state_changed = 0;

    /* first state machine for '\r'-'\n' and '--' handling */
    switch (pp->skip_rn)
    {
    case RN_Inactive:
      break;
    case RN_OptN:
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        ppm_log_event(pp, "RN_OptN matched LF at buf[0]", poff, post_data_len, ioff);
        goto AGAIN;
      }
    /* fall-through! */
    case RN_Dash:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Dash2;
        ppm_log_event(pp, "RN_Dash matched '-' at buf[0]", poff, post_data_len, ioff);
        goto AGAIN;
      }
      pp->skip_rn = RN_Full;
    /* fall-through! */
    case RN_Full:
      if (buf[0] == '\r')
      {
        if ( (pp->buffer_pos > 1) &&
             ('\n' == buf[1]) )
        {
          pp->skip_rn = RN_Inactive;
          ioff += 2;
          ppm_log_event(pp, "RN_Full matched CRLF", poff, post_data_len, ioff);
        }
        else
        {
          pp->skip_rn = RN_OptN;
          ioff++;
          ppm_log_event(pp, "RN_Full matched CR only", poff, post_data_len, ioff);
        }
        goto AGAIN;
      }
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        ppm_log_event(pp, "RN_Full matched LF", poff, post_data_len, ioff);
        goto AGAIN;
      }
      pp->skip_rn = RN_Inactive;
      pp->state = PP_Error;
      ppm_log_event(pp, "RN_Full failed: no CRLF", poff, post_data_len, ioff);
      return MHD_NO;            /* no '\r\n' */
    case RN_Dash2:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Full;
        pp->state = pp->dash_state;
        ppm_log_event(pp, "RN_Dash2 matched second '-'", poff, post_data_len, ioff);
        goto AGAIN;
      }
      pp->state = PP_Error;
      ppm_log_event(pp, "RN_Dash2 failed: expected '-'", poff, post_data_len, ioff);
      break;
    }

    /* main state engine */
    switch (pp->state)
    {
    case PP_Error:
      ppm_log_event(pp, "RETURN_PP_ERROR", poff, post_data_len, ioff);
      return MHD_NO;
    case PP_Done:
      /* did not expect to receive more data */
      ppm_log_event(pp, "RETURN_PP_DONE_UNEXPECTED_DATA", poff, post_data_len, ioff);
      pp->state = PP_Error;
      return MHD_NO;
    case PP_Init:
      /**
       * Per RFC2046 5.1.1 NOTE TO IMPLEMENTORS, consume anything
       * prior to the first multipart boundary:
       *
       * > There appears to be room for additional information prior
       * * to the first boundary delimiter line and following the
       * > final boundary delimiter line.  These areas should
       * > generally be left blank, and implementations must ignore
       * > anything that appears before the first boundary delimiter
       * > line or after the last one.
       */
      (void) find_boundary (pp,
                            pp->boundary,
                            pp->blen,
                            &ioff,
                            PP_ProcessEntryHeaders,
                            PP_Done);
      break;
    case PP_NextBoundary:
      if (MHD_NO == find_boundary (pp,
                                   pp->boundary,
                                   pp->blen,
                                   &ioff,
                                   PP_ProcessEntryHeaders,
                                   PP_Done))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      break;
    case PP_ProcessEntryHeaders:
      pp->must_ikvi = true;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_PerformCheckMultipart))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_PerformCheckMultipart:
      if ( (NULL != pp->content_type) &&
           (MHD_str_equal_caseless_n_ (pp->content_type,
                                       "multipart/mixed",
                                       MHD_STATICSTR_LEN_ ("multipart/mixed"))))
      {
        pp->nested_boundary = strstr (pp->content_type,
                                      "boundary=");
        if (NULL == pp->nested_boundary)
        {
          pp->state = PP_Error;
          ppm_log_event(pp, "MISSING_NESTED_BOUNDARY", poff, post_data_len, ioff);
          return MHD_NO;
        }
        pp->nested_boundary =
          strdup (&pp->nested_boundary[MHD_STATICSTR_LEN_ ("boundary=")]);
        if (NULL == pp->nested_boundary)
        {
          /* out of memory */
          pp->state = PP_Error;
          ppm_log_event(pp, "OUT_OF_MEMORY_STRDUP_NESTED_BOUNDARY", poff, post_data_len, ioff);
          return MHD_NO;
        }
        /* free old content type, we will need that field
           for the content type of the nested elements */
        free (pp->content_type);
        pp->content_type = NULL;
        pp->nlen = strlen (pp->nested_boundary);
        pp->state = PP_Nested_Init;
        state_changed = 1;
        break;
      }
      pp->state = PP_ProcessValueToBoundary;
      pp->value_offset = 0;
      state_changed = 1;
      break;
    case PP_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->boundary,
                                               pp->blen,
                                               PP_PerformCleanup,
                                               PP_Done))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      break;
    case PP_PerformCleanup:
      /* clean up state of one multipart form-data element! */
      pp->have = NE_none;
      free_unmarked (pp);
      if (NULL != pp->nested_boundary)
      {
        free (pp->nested_boundary);
        pp->nested_boundary = NULL;
      }
      pp->state = PP_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_Init:
      if (NULL == pp->nested_boundary)
      {
        pp->state = PP_Error;
        ppm_log_event(pp, "NESTED_INIT_NO_BOUNDARY", poff, post_data_len, ioff);
        return MHD_NO;
      }
      if (MHD_NO == find_boundary (pp,
                                   pp->nested_boundary,
                                   pp->nlen,
                                   &ioff,
                                   PP_Nested_PerformMarking,
                                   PP_NextBoundary /* or PP_Error? */))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      break;
    case PP_Nested_PerformMarking:
      /* remember what headers were given
         globally */
      pp->have = NE_none;
      if (NULL != pp->content_name)
        pp->have |= NE_content_name;
      if (NULL != pp->content_type)
        pp->have |= NE_content_type;
      if (NULL != pp->content_filename)
        pp->have |= NE_content_filename;
      if (NULL != pp->content_transfer_encoding)
        pp->have |= NE_content_transfer_encoding;
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_ProcessEntryHeaders:
      pp->value_offset = 0;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_Nested_ProcessValueToBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_Nested_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->nested_boundary,
                                               pp->nlen,
                                               PP_Nested_PerformCleanup,
                                               PP_NextBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      break;
    case PP_Nested_PerformCleanup:
      free_unmarked (pp);
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_ProcessKey:
    case PP_ProcessValue:
    case PP_Callback:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
AGAIN:
    if (ioff > 0)
    {
      ppm_log_state(pp, buf, "BEFORE_MEMMOVE_LOOP", poff, post_data_len, ioff, (size_t) -1);

      memmove (buf,
               &buf[ioff],
               pp->buffer_pos - ioff);
      pp->buffer_pos -= ioff;
      ioff = 0;
      state_changed = 1;

      ppm_log_state(pp, buf, "AFTER_MEMMOVE_LOOP", poff, post_data_len, ioff, (size_t) -1);
    }
  }
END:
  if (0 != ioff)
  {
    ppm_log_state(pp, buf, "BEFORE_FINAL_MEMMOVE", poff, post_data_len, ioff, (size_t) -1);

    memmove (buf,
             &buf[ioff],
             pp->buffer_pos - ioff);
    pp->buffer_pos -= ioff;
  }

  if (poff < post_data_len)
  {
    ppm_log_event(pp, "POFF_LESS_THAN_POST_DATA_LEN_AT_END", poff, post_data_len, ioff);
    pp->state = PP_Error;
    return MHD_NO;              /* serious error */
  }

  ppm_log_state(pp, buf, "EXIT_SUCCESS", poff, post_data_len, ioff, (size_t) -1);
  return MHD_YES;
}
