static void
postproc_log_state(const struct MHD_PostProcessor *pp,
                   size_t ioff,
                   size_t poff,
                   size_t max,
                   const char *location)
{
  /* best-effort logging: do not change program state. */
  FILE *f = fopen("/tmp/logs/2329.txt", "a");
  if (!f)
    return;

  fprintf(f, "--- post_process_multipart LOG (%s) ---\n", location);
  fprintf(f, "pp=%p state=%d skip_rn=%d buffer_size=%zu buffer_pos=%zu poff=%zu ioff=%zu max=%zu\n",
          (const void*)pp,
          (int)pp->state,
          (int)pp->skip_rn,
          pp->buffer_size,
          pp->buffer_pos,
          poff,
          ioff,
          max);

  if (pp->buffer_pos > 0)
  {
    /* Print up to first two bytes of internal buffer (safe only when buffer_pos>0/1) */
    const char *buf = (const char *)&pp[1];
    fprintf(f, "buf[0]=0x%02x (%c)\n", (unsigned char)buf[0],
            (buf[0] >= 32 && buf[0] < 127) ? buf[0] : '.');
    if (pp->buffer_pos > 1)
      fprintf(f, "buf[1]=0x%02x (%c)\n", (unsigned char)buf[1],
              (buf[1] >= 32 && buf[1] < 127) ? buf[1] : '.');
    else
      fprintf(f, "buf[1]=<not available>\n");
  }
  else
  {
    fprintf(f, "buf[0]=<no data> buf[1]=<no data>\n");
  }

  fflush(f);
  fclose(f);
}

static enum MHD_Result
post_process_multipart (struct MHD_PostProcessor *pp,
                        const char *post_data,
                        size_t post_data_len)
{
  char *buf;
  size_t max;
  size_t ioff;
  size_t poff;
  int state_changed;

  buf = (char *) &pp[1];
  ioff = 0;
  poff = 0;
  state_changed = 1;
  while ( (poff < post_data_len) ||
          ( (pp->buffer_pos > 0) &&
            (0 != state_changed) ) )
  {
    /* probe: start-of-loop state */
    postproc_log_state(pp, ioff, poff, 0, "loop-start");

    /* first, move as much input data
       as possible to our internal buffer */
    max = pp->buffer_size - pp->buffer_pos;
    if (max > post_data_len - poff)
      max = post_data_len - poff;
    memcpy (&buf[pp->buffer_pos],
            &post_data[poff],
            max);
    poff += max;
    pp->buffer_pos += max;

    /* probe: after memcpy */
    postproc_log_state(pp, ioff, poff, max, "after-memcpy");

    if ( (0 == max) &&
         (0 == state_changed) &&
         (poff < post_data_len) )
    {
      pp->state = PP_Error;
      return MHD_NO;            /* out of memory */
    }
    state_changed = 0;

    /* first state machine for '\r'-'\n' and '--' handling */
    switch (pp->skip_rn)
    {
    case RN_Inactive:
      break;
    case RN_OptN:
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        postproc_log_state(pp, ioff, poff, 0, "RN_OptN->matched-\n");
        goto AGAIN;
      }
    /* fall-through! */
    case RN_Dash:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Dash2;
        postproc_log_state(pp, ioff, poff, 0, "RN_Dash->matched-\-");
        goto AGAIN;
      }
      pp->skip_rn = RN_Full;
    /* fall-through! */
    case RN_Full:
      if (buf[0] == '\r')
      {
        if ( (pp->buffer_pos > 1) &&
             ('\n' == buf[1]) )
        {
          pp->skip_rn = RN_Inactive;
          ioff += 2;
        }
        else
        {
          pp->skip_rn = RN_OptN;
          ioff++;
        }
        postproc_log_state(pp, ioff, poff, 0, "RN_Full->matched-\r");
        goto AGAIN;
      }
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        postproc_log_state(pp, ioff, poff, 0, "RN_Full->matched-\n");
        goto AGAIN;
      }
      pp->skip_rn = RN_Inactive;
      pp->state = PP_Error;
      postproc_log_state(pp, ioff, poff, 0, "RN_Full->error-no-\r\n");
      return MHD_NO;            /* no '\r\n' */
    case RN_Dash2:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Full;
        pp->state = pp->dash_state;
        postproc_log_state(pp, ioff, poff, 0, "RN_Dash2->matched-\-");
        goto AGAIN;
      }
      pp->state = PP_Error;
      postproc_log_state(pp, ioff, poff, 0, "RN_Dash2->error");
      break;
    }

    /* main state engine */
    switch (pp->state)
    {
    case PP_Error:
      postproc_log_state(pp, ioff, poff, 0, "state-PP_Error");
      return MHD_NO;
    case PP_Done:
      /* did not expect to receive more data */
      pp->state = PP_Error;
      postproc_log_state(pp, ioff, poff, 0, "state-PP_Done");
      return MHD_NO;
    case PP_Init:
      /**
       * Per RFC2046 5.1.1 NOTE TO IMPLEMENTORS, consume anything
       * prior to the first multipart boundary:
       *
       * > There appears to be room for additional information prior
       * * to the first boundary delimiter line and following the
       * > final boundary delimiter line.  These areas should
       * > generally be left blank, and implementations must ignore
       * > anything that appears before the first boundary delimiter
       * > line or after the last one.
       */
      (void) find_boundary (pp,
                            pp->boundary,
                            pp->blen,
                            &ioff,
                            PP_ProcessEntryHeaders,
                            PP_Done);
      postproc_log_state(pp, ioff, poff, 0, "state-PP_Init-after-find_boundary");
      break;
    case PP_NextBoundary:
      if (MHD_NO == find_boundary (pp,
                                   pp->boundary,
                                   pp->blen,
                                   &ioff,
                                   PP_ProcessEntryHeaders,
                                   PP_Done))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      break;
    case PP_ProcessEntryHeaders:
      pp->must_ikvi = true;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_PerformCheckMultipart))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      postproc_log_state(pp, ioff, poff, 0, "state-PP_ProcessEntryHeaders-after-headers");
      break;
    case PP_PerformCheckMultipart:
      if ( (NULL != pp->content_type) &&
           (MHD_str_equal_caseless_n_ (pp->content_type,
                                       "multipart/mixed",
                                       MHD_STATICSTR_LEN_ ("multipart/mixed"))))
      {
        pp->nested_boundary = strstr (pp->content_type,
                                      "boundary=");
        if (NULL == pp->nested_boundary)
        {
          pp->state = PP_Error;
          postproc_log_state(pp, ioff, poff, 0, "state-PP_PerformCheckMultipart-no-boundary");
          return MHD_NO;
        }
        pp->nested_boundary =
          strdup (&pp->nested_boundary[MHD_STATICSTR_LEN_ ("boundary=")]);
        if (NULL == pp->nested_boundary)
        {
          /* out of memory */
          pp->state = PP_Error;
          postproc_log_state(pp, ioff, poff, 0, "state-PP_PerformCheckMultipart-strdup-failed");
          return MHD_NO;
        }
        /* free old content type, we will need that field
           for the content type of the nested elements */
        free (pp->content_type);
        pp->content_type = NULL;
        pp->nlen = strlen (pp->nested_boundary);
        pp->state = PP_Nested_Init;
        state_changed = 1;
        postproc_log_state(pp, ioff, poff, 0, "state-PP_PerformCheckMultipart-nested-init");
        break;
      }
      pp->state = PP_ProcessValueToBoundary;
      pp->value_offset = 0;
      state_changed = 1;
      postproc_log_state(pp, ioff, poff, 0, "state-PP_PerformCheckMultipart-fallthrough");
      break;
    case PP_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->boundary,
                                               pp->blen,
                                               PP_PerformCleanup,
                                               PP_Done))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      break;
    case PP_PerformCleanup:
      /* clean up state of one multipart form-data element! */
      pp->have = NE_none;
      free_unmarked (pp);
      if (NULL != pp->nested_boundary)
      {
        free (pp->nested_boundary);
        pp->nested_boundary = NULL;
      }
      pp->state = PP_ProcessEntryHeaders;
      state_changed = 1;
      postproc_log_state(pp, ioff, poff, 0, "state-PP_PerformCleanup");
      break;
    case PP_Nested_Init:
      if (NULL == pp->nested_boundary)
      {
        pp->state = PP_Error;
        postproc_log_state(pp, ioff, poff, 0, "state-PP_Nested_Init-no-nested-boundary");
        return MHD_NO;
      }
      if (MHD_NO == find_boundary (pp,
                                   pp->nested_boundary,
                                   pp->nlen,
                                   &ioff,
                                   PP_Nested_PerformMarking,
                                   PP_NextBoundary /* or PP_Error? */))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      break;
    case PP_Nested_PerformMarking:
      /* remember what headers were given
         globally */
      pp->have = NE_none;
      if (NULL != pp->content_name)
        pp->have |= NE_content_name;
      if (NULL != pp->content_type)
        pp->have |= NE_content_type;
      if (NULL != pp->content_filename)
        pp->have |= NE_content_filename;
      if (NULL != pp->content_transfer_encoding)
        pp->have |= NE_content_transfer_encoding;
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      postproc_log_state(pp, ioff, poff, 0, "state-PP_Nested_PerformMarking");
      break;
    case PP_Nested_ProcessEntryHeaders:
      pp->value_offset = 0;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_Nested_ProcessValueToBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      postproc_log_state(pp, ioff, poff, 0, "state-PP_Nested_ProcessEntryHeaders");
      break;
    case PP_Nested_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->nested_boundary,
                                               pp->nlen,
                                               PP_Nested_PerformCleanup,
                                               PP_NextBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      break;
    case PP_Nested_PerformCleanup:
      free_unmarked (pp);
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      postproc_log_state(pp, ioff, poff, 0, "state-PP_Nested_PerformCleanup");
      break;
    case PP_ProcessKey:
    case PP_ProcessValue:
    case PP_Callback:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
AGAIN:
    if (ioff > 0)
    {
      /* probe: just before memmove in loop */
      postproc_log_state(pp, ioff, poff, 0, "before-memmove-loop");

      memmove (buf,
               &buf[ioff],
               pp->buffer_pos - ioff);
      pp->buffer_pos -= ioff;
      ioff = 0;
      state_changed = 1;

      /* probe: after memmove in loop */
      postproc_log_state(pp, ioff, poff, 0, "after-memmove-loop");
    }
  }
END:
  if (0 != ioff)
  {
    /* probe: before final memmove */
    postproc_log_state(pp, ioff, poff, 0, "before-final-memmove");

    memmove (buf,
             &buf[ioff],
             pp->buffer_pos - ioff);
    pp->buffer_pos -= ioff;
  }

  if (poff < post_data_len)
  {
    pp->state = PP_Error;
    postproc_log_state(pp, ioff, poff, 0, "end-poff-less-than-post_data_len");
    return MHD_NO;              /* serious error */
  }
  postproc_log_state(pp, ioff, poff, 0, "end-success");
  return MHD_YES;
}
