#include <stdio.h>
#include <stdarg.h>
#include <string.h>
#include <stdlib.h>

/* Probe logging helpers. These are lightweight and append logs to the
   file /tmp/logs/2323.txt so other agents can reproduce the control
   flow and capture the state relevant to the Rule 8.13 scenario. */
static void probe_log(const char *fmt, ...)
{
    FILE *f = fopen("/tmp/logs/2323.txt", "a");
    if (NULL == f)
        return;
    va_list ap;
    va_start(ap, fmt);
    vfprintf(f, fmt, ap);
    va_end(ap);
    fprintf(f, "\n");
    fclose(f);
}

static void probe_log_hex_snippet(const char *label, const char *s, size_t maxlen)
{
    if (s == NULL)
    {
        probe_log("%s: NULL", label);
        return;
    }
    /* print up to maxlen bytes as printable or hex */
    char buf[256];
    size_t i, j = 0;
    for (i = 0; (i < maxlen) && s[i] && (j + 4 < sizeof(buf)); ++i)
    {
        unsigned char c = (unsigned char) s[i];
        if (c >= 0x20 && c < 0x7f)
            buf[j++] = (char) c;
        else
            j += snprintf(&buf[j], sizeof(buf) - j, "\\x%02x", c);
    }
    buf[j] = '\0';
    probe_log("%s: \"%s\" (len=%zu, printed=%zu)", label, buf, strlen(s), j);
}

static void probe_log_pointer(const char *label, const void *p)
{
    probe_log("%s: %p", label, p);
}

/* Modified post_process_multipart with inserted probe logging. The
   original logic is preserved; logging calls are inserted at key
   decision points to capture the state of pp->content_type and the
   control path when multipart content-types are processed. */
static enum MHD_Result
post_process_multipart (struct MHD_PostProcessor *pp,
                        const char *post_data,
                        size_t post_data_len)
{
  char *buf;
  size_t max;
  size_t ioff;
  size_t poff;
  int state_changed;

  probe_log("post_process_multipart: entry: pp=%p, post_data_len=%zu", (void *)pp, post_data_len);
  if (pp != NULL)
  {
    probe_log_pointer("pp->content_type (initial)", pp->content_type);
    /* If non-NULL, log a snippet of the string for debugging. */
    if (pp->content_type != NULL)
      probe_log_hex_snippet("pp->content_type (initial snippet)", pp->content_type, 200);
  }

  buf = (char *) &pp[1];
  ioff = 0;
  poff = 0;
  state_changed = 1;
  while ( (poff < post_data_len) ||
          ( (pp->buffer_pos > 0) &&
            (0 != state_changed) ) )
  {
    /* first, move as much input data
       as possible to our internal buffer */
    max = pp->buffer_size - pp->buffer_pos;
    if (max > post_data_len - poff)
      max = post_data_len - poff;
    memcpy (&buf[pp->buffer_pos],
            &post_data[poff],
            max);
    poff += max;
    pp->buffer_pos += max;
    if ( (0 == max) &&
         (0 == state_changed) &&
         (poff < post_data_len) )
    {
      pp->state = PP_Error;
      probe_log("post_process_multipart: out of memory condition: pp=%p", (void *)pp);
      return MHD_NO;            /* out of memory */
    }
    state_changed = 0;

    /* first state machine for '\r'-'\n' and '--' handling */
    switch (pp->skip_rn)
    {
    case RN_Inactive:
      break;
    case RN_OptN:
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
    /* fall-through! */
    case RN_Dash:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Dash2;
        goto AGAIN;
      }
      pp->skip_rn = RN_Full;
    /* fall-through! */
    case RN_Full:
      if (buf[0] == '\r')
      {
        if ( (pp->buffer_pos > 1) &&
             ('\n' == buf[1]) )
        {
          pp->skip_rn = RN_Inactive;
          ioff += 2;
        }
        else
        {
          pp->skip_rn = RN_OptN;
          ioff++;
        }
        goto AGAIN;
      }
      if (buf[0] == '\n')
      {
        ioff++;
        pp->skip_rn = RN_Inactive;
        goto AGAIN;
      }
      pp->skip_rn = RN_Inactive;
      pp->state = PP_Error;
      probe_log("post_process_multipart: expected CR/LF not found -> PP_Error");
      return MHD_NO;            /* no '\r\n' */
    case RN_Dash2:
      if (buf[0] == '-')
      {
        ioff++;
        pp->skip_rn = RN_Full;
        pp->state = pp->dash_state;
        goto AGAIN;
      }
      pp->state = PP_Error;
      break;
    }

    /* main state engine */
    switch (pp->state)
    {
    case PP_Error:
      probe_log("post_process_multipart: PP_Error state -> returning MHD_NO");
      return MHD_NO;
    case PP_Done:
      /* did not expect to receive more data */
      pp->state = PP_Error;
      probe_log("post_process_multipart: PP_Done but more data -> PP_Error");
      return MHD_NO;
    case PP_Init:
      /**
       * Per RFC2046 5.1.1 NOTE TO IMPLEMENTORS, consume anything
       * prior to the first multipart boundary:
       *
       * > There appears to be room for additional information prior
       * > to the first boundary delimiter line and following the
       * > final boundary delimiter line.  These areas should
       * > generally be left blank, and implementations must ignore
       * > anything that appears before the first boundary delimiter
       * > line or after the last one.
       */
      (void) find_boundary (pp,
                            pp->boundary,
                            pp->blen,
                            &ioff,
                            PP_ProcessEntryHeaders,
                            PP_Done);
      probe_log("post_process_multipart: after find_boundary PP_Init: pp=%p, state=%d", (void *)pp, (int)pp->state);
      break;
    case PP_NextBoundary:
      if (MHD_NO == find_boundary (pp,
                                   pp->boundary,
                                   pp->blen,
                                   &ioff,
                                   PP_ProcessEntryHeaders,
                                   PP_Done))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      break;
    case PP_ProcessEntryHeaders:
      pp->must_ikvi = true;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_PerformCheckMultipart))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_PerformCheckMultipart:
      /* Log the content type pointer and a snippet before any checks */
      probe_log("PP_PerformCheckMultipart: pp=%p, pp->content_type=%p", (void *)pp, (void *)pp->content_type);
      if (pp->content_type != NULL)
        probe_log_hex_snippet("PP_PerformCheckMultipart: content_type snippet", pp->content_type, 200);

      /* Evaluate the case-insensitive equality into a local variable so
         we can log the result and avoid changing control flow semantics. */
      {
        int is_multipart = 0;
        if (pp->content_type != NULL)
        {
          is_multipart = MHD_str_equal_caseless_n_ (pp->content_type,
                                                    "multipart/mixed",
                                                    MHD_STATICSTR_LEN_ ("multipart/mixed"));
          probe_log("PP_PerformCheckMultipart: MHD_str_equal_caseless_n_ returned %d", is_multipart);
        }

        if ( (NULL != pp->content_type) && (is_multipart) )
        {
          /* Log that we will search for the boundary token inside
             the content_type string. */
          probe_log("PP_PerformCheckMultipart: searching for \"boundary=\" in content_type");
          pp->nested_boundary = strstr (pp->content_type,
                                        "boundary=");
          probe_log("PP_PerformCheckMultipart: strstr returned %p", (void *)pp->nested_boundary);

          if (NULL == pp->nested_boundary)
          {
            pp->state = PP_Error;
            probe_log("PP_PerformCheckMultipart: nested boundary not found -> PP_Error");
            return MHD_NO;
          }
          pp->nested_boundary =
            strdup (&pp->nested_boundary[MHD_STATICSTR_LEN_ ("boundary=")]);
          if (NULL == pp->nested_boundary)
          {
            /* out of memory */
            pp->state = PP_Error;
            probe_log("PP_PerformCheckMultipart: strdup for nested_boundary failed -> PP_Error");
            return MHD_NO;
          }
          /* free old content type, we will need that field
             for the content type of the nested elements */
          probe_log("PP_PerformCheckMultipart: freeing pp->content_type at %p", (void *)pp->content_type);
          free (pp->content_type);
          pp->content_type = NULL;
          pp->nlen = strlen (pp->nested_boundary);
          probe_log("PP_PerformCheckMultipart: new nested_boundary=%p (len=%zu)", (void *)pp->nested_boundary, pp->nlen);
          pp->state = PP_Nested_Init;
          state_changed = 1;
          break;
        }
      }
      pp->state = PP_ProcessValueToBoundary;
      pp->value_offset = 0;
      state_changed = 1;
      break;
    case PP_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->boundary,
                                               pp->blen,
                                               PP_PerformCleanup,
                                               PP_Done))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      break;
    case PP_PerformCleanup:
      /* clean up state of one multipart form-data element! */
      pp->have = NE_none;
      free_unmarked (pp);
      if (NULL != pp->nested_boundary)
      {
        free (pp->nested_boundary);
        pp->nested_boundary = NULL;
      }
      pp->state = PP_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_Init:
      if (NULL == pp->nested_boundary)
      {
        pp->state = PP_Error;
        probe_log("PP_Nested_Init: nested_boundary NULL -> PP_Error");
        return MHD_NO;
      }
      if (MHD_NO == find_boundary (pp,
                                   pp->nested_boundary,
                                   pp->nlen,
                                   &ioff,
                                   PP_Nested_PerformMarking,
                                   PP_NextBoundary /* or PP_Error? */))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        goto END;
      }
      break;
    case PP_Nested_PerformMarking:
      /* remember what headers were given
         globally */
      pp->have = NE_none;
      if (NULL != pp->content_name)
        pp->have |= NE_content_name;
      if (NULL != pp->content_type)
        pp->have |= NE_content_type;
      if (NULL != pp->content_filename)
        pp->have |= NE_content_filename;
      if (NULL != pp->content_transfer_encoding)
        pp->have |= NE_content_transfer_encoding;
      probe_log("PP_Nested_PerformMarking: have mask=0x%x", (unsigned)pp->have);
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_Nested_ProcessEntryHeaders:
      pp->value_offset = 0;
      if (MHD_NO ==
          process_multipart_headers (pp,
                                     &ioff,
                                     PP_Nested_ProcessValueToBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        else
          goto END;
      }
      state_changed = 1;
      break;
    case PP_Nested_ProcessValueToBoundary:
      if (MHD_NO == process_value_to_boundary (pp,
                                               &ioff,
                                               pp->nested_boundary,
                                               pp->nlen,
                                               PP_Nested_PerformCleanup,
                                               PP_NextBoundary))
      {
        if (pp->state == PP_Error)
          return MHD_NO;
        break;
      }
      break;
    case PP_Nested_PerformCleanup:
      free_unmarked (pp);
      pp->state = PP_Nested_ProcessEntryHeaders;
      state_changed = 1;
      break;
    case PP_ProcessKey:
    case PP_ProcessValue:
    case PP_Callback:
    default:
      MHD_PANIC (_ ("internal error.\n")); /* should never happen! */
    }
AGAIN:
    if (ioff > 0)
    {
      memmove (buf,
               &buf[ioff],
               pp->buffer_pos - ioff);
      pp->buffer_pos -= ioff;
      ioff = 0;
      state_changed = 1;
    }
  }
END:
  if (0 != ioff)
  {
    memmove (buf,
             &buf[ioff],
             pp->buffer_pos - ioff);
    pp->buffer_pos -= ioff;
  }
  if (poff < post_data_len)
  {
    pp->state = PP_Error;
    probe_log("post_process_multipart: poff < post_data_len -> PP_Error");
    return MHD_NO;              /* serious error */
  }
  probe_log("post_process_multipart: exit OK: pp=%p", (void *)pp);
  return MHD_YES;
}
