#include <stdio.h>
#include <stdbool.h>

static void
log_probe(const char *event, struct MHD_Connection *connection,
          bool plain_send_next, bool push_data)
{
  FILE *f = fopen("/tmp/logs/1813.txt", "a");
  if (f)
  {
    fprintf(f, "EVENT: %s\n", event);
    if (connection)
    {
      fprintf(f,
              " connection=%p daemon=%p is_nonip=%d sk_corked=%d sk_nodelay=%d plain_send_next=%d push_data=%d\n",
              (void *)connection,
              (void *)connection->daemon,
              (int)connection->is_nonip,
              (int)connection->sk_corked,
              (int)connection->sk_nodelay,
              (int)plain_send_next,
              (int)push_data);
    }
    else
    {
      fprintf(f, " connection=NULL plain_send_next=%d push_data=%d\n",
              (int)plain_send_next, (int)push_data);
    }
    fflush(f);
    fclose(f);
  }
}

static void
post_send_setopt (struct MHD_Connection *connection,
                  bool plain_send_next,
                  bool push_data)
{
  /* Try to buffer data if not sending the final piece.
   * Final piece is indicated by push_data == true. */
  const bool buffer_data = (! push_data);

  log_probe("enter_post_send_setopt", connection, plain_send_next, push_data);

  if (_MHD_YES == connection->is_nonip)
  {
    log_probe("return_is_nonip", connection, plain_send_next, push_data);
    return;
  }
  if (buffer_data)
  {
    log_probe("return_buffer_data", connection, plain_send_next, push_data);
    return; /* Nothing to do after send(). */
  }

#ifndef MHD_USE_MSG_MORE
  (void) plain_send_next; /* Mute compiler warning */
#endif /* ! MHD_USE_MSG_MORE */

  /* Need to push data. */
#ifdef MHD_TCP_CORK_NOPUSH
#ifdef _MHD_CORK_RESET_PUSH_DATA_ALWAYS
#ifdef _MHD_NODELAY_SET_PUSH_DATA_ALWAYS
#ifdef MHD_USE_MSG_MORE
  if (_MHD_OFF == connection->sk_corked)
  {
    if (_MHD_ON == connection->sk_nodelay)
    {
      log_probe("return_sk_corked_off_sk_nodelay_on", connection, plain_send_next, push_data);
      return; /* Data was already pushed by send(). */
    }
  }
  /* This is Linux kernel. There are options:
   * * Push the data by setting of TCP_NODELAY (without change
   *   of the cork on the socket),
   * * Push the data by resetting of TCP_CORK.
   * The optimal choice depends on the next final send functions
   * used on the same socket. If TCP_NODELAY wasn't set then push
   * data by setting TCP_NODELAY (TCP_NODELAY will not be removed
   * and is needed to push the data by send() without MSG_MORE).
   * If send()/sendmsg() will be used next than push data by
   * resetting of TCP_CORK so next send without MSG_MORE will push
   * data to the network (without additional sys-call to push data).
   * If next final send function will not support MSG_MORE (like
   * sendfile() or TLS-connection) than push data by setting
   * TCP_NODELAY so socket will remain corked (no additional
   * sys-call before next send()). */
  if ((_MHD_ON != connection->sk_nodelay) ||
      (! plain_send_next))
  {
    log_probe("about_to_try_set_nodelay_true", connection, plain_send_next, push_data);
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      log_probe("return_set_nodelay_true_pushed", connection, plain_send_next, push_data);
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    log_probe("set_nodelay_failed_try_uncork", connection, plain_send_next, push_data);
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */
    if (MHD_connection_set_cork_state_ (connection, false))
    {
      log_probe("return_uncork_false_pushed", connection, plain_send_next, push_data);
      return; /* Data has been pushed by uncorking the socket. */
    }
    log_probe("uncork_failed_cannot_push", connection, plain_send_next, push_data);
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
  }
  else
  {
    log_probe("about_to_try_uncork_first", connection, plain_send_next, push_data);
    if (MHD_connection_set_cork_state_ (connection, false))
    {
      log_probe("return_uncork_false_pushed_else_branch", connection, plain_send_next, push_data);
      return; /* Data has been pushed by uncorking the socket. */
    }
    log_probe("uncork_failed_try_set_nodelay", connection, plain_send_next, push_data);
    /* Failed to uncork the socket.
     * Really unlikely to happen on TCP connections. */
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      log_probe("return_set_nodelay_true_pushed_after_uncork", connection, plain_send_next, push_data);
      return; /* Data has been pushed by TCP_NODELAY. */
    }
    log_probe("set_nodelay_failed_after_uncork", connection, plain_send_next, push_data);
    /* Failed to set TCP_NODELAY for the socket.
     * Really unlikely to happen on TCP connections. */

    /* The socket cannot be uncorked, no way to push data */
  }
#else  /* ! MHD_USE_MSG_MORE */
  /* Use setting of TCP_NODELAY here to avoid sys-call
   * for corking the socket during sending of the next response. */
  log_probe("about_to_try_set_nodelay_true_nomsgmore", connection, plain_send_next, push_data);
  if (MHD_connection_set_nodelay_state_ (connection, true))
  {
    log_probe("return_set_nodelay_true_nomsgmore", connection, plain_send_next, push_data);
    return; /* Data was pushed by TCP_NODELAY. */
  }
  log_probe("set_nodelay_failed_nomsgmore_try_uncork", connection, plain_send_next, push_data);
  /* Failed to set TCP_NODELAY for the socket.
   * Really unlikely to happen on TCP connections. */
  if (MHD_connection_set_cork_state_ (connection, false))
  {
    log_probe("return_uncork_false_nomsgmore", connection, plain_send_next, push_data);
    return; /* Data was pushed by uncorking the socket. */
  }
  log_probe("uncork_failed_nomsgmore_cannot_push", connection, plain_send_next, push_data);
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */

  /* The socket remains corked, no way to push data */
#endif /* ! MHD_USE_MSG_MORE */
#else  /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
  if (MHD_connection_set_cork_state_ (connection, false))
  {
    log_probe("return_uncork_false_nodeplay_disabled", connection, plain_send_next, push_data);
    return; /* Data was pushed by uncorking the socket. */
  }
  log_probe("uncork_failed_nodeplay_disabled_return_socket_remains_corked", connection, plain_send_next, push_data);
  /* Failed to uncork the socket.
   * Really unlikely to happen on TCP connections. */
  log_probe("return_socket_remains_corked", connection, plain_send_next, push_data);
  return; /* Socket remains corked, no way to push data */
#endif /* ! _MHD_NODELAY_SET_PUSH_DATA_ALWAYS */
#else  /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
  /* This is a typical *BSD or Darwin kernel. */

  if (_MHD_OFF == connection->sk_corked)
  {
    if (_MHD_ON == connection->sk_nodelay)
    {
      log_probe("return_bsd_sk_corked_off_nodelay_on", connection, plain_send_next, push_data);
      return; /* Data was already pushed by send(). */
    }

    /* Unlikely to reach this code.
     * TCP_NODELAY should be turned on before send(). */
    log_probe("about_to_try_set_nodelay_true_bsd", connection, plain_send_next, push_data);
    if (MHD_connection_set_nodelay_state_ (connection, true))
    {
      /* TCP_NODELAY has been set on uncorked socket.
       * Use zero-send to push the data. */
      log_probe("set_nodelay_true_bsd_try_zero_send", connection, plain_send_next, push_data);
      if (zero_send_ (connection))
      {
        log_probe("return_zero_send_pushed_bsd", connection, plain_send_next, push_data);
        return; /* The data has been pushed by zero-send. */
      }
    }

    log_probe("failed_all_means_bsd_cannot_push", connection, plain_send_next, push_data);
    /* Failed to push the data by all means. */
    /* There is nothing left to try. */
  }
  else
  {
#ifdef _MHD_CORK_RESET_PUSH_DATA
    enum MHD_tristate old_cork_state = connection->sk_corked;
#endif /* _MHD_CORK_RESET_PUSH_DATA */
    /* The socket is corked or cork state is unknown. */

    log_probe("about_to_try_uncork_bsd", connection, plain_send_next, push_data);
    if (MHD_connection_set_cork_state_ (connection, false))
    {
#ifdef _MHD_CORK_RESET_PUSH_DATA
      /* FreeBSD kernel */
      if (_MHD_OFF == old_cork_state)
      {
        log_probe("return_uncork_restored_old_state_off", connection, plain_send_next, push_data);
        return; /* Data has been pushed by uncorking the socket. */
      }
#endif /* _MHD_CORK_RESET_PUSH_DATA */

      /* Unlikely to reach this code.
       * The data should be pushed by uncorking (FreeBSD) or
       * the socket should be uncorked before send(). */
      log_probe("uncork_succeeded_but_try_zero_send", connection, plain_send_next, push_data);
      if ((_MHD_ON == connection->sk_nodelay) ||
          (MHD_connection_set_nodelay_state_ (connection, true)))
      {
        /* TCP_NODELAY is turned ON on uncorked socket.
         * Use zero-send to push the data. */
        log_probe("about_to_zero_send_bsd", connection, plain_send_next, push_data);
        if (zero_send_ (connection))
        {
          log_probe("return_zero_send_pushed_bsd_after_uncork", connection, plain_send_next, push_data);
          return; /* The data has been pushed by zero-send. */
        }
      }
    }
    log_probe("bsd_socket_remains_corked_cannot_push", connection, plain_send_next, push_data);
    /* The socket remains corked. Data cannot be pushed. */
  }
#endif /* ! _MHD_CORK_RESET_PUSH_DATA_ALWAYS */
#else  /* ! MHD_TCP_CORK_NOPUSH */
  /* Corking is not supported. Buffering is controlled
   * by TCP_NODELAY only. */
  mhd_assert (_MHD_ON != connection->sk_corked);
  if (_MHD_ON == connection->sk_nodelay)
  {
    log_probe("return_tcp_nodelay_on_no_cork", connection, plain_send_next, push_data);
    return; /* Data was already pushed by send(). */
  }

  /* Unlikely to reach this code.
   * TCP_NODELAY should be turned on before send(). */
  log_probe("about_to_try_set_nodelay_true_no_cork", connection, plain_send_next, push_data);
  if (MHD_connection_set_nodelay_state_ (connection, true))
  {
    /* TCP_NODELAY has been set.
     * Use zero-send to push the data. */
    log_probe("set_nodelay_true_no_cork_try_zero_send", connection, plain_send_next, push_data);
    if (zero_send_ (connection))
    {
      log_probe("return_zero_send_pushed_no_cork", connection, plain_send_next, push_data);
      return; /* The data has been pushed by zero-send. */
    }
  }

  log_probe("failed_to_push_no_cork", connection, plain_send_next, push_data);
  /* Failed to push the data. */
#endif /* ! MHD_TCP_CORK_NOPUSH */
#ifdef HAVE_MESSAGES
  log_probe("about_to_call_MHD_DLOG", connection, plain_send_next, push_data);
  MHD_DLOG (connection->daemon,
            _ ("Failed to push the data from buffers to the network. "
               "Client may experience some delay "
               "(usually in range 200ms - 5 sec).\n"));
  log_probe("after_call_MHD_DLOG", connection, plain_send_next, push_data);
#endif /* HAVE_MESSAGES */
  log_probe("return_end_post_send_setopt", connection, plain_send_next, push_data);
  return;
}
