#include <stdio.h>
#include <stdarg.h>
#include <stdint.h>
#include <stddef.h>

/* Lightweight probe logger used to record control flow and state. */
static void
mhd_probe_log (const char *fmt, ...)
{
  va_list ap;
  FILE *f = fopen("/tmp/logs/573.txt", "a");
  if (NULL == f)
    return;
  va_start (ap, fmt);
  vfprintf (f, fmt, ap);
  va_end (ap);
  fprintf (f, "\n");
  fclose (f);
}

/* Helper to dump a short hex/printable preview of the buffer. */
static void
mhd_probe_dump_buffer (const char *tag, const char *buf, size_t len)
{
  size_t i, n = (len < 32) ? len : 32;
  FILE *f = fopen("/tmp/logs/573.txt", "a");
  if (NULL == f)
    return;
  fprintf (f, "%s: buf=%p len=%zu preview=\'", tag, (const void *) buf, len);
  for (i = 0; i < n; ++i)
  {
    unsigned char c = (unsigned char) buf[i];
    if ((c >= 32) && (c <= 126))
      fputc ((int) c, f);
    else
      fprintf (f, "\\x%02x", c);
  }
  if (len > n)
    fprintf (f, "...\'");
  else
    fprintf (f, "\'");
  fprintf (f, "\n");
  fclose (f);
}

static void
process_request_body (struct MHD_Connection *connection)
{
  struct MHD_Daemon *daemon = connection->daemon;
  size_t available;
  bool instant_retry;
  char *buffer_head;
  const int discp_lvl = daemon->client_discipline;
  /* Treat bare LF as the end of the line.
     RFC 9112, section 2.2-3
     Note: MHD never replaces bare LF with space (RFC 9110, section 5.5-5).
     Bare LF is processed as end of the line or rejected as broken request. */
  const bool bare_lf_as_crlf = MHD_ALLOW_BARE_LF_AS_CRLF_ (discp_lvl);
  /* Allow "Bad WhiteSpace" in chunk extension.
     RFC 9112, Section 7.1.1, Paragraph 2 */
  const bool allow_bws = (2 < discp_lvl);

  mhd_assert (NULL == connection->rp.response);

  buffer_head = connection->read_buffer;
  available = connection->read_buffer_offset;

  /* Log entry state */
  mhd_probe_log ("process_request_body: entry conn=%p available=%zu read_buffer=%p discp_lvl=%d have_chunked=%d",
                 (void *) connection, available, (void *) buffer_head,
                 discp_lvl, (int) connection->rq.have_chunked_upload);
  mhd_probe_dump_buffer ("initial", buffer_head, available);

  do
  {
    size_t to_be_processed;
    size_t left_unprocessed;
    size_t processed_size;

    instant_retry = false;
    if (connection->rq.have_chunked_upload)
    {
      mhd_assert (MHD_SIZE_UNKNOWN == connection->rq.remaining_upload_size);
      if ( (connection->rq.current_chunk_offset ==
            connection->rq.current_chunk_size) &&
           (0 != connection->rq.current_chunk_size) )
      {
        size_t i;
        mhd_assert (0 != available);
        /* skip new line at the *end* of a chunk */
        i = 0;
        if ( (2 <= available) &&
             ('\r' == buffer_head[0]) &&
             ('\n' == buffer_head[1]) )
          i += 2;                        /* skip CRLF */
        else if (bare_lf_as_crlf && ('\n' == buffer_head[0]))
          i++;                           /* skip bare LF */
        else if (2 > available)
          break;                         /* need more upload data */

        /* Log behavior when skipping chunk-end delimiters */
        mhd_probe_log ("chunk-end-skip: skip_bytes=%zu available=%zu buffer_head=%p",
                       i, available, (void *) buffer_head);
        if (i > 0)
          mhd_probe_dump_buffer ("post-skip", buffer_head, (available >= i) ? i : available);

        if (0 == i)
        {
          /* malformed encoding */
          mhd_probe_log ("error: malformed chunk encoding detected (no CRLF or LF at end of chunk). conn=%p",
                         (void *) connection);
          transmit_error_response_static (connection,
                                          MHD_HTTP_BAD_REQUEST,
                                          REQUEST_CHUNKED_MALFORMED);
          return;
        }
        available -= i;
        buffer_head += i;
        connection->rq.current_chunk_offset = 0;
        connection->rq.current_chunk_size = 0;
        if (0 == available)
          break;
      }
      if (0 != connection->rq.current_chunk_size)
      {
        uint64_t cur_chunk_left;
        mhd_assert (connection->rq.current_chunk_offset < \
                    connection->rq.current_chunk_size);
        /* we are in the middle of a chunk, give
           as much as possible to the client (without
           crossing chunk boundaries) */
        cur_chunk_left
          = connection->rq.current_chunk_size
            - connection->rq.current_chunk_offset;
        if (cur_chunk_left > available)
          to_be_processed = available;
        else
        {         /* cur_chunk_left <= (size_t)available */
          to_be_processed = (size_t) cur_chunk_left;
          if (available > to_be_processed)
            instant_retry = true;
        }
      }
      else
      { /* Need the parse the chunk size line */
        /** The number of found digits in the chunk size number */
        size_t num_dig;
        uint64_t chunk_size;
        bool broken;
        bool overflow;

        mhd_assert (0 != available);

        overflow = false;
        chunk_size = 0; /* Mute possible compiler warning.
                           The real value will be set later. */

        /* Log before parsing chunk size: this is where buffer_head (char *)
           is read-only for the parser; we record a snapshot so others can
           reproduce the violation scenario where the pointer is not const. */
        mhd_probe_log ("before_parse_chunk_size: conn=%p buffer_head=%p available=%zu",
                       (void *) connection, (void *) buffer_head, available);
        mhd_probe_dump_buffer ("chunk_line_preview", buffer_head, available);

        num_dig = MHD_strx_to_uint64_n_ (buffer_head,
                                         available,
                                         &chunk_size);

        /* Log result of parsing */
        mhd_probe_log ("after_parse_chunk_size: conn=%p num_dig=%zu chunk_size=%llu available=%zu",
                       (void *) connection, num_dig, (unsigned long long) chunk_size, available);

        mhd_assert (num_dig <= available);
        if (num_dig == available)
          continue; /* Need line delimiter */

        broken = (0 == num_dig);
        if (broken)
          /* Check whether result is invalid due to uint64_t overflow */
          overflow = (((('0' <= buffer_head[0]) && ('9' >= buffer_head[0])) ||
                      (('A' <= buffer_head[0]) && ('F' >= buffer_head[0])) ||
                      (('a' <= buffer_head[0]) && ('f' >= buffer_head[0]))));
        else
        {
          /**
           * The length of the string with the number of the chunk size,
           * including chunk extension
           */
          size_t chunk_size_line_len;

          chunk_size_line_len = 0;
          if ((';' == buffer_head[num_dig]) ||
              (allow_bws &&
               ((' ' == buffer_head[num_dig]) ||
                ('\t' == buffer_head[num_dig]))))
          { /* Chunk extension */
            size_t i;

            /* Skip bad whitespaces (if any) */
            for (i = num_dig; i < available; ++i)
            {
              if ((' ' != buffer_head[i]) && ('\t' != buffer_head[i]))
                break;
            }
            if (i == available)
              break; /* need more data */
            if (';' == buffer_head[i])
            {
              for (++i; i < available; ++i)
              {
                if ('\n' == buffer_head[i])
                  break;
              }
              if (i == available)
                break; /* need more data */
              mhd_assert (i > num_dig);
              mhd_assert (1 <= i);
              /* Found LF position */
              if (bare_lf_as_crlf)
                chunk_size_line_len = i; /* Don't care about CR before LF */
              else if ('\r' == buffer_head[i - 1])
                chunk_size_line_len = i;
            }
            else
            { /* No ';' after "bad whitespace" */
              mhd_assert (allow_bws);
              mhd_assert (0 == chunk_size_line_len);
            }
          }
          else
          {
            mhd_assert (available >= num_dig);
            if ((2 <= (available - num_dig)) &&
                ('\r' == buffer_head[num_dig]) &&
                ('\n' == buffer_head[num_dig + 1]))
              chunk_size_line_len = num_dig + 2;
            else if (bare_lf_as_crlf &&
                     ('\n' == buffer_head[num_dig]))
              chunk_size_line_len = num_dig + 1;
            else if (2 > (available - num_dig))
              break; /* need more data */
          }

          if (0 != chunk_size_line_len)
          { /* Valid termination of the chunk size line */
            mhd_assert (chunk_size_line_len <= available);
            /* Start reading payload data of the chunk */
            connection->rq.current_chunk_offset = 0;
            connection->rq.current_chunk_size = chunk_size;

            /* Log successful parse and the computed parameters */
            mhd_probe_log ("chunk_size_line_parsed: conn=%p chunk_size=%llu chunk_size_line_len=%zu",
                           (void *) connection, (unsigned long long) chunk_size, chunk_size_line_len);

            available -= chunk_size_line_len;
            buffer_head += chunk_size_line_len;

            if (0 == chunk_size)
            { /* The final (termination) chunk */
              connection->rq.remaining_upload_size = 0;
              break;
            }
            if (available > 0)
              instant_retry = true;
            continue;
          }
          /* Invalid chunk size line */
        }

        if (! overflow)
        {
          mhd_probe_log ("error: chunked malformed (not overflow). conn=%p", (void *) connection);
          transmit_error_response_static (connection,
                                          MHD_HTTP_BAD_REQUEST,
                                          REQUEST_CHUNKED_MALFORMED);
        }
        else
        {
          mhd_probe_log ("error: chunk too large (overflow). conn=%p", (void *) connection);
          transmit_error_response_static (connection,
                                          MHD_HTTP_CONTENT_TOO_LARGE,
                                          REQUEST_CHUNK_TOO_LARGE);
        }
        return;
      }
    }
    else
    {
      /* no chunked encoding, give all to the client */
      mhd_assert (MHD_SIZE_UNKNOWN != connection->rq.remaining_upload_size);
      mhd_assert (0 != connection->rq.remaining_upload_size);
      if (connection->rq.remaining_upload_size < available)
        to_be_processed = (size_t) connection->rq.remaining_upload_size;
      else
        to_be_processed = available;
    }
    left_unprocessed = to_be_processed;
    connection->rq.client_aware = true;
    connection->in_access_handler = true;

    /* Log right before calling application default_handler */
    mhd_probe_log ("calling_default_handler: conn=%p to_be_processed=%zu left_unprocessed(before)=%zu buffer_head=%p",
                   (void *) connection, to_be_processed, left_unprocessed, (void *) buffer_head);
    mhd_probe_dump_buffer ("handler_payload_preview", buffer_head, to_be_processed);

    if (MHD_NO ==
        daemon->default_handler (daemon->default_handler_cls,
                                 connection,
                                 connection->rq.url,
                                 connection->rq.method,
                                 connection->rq.version,
                                 buffer_head,
                                 &left_unprocessed,
                                 &connection->rq.client_context))
    {
      connection->in_access_handler = false;
      /* serious internal error, close connection */
      mhd_probe_log ("error: application reported internal error, closing connection. conn=%p",
                     (void *) connection);
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Application reported internal error, " \
                                 "closing connection."));
      return;
    }
    connection->in_access_handler = false;

    /* Log after handler returned */
    mhd_probe_log ("handler_returned: conn=%p to_be_processed=%zu left_unprocessed(after)=%zu",
                   (void *) connection, to_be_processed, left_unprocessed);

    if (left_unprocessed > to_be_processed)
      MHD_PANIC (_ ("libmicrohttpd API violation.\n"));

    connection->rq.some_payload_processed =
      (left_unprocessed != to_be_processed);

    if (0 != left_unprocessed)
    {
      instant_retry = false; /* client did not process everything */
#ifdef HAVE_MESSAGES
      if ((! connection->rq.some_payload_processed) &&
          (! connection->suspended))
      {
        /* client did not process any upload data, complain if
           the setup was incorrect, which may prevent us from
           handling the rest of the request */
        if (MHD_D_IS_USING_THREADS_ (daemon))
          MHD_DLOG (daemon,
                    _ ("WARNING: Access Handler Callback has not processed " \
                       "any upload data and connection is not suspended. " \
                       "This may result in hung connection.\n"));
      }
#endif /* HAVE_MESSAGES */
    }
    processed_size = to_be_processed - left_unprocessed;
    /* dh left "processed" bytes in buffer for next time... */
    buffer_head += processed_size;
    available -= processed_size;
    if (! connection->rq.have_chunked_upload)
    {
      mhd_assert (MHD_SIZE_UNKNOWN != connection->rq.remaining_upload_size);
      connection->rq.remaining_upload_size -= processed_size;
    }
    else
    {
      mhd_assert (MHD_SIZE_UNKNOWN == connection->rq.remaining_upload_size);
      connection->rq.current_chunk_offset += processed_size;
    }

    /* Log loop iteration end and updated pointers/sizes */
    mhd_probe_log ("loop_end: conn=%p processed=%zu available=%zu buffer_head=%p current_chunk_offset=%llu current_chunk_size=%llu",
                   (void *) connection, processed_size, available, (void *) buffer_head,
                   (unsigned long long) connection->rq.current_chunk_offset,
                   (unsigned long long) connection->rq.current_chunk_size);

  } while (instant_retry);
  /* TODO: zero out reused memory region */
  if ( (available > 0) &&
       (buffer_head != connection->read_buffer) )
    memmove (connection->read_buffer,
             buffer_head,
             available);
  else
    mhd_assert ((0 == available) || \
                (connection->read_buffer_offset == available));
  connection->read_buffer_offset = available;

  /* Final state log */
  mhd_probe_log ("process_request_body: exit conn=%p final_available=%zu read_buffer=%p",
                 (void *) connection, available, (void *) connection->read_buffer);
  mhd_probe_dump_buffer ("final", connection->read_buffer, available);
}
